css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("64mi.com") {
    /**
     *========== bookcase ==========
     */
    /* reset to full screen */
    .notice, .main {
        width: auto;
    }
    .notice ul {
        display: none;
    }
    .notice:hover ul {
        display: list-item;
    }
    
    /* auto fit bookcase with full screen */
    #left {
        float: left;
    }
    #centerm {
        display: block;
        margin-left: 200px;
        float: none;
        width: auto;
    }
    #centerm > .cl {
        display: none;
    }
    
    /* reset bookcase layout */
    table {
        font-size: medium;
    }
    tr {
        line-height: normal;
    }
    th {
        width: auto;
        text-align: left;
    }
    td {
        overflow-x: hidden;
    }
    .odd {
        text-align: left;
    }
    
    /* fix bookmark label */
    p.fr>a[target="_blank"]:first-child:after {
        content: "书签";
    }
    
    /**
     *========== book reader ==========
     */
    body {
        font-size: small;
        font-family: 宋体;
        margin: 0px 8px;
    }
    /* shrink the search to the right */
    #a_head {
        float: right;
        right: 0px;
        height: 19px;
        line-height: normal;
        position: absolute;
    }
    #a_head > ul {
        width: auto;
    }
    #a_head form {
        display: inline;
    }
    #a_head > ul > li {
        display: none;
    }
    #a_head:hover > ul > li {
        display: inline;
    }
    /* shrink the settings to the left */
    .myset {
        float: left;
        margin: auto;
        height: 19px;
        line-height: normal;
        border: none;
        font-size: small;
    }
    .myset > select, .myset > input {
        border: 1px solid red;
        height: 17px;
    }
    .myset > br {
        display: none;
    }
    /* shift the main section away from the floats */
    #a_main {
        clear: both;
        margin: auto;
        width: auto;
    }
    .bdtop {
        border: none;
        display: none;
    }
    .bdsub {
        border: none;
    }
    #a_main dt {
        border: none;
        font-size: small;
        height: auto;
        line-height: normal;
        padding: 0;
    }
    #a_main dd:nth-of-type(1) {
        display: inline;
        float: left;
    }
    #a_main dd:nth-of-type(1) h1 {
        font-size: medium;
        height: auto;
        line-height: normal;
    }
    #a_main dd:nth-of-type(2) {
        display: inline;
        float: right;
    }
    #a_main dd:nth-of-type(2) h3 {
        font-size: small;
        height: auto;
        line-height: normal;
    }
    .adhtml {
        display: none;
    }
    #contents {
        clear: both;
        font-size: medium !important;
        padding: 0;
        line-height: normal;
    }    
    #contents br {
        display: block;
    }
    #contfoot {
        padding: 0;
        float: right;
        overflow-y: hidden;
    }
    #contfoot a {
        border: none;
        background: none;
        line-height: normal;
        height: auto;
        padding: 0;
        overflow: hidden;
        width: 2em;
        white-space: nowrap;
    }
    #contfoot .keep:before {
        content: "收藏";
    }
    #contfoot .case:before {
        content: "书架";
    }
    #contfoot .copy:before {
        content: "复制";
    }
    #contfoot .report:before {
        content: "报错";
    }
    #footlink {
        clear: none;
        float: right;
        line-height: normal;
        padding: 0;
    }
    #footlink a {
        float: none;
        margin: 0 10px 0 0;
    }
    .cl {
        height: 0px !important; /* force override style attribute */
    }
    #a_footer {
        display: none;
    }
}
