css:
@-moz-document url-prefix("http://tyda.se") {
/* MAIN */
body {
	background: #070707;
	color: #bbb;
}
/* Fix the color of the photos' credit texts */
.photo-author {
    color: #999;
}

/* FRONT PAGE */
.front-content .content {
	color: #bbb;
}
.boxShortNotes ul {
	text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.8)    
}    

/* HEADER */
/* Fix the menu items' colors */
#search-box a {
    color: #888
}
/* Fix the menu border's color */
#search-box .bottom {
    border-top: 1px solid #555;
}

/* Language buttons in the header */
.language-selector {
    box-shadow: none;
    background: none;
    border: none;
}
.arrowboth { display: none; }

.article-content h4, .article-content, h4 {
    color: #999 !important;
}

/* Main box for each word */
.box {
	background: #222 !important;
  	border: 1px solid #666 !important;
}

/* Sub-box */
.box-caption {
    background: #2b2b2b;   
}
.box-caption i {
 	color: #555   
}

/* THE FOLLOWING SHOULD BE SELF-EXPLANATORY */

/* Hide keyboard button in search field */
.latin-button { display:none; }

/* Make the search field dark *
input.input-text-search {
    background: #080808;
    border-color: #555;
}*/

/* Hide facebook and twitter etc */
#social-bar, .social, #ueButton {
	display: none;
}

#footer { display: none; }
}
