@-moz-document domain("naruho.do") {


body {background-color:#161616 !important;}


/* Wallpaper */
#wrap_content
{background-image:url('/*[[wall_image]]*/') !important; background-position:center;
 background-attachment:/*[[wall_attach]]*/; background-size:/*[[wall_stretch]]*/;}


/* Background */
#header, #footer
{background-color:#0C0C0C;}
#navBar #user_actions li .ua-box, .tag_date
{background-color:#101010;}
.box, .comment_wrapper .add-comment, #long_feeds .comments, #shareForm #typeshare, #navBar #user_actions li.items .icon .ua-notifications a.more, #index_card .card, .gridded_list li, .select, #user_seriesList li, #shareForm #picture_prev
{background-color:#202020;}
.comment_wrapper .comments li, .comment_wrapper .add-comment textarea, .tfield:not([type="file"]), [ng-model="search"], #searchBar input, #accounts #import_list input[type="text"], #fullnotification, #settings .profile_pic, #settings .banner_image, .ribborn, #user_seriesList .tabs li a:hover, #user_seriesList li .cel.position
{background-color:#303030 !important;}
.more-comments, .nav-tabs a:hover, .nav-tabs > .active
{background-color:#404040 !important;}
#side img.avatar, .load-more, .smallBtn.blue, .smallBtn.orange
{background-color:#505050;}

.ribborn a, #reviews .add-comment, #searchBar
{background:none;}


/* Images */
#long_feeds .message .social_opt span
{background-image:url('https://dl.dropbox.com/s/vuv7445srpo9p7v/social_opt.png');}

#top_options .icon
{background-image:url('https://dl.dropbox.com/s/igs233vcd5qmx63/options_icons.png');}

.comment_wrapper .vote a
{background-image:url('https://dl.dropbox.com/s/aj6q8oamwe2p2si/vote_icon_o.png');}

#user_seriesList #series_search
{background-image:url("https://dl.dropbox.com/s/wwoaa6bfmua4i06/grayglass.png");}

#shareForm #typeshare div a.icon
{background-image:url('https://dl.dropbox.com/s/sue1nbpf8phmpi3/publish_icon.png');}

.message img.tail
{background:url('https://dl.dropbox.com/s/0cyxya4akrlmr7q/triangleFeedsbox.png');  
 padding-left:16px; width:0 !important; height:31px;}

img[src="/images/ua-icon.png"], img[src="https://cdn.naruho.do/images/ua-icon.png"]
{background:url('https://dl.dropbox.com/s/vhlkpqn6t1bipq8/ua-icon.png');  
 padding-left:12px; width:0; height:11px;}

[id="ndo-series-long-list"] img {background:url("https://dl.dropboxusercontent.com/s/d1bigyx2vcb2l5f/pen-14.png");
 padding-right:14px; width:0 !important; height:14px;}


/* Font color */

.load-more:hover, .more-comments:hover, #navBar #user_actions li.items .icon .ua-notifications a:hover, #friends_request .notificationsList .list:hover, #friends_request .notificationsList .list:hover .user, .smallBtn.blue:hover, .smallBtn:not(.blue), #fullnotification a:hover *, #navBar .menu li a:visited, #navBar #user_actions .username .ua-box a:hover, #profile-nav li:not(.selected):not(:hover) a:visited
{color:white !important;}
a[href^="/hashtag/"]:hover, a[href^="/hashtag/"]:visited:hover
{color:#E0E0E0 !important;}
a[href^="/hashtag/"], a[href^="/hashtag/"]:visited
{color:#B0B0B0 !important;}
body, .comment_wrapper .add-comment textarea, #navBar #user_actions li.items .icon .ua-notifications h4, .gridded_list li .s-name, .select select, #fullnotification .ua-notifications a
{color:#909090;}
span.date, span.date a
{color:#787878;}
#navBar .date, #series_list li a, .gridded_list li anav-tabs
{color:#606060 !important;}


/* Border colors */

#shareForm #sharebox, #fullnotification a:not(.smallBtn)
{border-color:#202020 !important;}
#index_card .card, #friends_block .gridded_list li, .gridded_list li {border-color:#303030;}
.ribborn:before {border-color:#303030 transparent;}
.division_line, .top-nav, .avatar.rounded, .bottom-line, #navBar #user_actions li.items .icon .ua-notifications h4, #navBar #user_actions .username .ua-box li, #user_seriesList li, #user_seriesList .cel, .smallBtn.blue, .smallBtn.orange, .load-more, .select, .hashtag .subtitle
{border-color:#404040 !important;}
#index_card .card .descrip p, .tfield, #accounts #import_list input[type="text"], .category-section
{border-color:#505050 !important;}
.subtitle span, .message hr
{border-color:#808080 !important;}

.box, .comment_wrapper .add-comment, .comments, #long_feeds .comments, .comment_wrapper .add-comment textarea, #side img.avatar, #shareForm #typeshare, #shareForm #typeshare div, .more-comments, #fullnotification, #settings .profile_pic, #settings .banner_image, #navBar #user_actions li .ua-box, #reviews .add-comment, #shareForm #picture_prev
{border-color:transparent;}

#header, #searchBar
{border:none !important;}

/* Hide Adds */
.pub_300,
.adsbygoogle {
	display: none !important;
}


/* MISC */
#side .friends ul {
    margin: 0 -8px;
    background: #222;
    padding: 8px 4px;
    border-radius: 4px;
}
#shareForm #sharebox {box-shadow:none; border-width:2px; padding:2px 6px; font-size:13px; height:60px;}
.actions .delete {margin:-6px;}
.comments .delete {margin-right:-4px;}
.comments > ul > li {padding-left:4px !important;}
.more-comments {margin-bottom:4px; padding:4px;}
#header {box-shadow:0 0 8px rgba(0,0,0,.5);}
a {text-decoration:none !important;}
.innerS {text-shadow:none;}
.tfield, #accounts #import_list input[type="text"] {box-shadow:inset 1px 1px 6px rgba(0,0,0,.33) !important;}
img[src="/images/blue_plus.png"], img[src="https://cdn.naruho.do/images/blue_plus.png"], .date img
{display:none;}
img[src="/images/404_error.png"], img[src="https://cdn.naruho.do/images/404_error.png"]
{background-color:white; border-radius:24px;}
img[src="/images/h-loading.gif"], img[src="https://cdn.naruho.do/images/h-loading.gif"]
{filter: invert(1); -webkit-filter: invert(1); opacity: .5;}
.comment_wrapper .add-comment .buttons input, #shareForm #typeshare .smallBtn, #long_feeds.particular .smallBtn
{width:auto; padding:6px 12px;}
.message.box {padding-bottom:4px;}
.message.box .date {padding: 0;}
.add-comment {padding-top:4px !important;}
#series_list li {border:1px solid #404040 !important;}
#navBar .menu {margin-left:40px;}
#navBar .menu li {margin:0; height:30px;}
#navBar .menu li a {padding:4px 12px 0;}
.more-comments {border-color:transparent !important;}
#feeds-rightBlock .hashtag a:hover {opacity:1;}
#fullnotification .date {opacity:.66;}
#header {border:none;}
#navBar .icon {height:47px;}
#searchBar input {border:1px solid #505050;}
#side .avatar {margin-bottom: 4px !important;}
#side h3 span {margin-top: 2px; margin-right: -4px;}
#sharebox, .message.box {border-radius: 3px 3px 0 0;}
#typeshare {margin-top: 0 !important;}
#typeshare, [id^="ndo-message"] > *:last-child {border-radius: 0 0 3px 3px;}
[id^="ndo-message"] > *:last-child {padding-bottom: 6px !important; border-radius: 0 0 3px 3px;}
.add-comment {padding-top: 0px !important;}
.comment_wrapper .comments li, .comment_wrapper .add-comment textarea, .more-comments {border-radius: 3px;}
.comment_wrapper .comments li {padding: 2px 4px 2px 2px !important; margin: 2px 0 !important; border: 1px solid #3C3C3C !important;}

#settings .profile_pic, #settings .banner_image {border-radius:2px;}
#searchBar input, .new_chapter .about .gallery li {border-radius:3px;}
.new_release .poster, .new_chapter .poster {border-radius:4px;}


/* Animation */
.top-nav li:hover, .top-nav li.selected, .smallBtn, .more-comments, #navBar #user_actions .register a
{transition: .2s !important;}

textarea, #long_feeds a:not([class]), #user_seriesList li a
{transition: .1s !important;}


/* FORUM */

.discussion, .discussion-stats, .discussion-item-stats, .forum-content .discussion-item-title, .pop-post-header h4 {
    color: #999 !important;
}

.tag-label, .discussion code {
    background: #444;
    transition: .2s;
    opacity: .85;
    color: #BBB;
}

.tag-label:hover {
    opacity: 1;
}

.discussion blockquote,
.discussion .in-reply-to,
.discussion .spoiler {
    background: rgba(128, 128, 128, .1);
    border-color: rgba(128, 128, 128, .1);
}

.category-header, .discussion-item:hover {
    background: #333;
}

div.forum-header, .discussion-stats {
    background: none;
}

.discussion-post, .discussion-post hr, div.spoiler-content, .pop-post-header hr {
    border-color: #444;
}

.discussion-actions-items a, .post-actions-items a, a.new-discuss {
    opacity: 0.8;
    transition: .2s;
}

.discussion-actions-items a:hover, .post-actions-items a:hover, a.new-discuss:hover {
    opacity: 1;
    color: white !important;
}

.post-actions-items a.reply-reply:hover, a.new-discuss {
    color: #444 !important;
}

.forum-header .go-back a:not(:hover), .tag-label, .category-header h4 {
    color: #999 !important;
}

.tag-label a {
    color: #BBB !important;
}

.discussion-item-lastpost, .forum-header #forum-nav .selected a {
    color: #CCC !important;
}

.discussion-item-title {
    color: #DDD !important;
}

a.new-discuss, #forum-nav a:hover, #forum-nav .selected a {
    color: white !important;
}

.forum-header {    
    padding-top: 8px;
}

.discussion-post, .discussion-stats-content {
    background: #202020;
    border: none;
    margin: 12px 0;
    padding: 0 12px;
    border-radius: 4px;
}

.discussion-stats-content {
    margin-top: -8px;
}

.discussion-post-content {
    padding: 12px 0 42px 74px;
}

.discussion-stats-content {
    padding: 8px 12px;
    background: #1C1C1C;
    color: #999;
}

.discussion-stats-list li span:last-child {
    color: #666;    
}

.in-reply-to {
    padding: 0px 12px;
    border-radius: 4px;
}

.discussion blockquote {
    border-radius: 2px;
    border: 1px solid rgba(128, 128, 128, .1);
    border-left-width: 6px;
}

.category-header {
    border-radius: 4px;
    border: 1px solid #444;
}

.cat-icon {
    -webkit-filter: invert(.25);
    filter: invert(.25);
}



/* THEME COLOR */
/*[[theme_color]]*/


/*[[transparency]]*/

}

