css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("guerretribale.fr") {
    .vis > h4 {background-image: linear-gradient(to bottom, #004963 0%, #051E26 100%); color:#fff;}
    
    .quest{border-radius:2em;border:none}
    .quest_progress{border-radius:2em 0em 0em 2em;background:#004863;opacity:0.8}
    a{color:#004863;}
    a:hover{color:#3F7D93;}
    .forum:hover.selected a,.modemenu td.selected:hover a{color:#3F7D93;text-decoration:underline;}
   .forum{border:none;border-radius:0.2em;background:#fff}
   .forum:hover{border:none;background:#004863}
    .forum:hover a{color:#fff;}
    .btn,.btn-default,.modemenu td,.modemenu td a,span.forum,.forum a{transition:all 0.2s ease;}
    .modemenu td:hover{background:#004863;}
    .modemenu td:hover a{color:#fff}
    .btn,.btn-default{
        background:#3A80A8;
        border-radius:0.1em;
        border:none;
        padding:1em;
  
    }
    .btn:hover,.btn-default:hover{
        background:#124F72;

        
    }
    #show_outgoing_units .vis tr:hover td{background:#fff}
    
    #menu_row > .menu-item,.top_bar,#menu_row > td.menu-item > a,.top_bar .bg_left,.top_bar .bg_right{background:#004863;background-image: linear-gradient(to bottom, #004963 0%, #051E26 100%);transition:all 0.3s ease;}
    #menu_row > td.menu-item > a{;height:35px;left:0px;margin-left:-2px;}
    #topContainer{top:-2px;}
    
    
    #menu_row > .menu-item:hover,#menu_row > td.menu-item:hover > a{background:#004863;box-shadow:0px 0px 0.2em #000 inset;}

    
    
    .menu_column tr .menu-column-item a,.menu_column tr .menu-column-item,.menu_column tr:first-child .menu-column-item a,.menu_column tr:first-child .menu-column-item, .menu_column tr:first-child .menu-column-item a,#topdisplay .menu_column .menu-column-item a,#topdisplay .menu_column tr:first-child .menu-column-item a{background:#004863;}
    
        .menu_column tr .menu-column-item a:hover,.menu_column tr .menu-column-item:hover,.menu_column tr:first-child .menu-column-item a:hover,.menu_column tr:first-child .menu-column-item:hover,#topdisplay .menu_column .menu-column-item a:hover,#topdisplay .menu_column tr:first-child .menu-column-item a:hover{background:#009CF7;}
    
    #topTable .menu_column a:hover{color:#fff;}
    
#topdisplay .menu_column .menu-column-item a, .menu_column .menu-column-item a {left:-22px;}    
    
    .menu_column tr .bottom {display:none;}
    
   
     body{background:#1D2D33; }
