css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
domain("baidu.com"),
url-prefix(http://www.baidu.com/){
    .mnav{text-decoration:none !important;}
    .lb,.pf{text-decoration:none !important;color:blue !important}
    #u1{padding-right:36px;}
    .bri{display:none !important;}
    #lg img{transform:rotate(180deg);}
    #lg img:hover{animation:logo 6s infinite;}
    @keyframes logo {
        0%{transform:rotate(-180deg);}
        60%{transform:rotate(0deg);}
        90%{transform:rotate(0deg);}
        95%{transform:scale(1.8);}
        100%{transform:rotate(180deg);}
    }
    #ftCon{display:none !important;}    
    #nv a{text-decoration:none;font-size:16px;color:#555555;}
    #nv b{font-size:16px;}
    .s-treasure{display:none !important;}
    #bottom_container{display:none;}
    #result_logo img{transform:rotate(180deg);}
    #s_tab a{text-decoration:none;font-size:16px;color:#555555;}
    #s_tab b{font-size:16px;}
    .shouji{display:none;}
    .c-container{padding-bottom:16px;border-bottom:2px dashed #CCCCCC;}
    .op-sotckdynamic a,.c-row a,.c-offset a,.subLink_factory td a{text-decoration:none;color:#333333;}
    .op-sotckdynamic a:hover,.c-row a:hover,.c-offset a:hover,.subLink_factory td a:hover{text-decoration:underline;color:#111111;}
    #content_left h3 a{text-decoration:none;font-size:16px;color:#005D5D;}
    #content_left h3 a:hover{text-decoration:underline;color:#080808;}
    #content_left h3 em{text-decoration:none;font-size:18px;color:#0000CC;}
    #content_left em{color:#0000CC;}
    .toindex{display:none;}
}
