css:
@-moz-document domain("shroomery.org") {



.topright, a#random, a#online, a#users, a#faq, a#home, a#growery, a#calendar, a#arcade, a#store { display: none; }
.searchdiv { margin-right: 35px; }
table, th, td, body, #nav li ul a { font-family: 'Lato', Helvetica, Arial, sans-serif !important; font-size: 17px; }

table td.menubar span.navlinks a { font-size: 14px; }

* { box-sizing: border-box !important; -webkit-box-sizing: border-box !important; -moz-box-sizing: border-box !important; }

/* General tables */
table td { padding: 0 !important; }

/* Navigation */
body #nav, body #nav ul { background: #012b34 !important; border: none; }
#nav li a { display: inline-block !important; padding: 15px !important; }
#nav li ul { width: 250px; z-index: 999999 !important; }
#nav li li { width: 100% !important; }
#nav li ul a { width: 90% !important; }
#nav li { width: auto !important; border: none !important; }
table td[bgcolor="#189AC6"].white_txt { background: none !important; padding: 0 !important; }


/* Location */
table.location { display: none !important; }

/* Forum welcome bar */
table.tablesurround { background: #1a89b0; position: relative; top: -25px; }
table.tablesurround td { padding: 9px !important; }
table.tablesurround td.menubar:last-child { text-align: right; }
table.tablesurround .navlinks a { margin-right: 20px; }

/* Ad */
table.tablesurround + br + center { position: relative; top: -23px; }

/* Breadcrumbs & utilities */
script + table.tablesurround { background: none; border: none; padding: 0 15px; }
script + table.tablesurround table.tableborders { background: none; }
script + table.tablesurround td { padding: 0 !important; border: none; }
td.darktable table.tablesurround { position: static !important; }
table td.darktable a { font-size: 14px; display: inline-block !important; padding: 10px !important; }
table td.darktable table td[width="33%"][align="right"] table.tablesurround { background: none; }

/* Thread */
script + table.tablesurround + br + table.tablesurround, script + table.tablesurround + br + table.tablesurround table.tableborders { background: none; }
script + table.tablesurround + br + table.tablesurround td { padding: 0 !important; }
script + table.tablesurround + br + table.tablesurround td td.tdheader { display: none; }
td.lighttable { line-height: 1.618; }

/* Comment author */
td[rowspan="2"][style="width:17%;"][valign="top"].darktable { background: #012229; padding: 0 15px !important; }
td[rowspan="2"][style="width:17%;"][valign="top"].darktable .highslide, td[rowspan="2"][style="width:17%;"][valign="top"].darktable .highslide img { display: block; width: 100%; height: 130px; }
td[rowspan="2"][style="width:17%;"][valign="top"].darktable b a { padding: 0 !important; }
td[rowspan="2"][style="width:17%;"][valign="top"].darktable span.small > div { margin: 0 auto; }
td[rowspan="2"][style="width:17%;"][valign="top"].darktable span { display: block; width: 100%; text-align: center; }
 
/* Post */
script + table.tablesurround + br + table.tablesurround td.subjecttable { padding: 15px !important; }
script + table.tablesurround + br + table.tablesurround td.subjecttable b { font-size: 20px; font-weight: 600; }
script + table.tablesurround + br + table.tablesurround td.subjecttable span.small.count + br + font.small { padding: 0 15px !important; }


.postholder { font-size: 16px; padding: 15px 25px; }

td.lighttable .postholder + font > .posterholder { background: #012b34 !important; }
font.small { display: block; padding: 15px 25px; }
}
