body{background:#eee;
color: #2c2c2c;
font-size: 1.3em;
}
#wrapper{background:#eee;
  box-shadow: none;
  
}
a{color: #0078a0; text-decoration:none; }
a:hover{color: #040404; text-decoration:underline;}
