#next{margin-left:55px;border: 1px solid #0078a0;border-radius:5px;}

#menu{}
.menublock{}
.menuitem{}
.submenuitem{}
.pict_div{border:none;}
.content2{}
.content{}
.shadow{-webkit-box-shadow:none;}
#prev:hover, #next:hover, .shadow:hover{
  border: 3px solid #0078a0;
