css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://ebible.org/web/) {

html, body {
	background: #fefefe;
	color: #444;
}
body {
	font-size: 100%;
	max-width: 33em;
	margin: 0 auto;
	width: 33em;
}
body {
	font-family: "Hoefler Text", Constantia, "Lucida Bright", Lucidabright, "Lucida Serif", Lucida, "DejaVu Serif", "Bitstream Vera Serif", "Liberation Serif", Georgia, serif;
	line-height: 1.5em;
}
.tnav a {
	background-color: #aaa;
	border-radius: 3px;
	color: #444;
	border: 1px solid #444;
	display: inline-block;
	padding: 0.1em;
	text-decoration: none;
}
a, .f, .notemark, .notebackref, .popup {
	color: #e22;
	font-size: 80%;
}
h2, .mt, .mt2, .mt3 {
	font-variant: small-caps;
	font-weight: normal;
	letter-spacing: 0.05em;
}
.mt {
	margin: 0 2em;
	text-align: center;
}
.mt:after { 
	color: #e22;
	content: "†";
	display: block;
	margin-top: 1em;
}
h3, .chapterlabel {
	font-weight: normal;
	font-style: italic;
	text-align: left;
}
p, .p {
	margin-bottom: 0;
}
p + p, .p +.p {
	margin-top: 0;
}
p + p, .p +.p, .block-indent + p {
	text-indent: 2em;
}
p.line-group, .block-indent + p.same-paragraph {
	text-indent: 0;
	margin-top: 1em;
	margin-bottom: 0;
}
.block-indent {
	margin-left: 3em;
}
.speaker {
	font-style: italic;
	margin-left: -3em;
}
.footnote, .f {
	border: none;
	font-size: 90%;
	font-style: italic;
	margin: 0;
	text-align: left;
}
.footnote a, .footnotes {
	text-decoration: none !important;
}
.footnote:before {
	color: #e22;
	content: "¶";
	display: block;
	font-size: 120%;
	font-style: normal;
	margin: 1em auto;
	text-align: center;
}
.copyright a {
	display: block;
}
.excerpted-from {
	margin-top: 3em;
	margin-bottom: 2em;
	text-indent: 0;
}
.excerpted-from:before {
	color: #e22;
	content: "❦";
	display: block;
	font-size: 150%;
	font-style: normal;
	margin: 1em auto;
	text-align: center;
}
/* Optional */
.verse, .noprint {
	display: none;
}
}
