  border: 0; /* 1 */
  padding: 0; /* 2 */
}

/**
 * Remove default vertical scrollbar in IE 8/9/10/11.
 */

textarea {
  overflow: auto;
}

/**
 * Don't inherit the `font-weight` (applied by a rule above).
 * NOTE: the default cannot safely be changed in Chrome and Safari on OS X.
 */

optgroup {
  font-weight: bold;
}

/* Tables
   ========================================================================== */

/**
 * Remove most spacing between table cells.
 */

table {
  border-collapse: collapse;
  border-spacing: 0;
}

td,
th {
  padding: 0;
}

  

/*
======================================================================
  Start actual re-styling
======================================================================
 */

  table {
   /* width: 100%; */
  }
  
  tr, td {
    display: block;
    width: 100%;
    height: auto !important;
  }
  
  td.row1 .gensmall,
  td.row2 .gensmall {
  /*  display: block; */
  }
  
  td.row2,
  td.row3,
  td.row3Right {
    width: 25%
  }
  
  span.mainmenu a {
    display: inline-block;
    font-size: 2.5em;
    padding: 1em 1em;
  }
  
  td.nav a {
    font-size: 1.125em;
  }
  
  span.gensmall,
  span.nav {
   display: table-cell !important;
    font-size: 2em;
    padding: .5em .75em;
  }
   
      span.gensmall a,
      span.nav a {
        padding: .5em .75em;
      }
  
    .forumline td span {
      display: inline-block;
      padding: .75em 1em;
      font-size: 1.25em;
   }
  
  .forumline {
    display: block;
    max-width: 100% !important;
    width: 100% !important;
  }
  
    .forumline td span.gensmall {
      font-size: 1.75em;
    }
  
  .forumline td span a.forumlink,
  .forumline td span a.topictitle{
     font-size: 2em;
  }
  
    /* end of mozilla style */
}
@-moz-document url-prefix("http://tgdmb.com/index.php") {

  .bodyline td:nth-child(1) {
   display: none;
 }
  
 .bodyline td:nth-child(3),
 .bodyline td:nth-child(4),
 .bodyline td:nth-child(5) {
    display: table-cell;
 } 
  
  /* end of main page style */
}

@-moz-document url-prefix("http://tgdmb.com/viewforum.php") {
  /* Start sub forum styles */

  .bodyline td:nth-child(1) {
    display: none;
  }
    
  .bodyline td:nth-child(3),
  .bodyline td:nth-child(4),
  .bodyline td:nth-child(5),
  .bodyline td:nth-child(6){
    display: table-cell;
  } 
    
  .bodyline span.gensmall br { display: none; }

  /* End sub forum styles */
}

@-moz-document url-prefix("http://tgdmb.com/viewtopic.php") {
  td.row1,
  td.row2,
  td.row3,
  td.row3Right {
   display: table-cell;
  }
  
  .forumline tr td.row1:nth-child(1),
  .forumline tr td.row2:nth-child(1) {
    background: red;
    max-width: 20%;
  }
  
  .forumline tr td.row1:nth-child(2),
  .forumline tr td.row2:nth-child(2) {
    width: 100%
  }
  
  img {
    max-width:800px;
  }
  
  a.name {
    font-size: larger;
  }
  
  .bodyline .row1 {
    display: table-cell;
  }
  
  .forumline td[width="150"] {
    display: table-cell !important;
    margin: 0;
    padding: 0 0 0 0;
    background: yellow !important;
    width: 100% !important;
  }
  
  .forumline td[width="150"] span {
    display: inline-block;
    position: relative;
  }
  
  .forumline td[width="150"] br {
    display: none;
  }
  
  .forumline td[width="150"] img {
    max-width: 50px;
    padding : 0 .5em;
  }
  
  .forumline td[height="28"].row2 {
    display: block; 
  }
  
  span.postbody {
    line-height: 125%;
    font-size: 1.5em !important;
  }
  
  .forumline td span.postbody span[style] {
      display: inline;
      font-size: 1em;
      padding: 1em 0;
  }
  
  .bodyline td.quote {
    display: block;
    font-size: 24px !important;
    padding: 1em;
  }

  /* End thread styles */
}
