css:
@-moz-document url("https://www.youtube.com/"), url-prefix("https://www.youtube.com/feed/") {
	#masthead-positioner,
	#masthead-appbar-container {
		position: static;
	}

	#masthead-positioner-height-offset {
		display: none;
	}
}

@-moz-document url-prefix("https://www.youtube.com/feed/subscriptions") {
	#masthead-appbar-container { display: none }
}
