css:
@-moz-document domain("mail.google.com") {
/*========================================================
=            Clean Metro Interface for Gmail             =
========================================================

  @author: Vin√≠cius Stutz
  @site: http://www.vinicius-stutz.com/
  @date: 4 aug 2014

*/
  
* {
    box-shadow: none !important;
    border-radius: 0px !important;
    text-shadow: none !important;
    font-family: 'Segoe UI', 'Open Sans' !important;
    font-size: 13px !important;
}
html { border: none !important; }
body {
  background: #fff !important;
  font-family: 'Segoe UI', 'Open Sans' !important;
}

/* ---------- */

.gb_Lb { background: #F4F4F4 !important; }
#gbq1, .gb_sc, #gbwa { display: none !important; }
#gbq2 {
  margin-left: 30px !important;
  width: 100% !important;
}
#gbqfb, .T-I-KE, .akh {
  background-color: #3498db !important;
  background-image: none !important;
  color: #fff !important;
  border: 1px solid #2980b9 !important;
  border-radius: 0 !important;
  cursor: pointer !important;
  text-transform: uppercase !important;
}
.T-I-KE { z-index: 6 !important; }
.J-M { z-index: 7 !important; }
#gbqfw { margin-left: 196px !important; }
.G-atb { border-bottom: 0 !important; }
.akh {
  padding: 0 30px !important;
  line-height: 28px !important;
  margin-left: 7px !important;
  position: absolute !important;
  margin-top: -54px !important;
}
.aic, .n6 {
  margin-left: 23px !important;
  margin-top: -17px !important;
}
.ake {
  color: #3498db !important;
  font-weight: bold !important;
}
.asT-asx {
  background: none !important;
  vertical-align: middle !important;
  font-size: 0 !important;
  width: 0 !important;
  height: 0 !important; 
  border-left: 5px solid transparent !important;
  border-right: 5px solid transparent !important;
  border-top: 5px solid #fff !important;
}
.aAA.J-KU-Jg-K9 {
  font-weight: 300 !important;
  text-transform: uppercase !important;
}
.gbii { border-radius: 50% !important; }
.nZ .nU, .nZ .n0, .bQ, .nU a, .nU a:link, .nU a:visited {
  color: #444 !important;
  text-transform: uppercase !important;
}
.nZ .nU, .nZ .n0 { font-weight: bold !important; }
.ain { border-left: 4px solid #3498db !important; }
.x7 { background: #cce6f7 !important; }
.yO {
  background: #fff !important;
  color: #222 !important;
}
.yO:hover {
  background: #efefef !important;
  color: #222 !important;
}
.TI .T-I-ax7, .z0 .T-I-ax7, .G-atb .T-I-ax7, .d-Na-JX-I {
  background-color: #fff !important;
  background-image: none !important;
  cursor: pointer !important;
  text-transform: uppercase !important;
}
.TI .T-I-ax7, .z0 .T-I-ax7, .G-atb .T-I-ax7, .TI .T-I-ax7:hover, .z0 .T-I-ax7:hover, .G-atb .T-I-ax7:hover,
.TI .T-I-ax7:focus, .z0 .T-I-ax7:focus, .G-atb .T-I-ax7:focus, .d-Na-JX-I, .d-Na-JX-I:focus, .d-Na-JX-I:focus {
  border: 0 !important;
  color: #444 !important;
}
.TI .T-I-ax7.T-I-Js-Gs, .G-atb .T-I-ax7.T-I-Js-Gs {
  margin-left: -1px !important;
  border-left: 0 !important;
}
.F {
  border-right: 1px #e5e5e5 solid !important;
  border-left: 1px #e5e5e5 solid !important;
}
.yW, .y6 span { font-size: 15px !important; }
span.y2 {
  font-size: 13px !important;
  display: block !important;
}
div.l, div.l.m, div.k { background: #2980b9 !important; }

/*-----  End of Clean Metro Interface for Gmail   ------*/
}
