css:
@-moz-document domain("dailymotion.com"), domain("dmcdn.net") {

#player_container * {border: none !important; } 

/* ::::::::::: UNHIDE IMAGES IN PALE MOON / REMOVE BLUR  :::::::::::: */
.pl_videos_listwithplayer > div:nth-child(1) > div:nth-child(2),
IMG.preview.blurred{ filter: none !important; }

/* :::::::::::::::::::::::::::::::::::::::: WIDE ::::::::::::::::::::::::::::::::::::::::::: */
DIV.pl_videos_listwithplayer.row,
#player_box, #player_main, #container_player_main, #video_player_main, 
#player_container, #main_player, #container_main_player, #video_main_player,
#content, .col-8, .left_content.span-8, #dual_list, .pl_video_related, .related_list, 
.pl_video_bottombox.dmco_box.column.bottom_box, .tabs_video_list.column.last, 
.column.span-4.last.right_box, .dmpi_list.column, .column.span-8.left_box{      width: 100% !important; }

#player_box, #player_main, #container_player_main, #video_player_main, div.row,
#player_container, #main_player, #container_main_player, #video_main_player,
#playerv5-iframe{    height: 600px !important;  }

.col-8{  margin-right: -20px !Important; }

/* :::::::::::::::::::::::::::: MAIN WIDTH ::::::::::::::::::::::::::::::::::::::::::: */
.column.span-8.left_box, .left_content.span-8, .pl_video_tabvideos.tab_content.clearfix, 
#player_container{  margin-left: -14px !important; width: 102.5% !important; border: none !important; } 

.pl_video_item,
.dmpi_video_item{  margin: 5px !important; }

div.sd_playlist_listitem > DIV.media{      width: 400px !important; }

DIV.sd_playlist_listitem{ position: absolute !important; }

div.sd_playlist_listitem:nth-child(2), div.sd_playlist_listitem:nth-child(3), div.sd_playlist_listitem:nth-child(4),
div.sd_playlist_listitem:nth-child(5), div.sd_playlist_listitem:nth-child(6), div.sd_playlist_listitem:nth-child(7),
div.sd_playlist_listitem:nth-child(8), div.sd_playlist_listitem:nth-child(9), div.sd_playlist_listitem:nth-child(10),
div.sd_playlist_listitem:nth-child(11), div.sd_playlist_listitem:nth-child(12), div.sd_playlist_listitem:nth-child(13),
div.sd_playlist_listitem:nth-child(14), div.sd_playlist_listitem:nth-child(15), div.sd_playlist_listitem:nth-child(16),
div.sd_playlist_listitem:nth-child(17), div.sd_playlist_listitem:nth-child(18), div.sd_playlist_listitem:nth-child(19)
{ margin-right: 0 !important; margin-bottom: 0 !important; }

div.sd_playlist_listitem:nth-child(2), div.sd_playlist_listitem:nth-child(5), div.sd_playlist_listitem:nth-child(8),
div.sd_playlist_listitem:nth-child(11), div.sd_playlist_listitem:nth-child(14), div.sd_playlist_listitem:nth-child(17)
{ margin-left: -15px !important;}

div.sd_playlist_listitem:nth-child(3), div.sd_playlist_listitem:nth-child(6), div.sd_playlist_listitem:nth-child(9),
div.sd_playlist_listitem:nth-child(12), div.sd_playlist_listitem:nth-child(15), div.sd_playlist_listitem:nth-child(18)
{ margin-left: 400px  !important;}

div.sd_playlist_listitem:nth-child(4), div.sd_playlist_listitem:nth-child(7), div.sd_playlist_listitem:nth-child(10),
div.sd_playlist_listitem:nth-child(13), div.sd_playlist_listitem:nth-child(16), div.sd_playlist_listitem:nth-child(19)
{ margin-left: 820px !important;}

div.sd_playlist_listitem:nth-child(5), div.sd_playlist_listitem:nth-child(6), div.sd_playlist_listitem:nth-child(7)
{ margin-top: 180px !important;}

div.sd_playlist_listitem:nth-child(8), div.sd_playlist_listitem:nth-child(9), div.sd_playlist_listitem:nth-child(10)
{ margin-top: 360px !important;}

div.sd_playlist_listitem:nth-child(11), div.sd_playlist_listitem:nth-child(12), div.sd_playlist_listitem:nth-child(13)
{ margin-top: 540px !important;}

div.sd_playlist_listitem:nth-child(14), div.sd_playlist_listitem:nth-child(15), div.sd_playlist_listitem:nth-child(16)
{ margin-top: 720px !important;}

div.sd_playlist_listitem:nth-child(17), div.sd_playlist_listitem:nth-child(18), div.sd_playlist_listitem:nth-child(19)
{ margin-top: 900px !important;}
/* MISC */
a.inverted-link{     font-size: 17pt; font-weight: bold; }

/* HIDE */

/* .light_border_bottom, */ .sd_metaheader, DIV.dmpi_video_relatedlist, .dmpi_video_info,  .dmpi_video_tools_simple, #dmpi_video_tools,
.btn-sm.mrg-btm-md.pull-start.js-tipsy.js-tipsy-light.btn, .bottom_bar.bg_section,
.js-switch-view.foreground, .js-switch-view.mrg-end-sm.foreground.strong, #player_tools,
.sd_footer--bottom{ display: none !important;   }


/* :::::::::::::::::::::::::::::::::::::::::::::::::: DARK :::::::::::::::::::::::::::::::::::::::::::::::::: */

.tipsy-arrow-bg , .tipsy-arrow{ display: none !important;  }

A.btn.btn-primary.mrg-btm-md, #input_search{  transition: all ease .5s; }
#input_search:focus{        box-shadow: red 0 0 1px,  red 0 0 5px !important;   }
#input_search{  background: #000 !important; color: #eee !important; border-radius: 8px 0 0 8px !important; }
#search_submit{  background: #222 !important; border-radius: 0 8px 8px 0 !important; border: none !important;  }
#search_submit:hover{   background: #a00 !important; box-shadow: #a00 0 0 1px,  red 0 0 5px !important; transition: background ease .1s, box-shadow .5s ease; }
.icon.icon-search{ color: #EEE !important; }

*{ border-color: #3e3e3e !important; }

A.btn.btn-primary.mrg-btm-md{ background: #c00 !important; border: none !important; margin: 5px !Important; }
A.btn.btn-primary.mrg-btm-md:hover{ box-shadow:  #c00 0 0 3px, #f00 0 0 8px; }

.tipsy-inner{   box-shadow: none !important;
                background: rgba(0,0,0,.7) !important;
                text-shadow: #000 0 0 1px, #000 0 0 2px, #000 0 0 3px  !important;  }
                
.mo_subnav_linkcontainer:hover{ background: steelblue !important; }
.mo_subnav_linkcontainer{ padding: 0 10px !important; }

#topwrapper, html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, #footer, #push_footer,
.container, .search, .foreground, .foreground_hoverable, .foreground a, #content, #header, #header_nav, .content_right,
.box_content, div.dmpi_bigbox, body.dm_page_html_video div.video_description, .foreground2, .foreground2 a, .background_3, 
.background_3_on_hover:hover .sd_homenav .nav_link, .sd_homenav #nav_all,  #video_flyout_box{ background: #3e3e3e !important;
                                                                                                color: #fff !important;  }

#video_flyout_boxh3.dmpi_video_title a.dmco_link, body.dm_page_html_login, body.dm_page_html_login #topwrapper,
body.dm_page_html_login #content, body.dm_page_html_login div.dmpi_box div.box_content, 
body.dm_page_html_login div.dmpi_box div.box_content div.content_right, .sd_homeeventlist, .lt_homefeed .sd_homefeed_block,
.lt_homefeed .sd_video_homebigitem, .lt_homefeed .dmpi_bigbox, .pl_videos_featuredsearchitem,
.lt_homefeed .sd_video_homehorizontalitem {    background-color: #3e3e3e !important;   }

.lt_homemedia .sd_homemedia_block .sd_video_homeinfo, .lt_homemedia .sd_video_homebigitem .sd_video_homeinfo, 
.lt_homemedia .sd_video_homehorizontalitem .sd_video_homeinfo, .sd_homefeed_block,
.higher_footer #push_footer, .sd_footer, div#header, #popup_dimmer, .sd_homenav .tools button.selected,
.sd_video_homebuzzlist {    background: #3e3e3e !important; }

div.grid_view div.dmpi_video_item h3.dmpi_video_title, div.grid_view div.dmpi_video_item h3.dmpi_video_title a.dmco_link,
input.dmco_form_text, .video_title, div.dmpi_channel_item span.dmco_simpletext.description,
form.nd div.form_item div.form_input input.dmco_form_text, form.nd div.form_item div.form_input input.dmco_form_password, 
form.nd div.form_item div.form_input input.rule_captcha, div.autocomplete div.suggestions div span.suggestion,
.label, .sd_video_homeinfo .sd_video_owner a,
.sd_video_homeinfo .sd_video_owner, .sd_video_homeinfo .description   {  color: #fff !important;    }

a, .dmco_simplelink.name, .name, 
.sd_homeeventitem .event_title a, #footer .footer_title, .sd_video_homeinfo .sd_video_title a,
.sd_video_homebuzzitem .sd_video_owner a, div.grid_view div.dmpi_video_item h3.dmpi_video_title, 
div.grid_view div.dmpi_video_item h3.dmpi_video_title a.dmco_link, 
#video_flyout_box h3.dmpi_video_title a.dmco_link {     color: #5cf !important;  }


#popupContent input.text {  color: #0F52BA !important;  }

.pg_video .video_infos, .pg_video .pl_video_bottombox, .pg_video .dmpi_bigbox.background,
.js-tipsy-ballon{  background: transparent !important; border: none !important; }

textarea, pre, input.dmco_form_text{    background: #222 !important; }

.sd_header, #dm_public_widget_header {  background-color: #3e3e3e !important;
                                        background-image: none !important;  }

.edit_mode .nav_link, .edit_mode .nav_link .action, .sd_video_homeinfo .video_context,
.lt_homefeed .channel_list .channel_list_item a:hover, div.autocomplete div.suggestions div.selected, 
.pl_video_itemrelated:hover  {     background: #0F52BA !important;
                                   background-image: linear-gradient(#3e3e3e,#0F52BA) !important;
                                   color: white !important;    }

.lt_homefeed .sd_video_homebigitem .sd_video_homeinfo, 
.lt_homefeed .sd_video_homehorizontalitem .sd_video_homeinfo {  background: #3e3e3e !important;
                                                                border-top: 1px solid #3e3e3e !important;
                                                                border-left: 1px solid #3e3e3e !important;
                                                                border-right: 1px solid #3e3e3e !important; }
                                                                
.empty_spot_big, .empty_spot{    background: #111 !important; }
}


/* ::::::::::::::::::::::::::::::::::: PLAYLISTS ONLY ::::::::::::::::::::::::::::::::::::::::::::::: */

@-moz-document url-prefix(http://www.dailymotion.com/playlist/){

.title, .badge--duration, .media-block.mrg-top-sm{ 
font-family: "Cabin Condensed", "Ubuntu Condensed", "Roboto Condensed", "Skia Condensed", "Arial Narrow"  !important; }

/* HIDE */

div.tipsy, .col-8 h1,
#top_banner, scrollbar, #bottom_banner, #push_footer, #footer, #footer *, DIV.page-title.mrg-btm-sm, 
.foreground2.mrg-btm-lg, .foreground2.mrg-btm-md, .sd_metaheader, .sd_footer, .span-12{ display: none !important;   }

.sd_header, #playlist_mode, #playlist_mode_on.on.selected, .mrg-end-md.js-switcher, .pl_videos_listswitcher .js-switcher.clickable[data-list-mode="list"], 
#input_search_shadow, .col-4{  visibility: hidden !important;   }
.col-8{ margin-bottom: -265px !important; padding: 0 !important; }
#playlist_mode_on.on, .js-switcher.clickable{ visibility: visible !important; }

.pl_videos_listswitcher{  position: absolute !important; top: -45px !important; left: 35px !important; }

/* ::::::: WIDE ::::::::: */
#content{    position: absolute !important; top: -90px !important;  bottom: -1020px !important;  }

DIV.row{ margin-top: 30px !important; margin-bottom: 0px !important; margin-left: -4px !important; margin-right: 85px !important;   }
/* :::::::::::::::::::::::::::: MAIN WIDTH ::::::::::::::::::::::::::::::::::::::::::: */

.pages{ z-index: 999999 !important;  position: absolute !important; top: 25px !important; left: 30% !important;  right: 30% !important; }

.column.span-8.left_box, .left_content.span-8, .pl_video_tabvideos.tab_content.clearfix, 
#player_container{  margin-left: -2px !important; width: 117.6% !important; } 

#header_search{ visibility: visible !important; z-index: 999999 !important; 
                position: absolute !important; top: 5px !important; right: 45px !important; min-width: 50px !important;  width: 150px !important;}

#input_search{  margin-right: 2px !important;  min-width: 50px !important;  width: 150px !important; }

#search_submit{  margin-right: -30px !important; }

.col-2{ margin-right: 0px !important; }

.col-2, .preview_link, .now_playing, .span-4.card   { width: 142px !important;  }
IMG.preview                                         { width: 200px !important;  }

.video-list-grid > div{ position: absolute !important; }

.video-list-grid > div:nth-child(1), .video-list-grid > div:nth-child(2),
.video-list-grid > div:nth-child(3){ margin-top: 10px !important; margin-right: 0px !important; }

.video-list-grid > div:nth-child(4), .video-list-grid > div:nth-child(5),
.video-list-grid > div:nth-child(6){ margin-top: 160px !important;  margin-right: 0px !important; }

.video-list-grid > div:nth-child(1),
.video-list-grid > div:nth-child(4){ margin-left: -6px !important;}
.video-list-grid > div:nth-child(2),
.video-list-grid > div:nth-child(5){ margin-left: 420px !important;}
.video-list-grid > div:nth-child(3),
.video-list-grid > div:nth-child(6){ margin-left: 846px !important;}



h3.title{  position: absolute !important; 
           line-height: 9pt !important; 
           text-align: center !important;
           margin-top: -28px !important; 
           max-height: 26px !important; min-height: 26px !important; 
           background: linear-gradient(transparent, #000) !important;
           text-shadow:  #000 0 0 1px, #000 0 0 3px, #000 0 0 5px;
           width: 142px !important; }
           
h3.title a{ color: #fff !important; }

.badge--duration{ margin-right: 5px !important; top: 5px !important; text-shadow:  #000 0 0 1px, #000 0 0 3px, #000 0 0 5px;
                  background: none !important;  }

.title, .badge--duration{ font-size: 8pt !important; }

.js-wl_chkbx.button.wl_done.icon-check,
.watch_later, .wl_chkbx.js-wl_chkbx.button,
div.duration, .pl_video_item h3.sd_video_title{ background: none !important; border: none !important; box-shadow: none !important;
                                                color: #fff !important;
                                                text-shadow: #000 0 0 1px, #000 0 0 2px, #000 0 0 3px  !important;  }
.js-wl_chkbx.button.wl_done.icon-check,
.watch_later:hover, .watch_later:active{ color: rgb(70, 170, 220) !important;
                                         text-shadow: #00f 0 0 1px, #00f 0 0 2px, #00f 0 0 3px  !important;
                                         font-size: 12pt !important;} 
                                                
.pl_video_item h3.sd_video_title {  background: linear-gradient(transparent, #000) !important;  }

.current_video, .preview_link, img.preview, .image_border, .preview_large{ border: none !important; }
.current_video, .preview_link{ box-shadow: #000 0 2px 3px !important;  padding-bottom: 1px !important; }
.media-img{ padding-bottom: 5px !important; margin-bottom: -5px !important; }

.preview_medium{ width: 149px !important; height: 92px !important; }
.media.light_border_top{ width: 149px !important; height: 140px !important; }

.now_playing, div.pl_video_item.selected .preview_link .pmode8,
.current_video, div.preview-message.now_playing{   background: rgba(100, 0, 0, .8) !important; margin-top: -15px !important;  height: 35px !important; right: 52px !important;  }

.now_playing,
.current_playing{ font-size: 0 !important;  padding-bottom: 90px !important;  }

.now_playing::after{ font-size: 40pt; }
.current_playing::after,
div.pl_video_item.selected .preview_link .pmode8::after{ font-size: 50pt;   }

.now_playing::after,
.current_playing::after,
div.pl_video_item.selected .preview_link .pmode8::after{    content: "▶"; color: #fff; 
                            text-shadow:  #f00 0 0 3px, #f00 0 0 5px, #f00 0 0 8px; }

div.pl_video_item.selected .preview_link .pmode8::after{  margin-left: 35%; }
.now_playing::after, .current_playing::after{ margin: 12%; }
#footer{ margin-left: -50px !important; }

.media-block.mrg-top-sm DIV.foreground2{ visibility: hidden !important;  height: 12px !important;   }

.media-block.mrg-top-sm DIV.foreground2 A.link-on-hvr.js-channel-tip{ visibility: visible  !important; margin-left: -12px !important;  }

.media-block.mrg-top-sm > div:nth-child(2){ font-style: italic !important; font-size: 8pt !important; margin: 0px !important; text-align: center !important; }
}
