css:
@-moz-document domain("www.emudesc.com") {
.node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
	background-image: url('http://i.imgur.com/BmHc80z.png');
	background-repeat: no-repeat;
	background-position: 0 0;
}

.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
	background-image: url('http://i.imgur.com/BmHc80z.png');
	background-repeat: no-repeat;
	background-position: -48px 0;
}

.node .pageNodeInfo .nodeIcon {
	background-image: url('http://i.imgur.com/BmHc80z.png');
	background-repeat: no-repeat;
	background-position: -48px 0;
}

.node .linkNodeInfo .nodeIcon {
	background-image: url('http://i.imgur.com/BmHc80z.png');
	background-repeat: no-repeat;
	background-position: -96px 0;
}

.node .nodeIcon {
	margin: 10px 0 10px 10px;
	float: left;
	width: 48px;
	height: 48px;
}

.node .nodeText
{
	margin: 10px 270px 10px 65px;

}
}
