css:
@-moz-document url-prefix("http://vaughnlive.tv") {

#vn_body {
	background-repeat: repeat !important; 
}

div.header_wrapper {
	height: 60px !important; 
  	background: url('http://i.imgur.com/0s00KmR.png') !important;
	background-repeat: repeat !important;
}

div.header {
	height: 20px !important; 
}

.mvn_hdr_wrapper {
	display: none !important;
}
  
.mvn_roundup {
	border-radius: 1px !important;
	background: #bdbdbd !important;
}

.mvn_info_wrapper {
  	background: #bdbdbd !important;
}

.mvn_info_content {
	background: #f2f2f2 !important;
}

.topbar {
	background: #f2f2f2 !important;
}
  
.channel {
	background: #bdbdbd !important;
}

div.header_menu_wrapper {
	margin-top: -57px !important; 
  	background: none !important;
	border-bottom: none !important;
}
  
div.header_menu {
	height: 50px !important;
  	width: 1090px !important;
	padding-bottom: 20px !important;
	margin-top: 3px !important;
}
 
div.header_menu_left {
 	position: absolute !important;
	z-index: 99 !important;
  	width: 300px !important;
	height: 32px !important;
	padding-left: 70px !important;
	float: left !important;
}

div.header_menu_right {
  	width: 1090px !important;
	height: 32px !important;
	padding: 0px !important;
	clear: left !important;
	position: absolute !important;
}

div.header_left {
	height: 60px !important;
	width: 60px !important;
}

div.header_right {
	display: none !important;
}


div.header_logo {
  	background-image: url('http://i.imgur.com/VihtOul.png') !important;
  	background-position: 50% 50% !important;
  	background-repeat: no-repeat !important;
	width: 60px !important;
	height: 60px !important;
	z-index: 9999 !important;
	margin-top: -25px !important;
}

.channel_left {
	border-radius: 1px !important;
}

.barbtn {
	background: none !important;
	color: #fff !important;
	border: none !important;
}

.barbtn:hover {
	color: #ccc !important;
}

.barbtn_pic {
	height: 28px !important;
	margin-left: -40px !important;
	position: absolute !important;
}
    
.barbtn_line_wrap {
	display: none !important;
}

.barbtn_ma {
	background: #2f2f2f !important;
}

.button {
	font-size: 12px !important;
  	border-radius: 2px !important;
}

.button_blue {
	background: #0f0f0f !important;
	border: 1px solid #585858 !important;
}

.button_blue:hover {
	background: #404040 !important;
	color: #fff !important;
}
  
.button_red {
	background: #c53f46 !important;
	border: 1px solid #585858 !important;
}

.button_red:hover {
	background: #d75e5e !important;
	color: #fff !important;
}

.channel_content_right_2{
	display: none !important;
}

.channel_info_wrapper {
	display: none !important;
}
  
.channel_info_wrapper iframe {
	display: none !important;
}

.channel_header_img {
	border-radius: 1px !important;
}

.channel_info_content {
    display: none !important;
}

.adsbygoogle {
  	display: none !important;
}
  
.footer_bar{
	display: none !important;
} 
    
.footer_content {
	margin-bottom: 30px !important;
	background: url('http://i.imgur.com/0s00KmR.png') !important;
	background-repeat: repeat !important;
}

.footer_content_wrapper {

}
}
