css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("learnperl.scratchcomputing.com") {

  h1,h2,h3,h4,h5
  {
    text-shadow: gray 0px 0px 2mm !important;
  }
  
  body
  {
    background: url("data:image/png;base64,") rgb(34,34,34) !important;    
    border-color: rgb(64,64,64) !important;          
  }
  body *
  {
    background-color: transparent !important;
  }
  
  body
  {
    align-content: center !important;
    width: 100% !important;
    max-width: 100% !important; /* this is the trouble maker that actually restricts the width at the time of this scripts creation (aug 2013)*/
  }
  div#header
  {    
    align-self: center !important;
    display: block !important;
    text-align: center !important;
    border-radius: 14px !important;
    margin: 14px !important;
    padding: 14px !important;
    background: none !important;
    border: 1px gray solid !important;
    box-shadow: gray 0px 0px 7px !important;
    text-shadow: lime 0px 0px 7px !important;
    font-style: italic !important;
    font-family: "Times New Roman" !important;
    font-size: 32pt !important;
  }
  div#header:after
  {
    display: block;
    text-align: right;
    text-shadow: none;
    font-style: normal;
    font-size: 7pt;
    font-family: "arial";
    opacity: 0.1;
    content: "dark/wide lp [get latest version at userstyles.org/search?q=lernperl+dark+wide+osirisgothra" !important;
  }
  div#header:hover:after  { opacity: 1 !important;;  }
  
  
  div#content
  {
    text-align: justify !important;
    color: ghostwhite;
  }
  div#header *, div#content *
  {
    width: 100% !important;    
    color: ghostwhite;
    
  }
  
  pre { margin-left:1em; font-family: "liberation mono","lucida console","monospace","mono"; font-size: 12pt; color: lightgoldenrodyellow !important;}
  .perl { color: slategray !important; margin-left: 0em;}
  .synLineNumber { color: blue !important; }
  .synPreProc { color: lightblue !important;}
  .synStatement { color: cyan !important;}
  .synConstant { color: lightgreen !important; font-weight: bold !important; }
  .synIdentifier { color: white !important; font-weight: bold !important;}
  
  pre+p
  {
    color: rgba(0,0,0,0) !important;
    font-style: italic;
    font-weight: bold;
    font-size: 6pt !important;
    max-height: 16px !important;
    max-width: 16px !important;    
    overflow:  hidden !important;
    background-image: url("http://alvinalexander.com/images/icons/search.png") !important;
    background-size: 16px !important;
    background-attachment: local !important;
    opacity: 0.2;
    
  }
  pre+p:hover
    {
      color: whitesmoke !important;
      max-width: 320px !important;
      border: 1px dotted gray;
      border-radius: 16px;
      padding: 16px;
      max-height: 100% !important;
      background-color: rgba(0,0,0,0.15) !important;;
      font-size: 12pt !important;
      overflow: unset !important;
      background-image: unset !important;
      opacity: 1.0;
      
    }
  
  
  
}
