css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Login page */
@-moz-document url-prefix("https://cas.sfu.ca/cas") {

  #page-content {
    background-color: #333 !important;
    color: #ccc !important;
  }

  header {
   background: #222 !important;
  }

  .form-background {
    background-color: #333 !important;
  }

  form {
    background-color: #333 !important;
  }

}

@-moz-document domain("/*[[canvas_domain]]*/") {

  /* Basic style */

  body, #wrapper-container {
    background-color: #222 !important;
  }

  body, td, pre {
    color: #ccc !important;
  }

  #main {
    background-color: #222 !important;
    border-color: #666 #666 #666 !important;
  }

  img:hover {
     background-color: #fff !important;
  }

  #content {
    color: #ddd !important;
    background-color: #333 !important;
  }

  #breadcrumbs {
    background: #222 !important;
    border-bottom: 0px solid #333 !important;
  }

  #breadcrumbs > ul > li + li:last-of-type a {
    color: #666 !important;
  }

  .header-bar {
    background: url('https://canvas.sfu.ca/sfu/images/clf-header-bg-rainbow.png') repeat scroll 0% 0% #6A7177;
  }

  .item-group-container {
    color: #ddd !important;
    background-color: #333 !important;
  }

  .item-group {
    background: #222 !important;
  }


  /* Dashboard */
  .ic-DashboardCard__header_content {
    background-color: #272727 !important;
  }

  .ic-DashboardCard__action-container {
    background-color: #222 !important;
  }


  /* Main Sidebar */

  .ReactTray__Content {
    background-color: #171717 !important;
  }


  /* Page Sidebar */

  .list-view > header,
  .list-view #section-tabs-header {
    background: #222 !important;
    background-image: none !important;
    color: #aaa !important;
    text-shadow: 0px 1px 1px rgba(255, 255, 255, 0.4);
    border-bottom: 1px solid #666 !important;
    box-shadow: 0px 1px 0px #666 !important;
  }

  .list-view a.active {
    background-color: #666 !important;
  }

  .list-view > ul > li > a,
  .list-view > nav > ul > li > a {
    border-top: 1px solid #666 !important;
    border-bottom: 1px solid #555 !important;
  }

  .list-view > ul > li > a:hover,
  .list-view > ul > li > a:focus,
  .list-view > nav > ul > li > a:hover,
  .list-view > nav > ul > li > a:focus {
    background-color: #555 !important;
  }

  #left-side {
    border-bottom: 1px solid #777 !important;
  }


  /* Modules, homework */

  .list-view > ul > li > a,
  .list-view > nav > ul > li > a {
    color: #eee !important;
  }

  #section-tabs {
    color: #eee !important;
    background-color: #444 !important;
  }

  .table-bordered {
    background: #444 !important;
  }

  .table > thead {
    background: #555 !important;
  }

  .quiz-submission, .question_editing {
    background: #333 !important;
  }

  #questions.assessment_results .question .header {
    background-color: #383838 !important;
  }

  .question .header {
    background-color: #272727 !important;
  }

  #questions.assessing {
    background-color: #333 !important;
  }

  .question {
    background-color: #444 !important;
  }


  /* Announcements */

  .discussionTopicIndexList .discussion-topic:nth-child(2n+1) {
    background-color: #222 !important;
  }

  .discussionTopicIndexList .discussion-topic {
    background-color: #171717 !important;
    border-top: 1px solid #353535 !important;
  }

  body.ff .discussionTopicIndexList .discussion-topic:nth-child(2n+1) .ellipsis::after {
    background: linear-gradient(to right, rgba(255,255,255,0) 0%, #222 75%) !important;
  }

  body.ff .discussionTopicIndexList .discussion-topic .ellipsis::after {
    background: linear-gradient(to right, rgba(255,255,255,0) 0%, #171717 75%) !important;
  }

  .discussionTopicIndexList .discussion-summary {
    color: #999 !important;
  }

  .discussionTopicIndexList .discussion-unread-container,
  .discussionTopicIndexList .discussion-last-reply,
  .discussionTopicIndexList .discussion-info-icons {
    color: #555 !important;
  }


  /* Discussions */

  .discussion-collections {
    background: #222 !important;
  }

  .discussion {
    background: #272727 !important;
    border: 1px solid #555 !important;
  }

  .ig-header, .ig-text {
    color: #ccc !important;
    text-shadow: 1px 1px 0px #444 !important;
  }

  .discussion-list .title-help {
    color: #888 !important;
  }

  .toolbarView,
  .toolbarView .headerBar {
    background: #171717 !important;
  }

  .topic .entry-content {
    background: #272727 !important;
  }

  .discussion_entry {
    background: #333 !important;
  }

  .discussion-entries .entry {
    /* effects padding color */
    background: #333 !important;
    border: 1px solid #666 !important;
    box-shadow: 0px 1px 0px #444 !important;
  }

  .entry > .bottom-reply-with-box .discussion-entry-reply-area {
    border-top: 1px solid #555 !important;
  }

  .discussion-entry-reply-area {
    background: #333 !important;
  }

  .discussion-reply-box {
    background: #272727 !important;
  }

  .discussion-pubdate {
    color: #777 !important;
  }

  .equation_image {
    background: #fff !important;
    filter: invert(100%) !important;
    -webkit-filter: invert(100%) !important;
    padding: 5px !important;
  }


  /* Submission details */

  .submission-details-comments .comments {
    background-color: #222 !important;
  }


  /* People > Everyone */
  /* table-striped is from old canvas site */

  .ui-tabs .ui-tabs-panel {
    background: #333 !important;
  }

  .table-striped,
  .table-striped > thead {
    border: 1px solid #4F4F4F !important;
  }

  .table-striped > tbody > tr td {
    background-color: #222 !important;
  }

  .ic-Table.ic-Table--striped tbody tr:nth-child(2n+1),
  .table-striped tbody tr:nth-child(2n+1) td,
  .table-striped tbody tr:nth-child(2n+1) th {
    background-color: #222 !important;
  }

  .ic-Table.ic-Table--striped tbody tr {
    background-color: #171717 !important;
  }

  .table th, .table td {
    border-top: 1px solid #4F4F4F !important;
  }


  /* People > Groups table */

  .student-groups {
    border: 1px solid #222 !important;
  }

  .student-groups.show-body .student-group-header {
    background-color: #272727 !important;
    border-bottom: 1px solid #444444 !important;
  }

  .ui-widget-content {
    background: #272727 !important;
    color: #ccc !important;
  }


  /* Assignments */
  .accessible-toggler {
    color: #999 !important;
  }

  .item-group-condensed .ig-row {
    background-color: #1f1f1f !important;
    border-bottom: 1px solid #272727 !important;
  }

  .ig-list .ig-row a.ig-title {
    color: #999 !important;
  }

  .ig-list .ig-row {
    border: 1px solid #272727 !important;
  }

  .item-group-condensed .ig-list li:first-child .ig-row,
  .item-group-condensed .ig-list li:first-of-type .ig-row,
  .item-group-condensed .first_visible .ig-row {
    border-top: 1px solid #272727 !important;
  }


  /* Grades table */

  #grades_summary tr > * {
    border-top: 2px solid #222 !important;
    background-color: #303030 !important;
    opacity: 0.7;
  }

  #grades_summary tr > *:hover {
    border-top: 2px solid #222 !important;
    background-color: #333 !important;
    opacity: 1.0;
  }

  /* Graded items */

  #grades_summary tr.assignment_graded > * {
    border-top: 2px solid #222 !important;
    background-color: #303030 !important;
    opacity: 1.0 !important;
  }

  /* Right side table */

  #right-side {
    background-color: #222 !important;
    border: 0px solid #222 !important;
  }

  #right-side table.summary td,
  #right-side table.summary tbody th {
    background-color: #272727 !important;
  }

  /* Gear button */

  .dim-till-hover {
    opacity: 0.7 !important;
  }

  .Button--icon-action-rev:focus {
      box-shadow: inset 0 0 0 0px #333;
  }

  /* Inbox */

  .conversations .panel {
    background-color: #222 !important;
  }

  .message-list .messages > li {
    background-color: #272727 !important;
    border-bottom: 1px solid #444 !important;
  }

  .message-list .messages > li.active,
  .message-list .messages > li:hover {
    background-color: #2c2c2d !important;
  }

  .message-actions .read-state {
    background: #0096db !important;
    /* background: #555 !important; */
  }


  /* Calendar */

  #calendar-app .fc-month-view .fc-body,
  #calendar-drag-and-drop-container .fc-month-view .fc-body {
    background-color: #222 !important;
  }

  #calendar-app .fc-state-highlight,
  #calendar-drag-and-drop-container .fc-state-highlight {
    background-color: #272727 !important;
  }

  #calendar-app .fc-event,
  #calendar-drag-and-drop-container .fc-event {
    background-color: #070707 !important;
  }

  .calendar .fc-widget-header,
  .calendar .fc-widget-content,
  #calendar-drag-and-drop-container .fc-widget-header,
  #calendar-drag-and-drop-container .fc-widget-content {
    border-color: #444 !important;
  }

  .calendar .fc-row .fc-content-skeleton td,
  .calendar .fc-row .fc-helper-skeleton td,
  #calendar-drag-and-drop-container .fc-row .fc-content-skeleton td,
  #calendar-drag-and-drop-container .fc-row .fc-helper-skeleton td {
    border-color: #444 !important;
  }

  #minical {
    background-color: #111 !important;
    border: 0px solid #111 !important;
  }

  #minical .fc-widget-content {
    border: 1px solid #171717 !important;
  }

  #context-list-holder, #undated-events {
    background-color: #171717 !important;
    border: 1px solid #171717 !important;
  }

  #context-list .context_list_context:hover {
    background-color: #222 !important;
  }

}
