css:
/* To be removed and applied to all sites */
@-moz-document domain("nanapro.org") {
body, body *
{
  font-family: sans-serif !important;
}

code, code *
, pre, pre *
, .code, .code *
, .pre, .pre *
{
  font-family: monospace !important;
}

.glyphicon, [class*="glyphicon="]
{
  font-family: "Glyphicons Halflings" !important;
}

.fa
{
  font-family: FontAwesome !important;
}
}

@-moz-document domain("github.com") {
[class*="octicon"]:not(#fakeId)
, .diffstat-bar:not(#fakeId)
, .plus:not(#fakeId)
{
  font-family: octicons !important;
}

.highlight, .highlight *
{
  font-family: monospace !important;
  font-size: 1.02em !important;
}
}

@-moz-document domain("gitlab.com")/*, domain("git.zheng-exp.lab")*/ {
[class*="icon"]:not(#fakeId)
{
  font-family: FontAwesome !important;
}

.file .content table, .file .content table *
, .diff-content, .diff-content *
{
  font-family: monospace !important;
  font-size: 1.02em !important;
}

code, code *
{
  font-size: 1.02em !important;
}

.notes_holder:not(#fakeId), .notes_holder *:not(#fakeId):not([class*="icon"])
{
  font-family: sans-serif !important;
  font-size: 1em !important;
}
}

@-moz-document domain("nanapro.org") {
[style*="Courier"], [style*="Courier"] *
, [class*="auto-style"], [class*="auto-style"] *
{
  font-family: monospace !important;
}
}

@-moz-document domain("uuu9.com") {
[class*="lst"] li, [class*="lst"] li *
{
  font-size: 13px !important;
}

.mapdown li:not(#fakeId)
{
  width: 172px !important;
}

.mapdown li:not(#fakeId), .mapdown li *:not(#fakeId)
{
  font-size: 12px !important;
}
}

@-moz-document domain("taobao.com"), domain("tmall.com"), domain("alipay.com") {
.g-icon
{
  font-family: "global-iconfont" !important;
}

.s-icon, [class*="s-tool"]
{
  font-family: "mytaobao2014" !important;
}

.iconfont
{
  font-family: rei !important;
}

.tb-fp-iconfont
{
  font-family: tb-fp-iconfont !important;
}
}

@-moz-document domain("tudou.com") {
.iconfont
{
  font-family: tuiicon !important;
}
}
