css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fanfare.metafilter.com") {

body { background: #273e54 !important; color: #ffffff!important;}
#navglobal a, #navseldom a, #navoften a, .posttitle a, a:link, .smallcopy a { color: #cc0!important;}
a.signout {color:#ccc!important;}
a {color:#cc0!important;}
a:visited {color: #cc9!important;}
a:hover {color: white!important;}
a:link:visited, .smallcopy > a:visited {color: #cc9!important;}
#navglobal a:hover, #navseldom a:hover, #navoften a:hover, a:link:hover, .smallcopy > a:hover { color: white!important;}
a.vid {background:url("http://d217i264rvtnq0.cloudfront.net/images/mefi/youtube_yellow.png");background-repeat:no-repeat;background-position:0px 1px;}
a.vid img{visibility:hidden!important;}
#bottomline, #topline, .sidebar,#footer,.tags {
background: none repeat scroll 0 0 #001c37!important;}
#maintab {border-bottom:1px solid #777!important;}
#maintab li a {background-color: #001c37!important; color:#cc0!important; border:1px solid #777!important;}
#maintab li.selected a {background-color:#273e54!important; color:#cc0!important; border-bottom:none!important;}
#maintab li a:visited {color:#cccc00!important;}
#maintab li a:hover {color:#cccc00!important; background-color:#273e54!important;}
#footer {border-top: 1px solid #C9CC94!important;}
#yellowbar {
background: none repeat scroll 0 0 #cccc00!important;
border-bottom: 1px solid white!important;
border-top: 1px solid white!important;}
div.copy {background-color:#273e54!important;border-left:none!important;}
#tv {background-color:#001c37!important;}
#lightbox {background-color: #001c37!important;}
#prevDiv {background-color: #001c37!important;}
#prevDiv2 {background-color: #001c37!important;}
#deck {background-color: #001c37!important;border-left:1px solid white!important; border-right:1px solid white!important;}
.smallcopy {color: #cccccc!important;}
.tags a {color:#ccc!important}
.mefimessages {color: white!important;}
.button {border:solid 1px #273e54!important;}
.button:hover {border:solid 1px #273e54!important;}
.mefimessages > a {color: #cccc00!important;}
#spoiler a {color:#cccc00!important;}
#loc {background-color:#001c37!important;}
.posttitle {color: #ccc!important;}
.posttitle a {color:#cc0!important;}
.posttitle a:visited {color:#cc9!important;}
.posttitle a:hover {color:white!important;}
.dtype a {color:white!important;}
.dtype a:visited {color:white!important;}
.copy.warning {background-color:#001c37!important;border-left:1px solid #cccccc!important;}
.sidebar {background-color:#001c37!important;}
.sidebar a:visited {color:#cc9!important;}
.sidebar a:hover {color:white!important;}
.whitesmallcopy {color: #FFFFFF!important;}
.new {color:white!important;}
.new a {color:white!important;}
a.new {color:white!important;}
a.new a {color:white!important;}
b.new {color:white!important;}
b.new a {color:white!important;}
#addphotos {background-color:#001c37!important;}
#photoinput {color:#333!important;}
#photoinput2 {color:#333!important;}
#newcommentsmsg {background:#001c37!important;}
}
