css:
@-moz-document domain(publiclir.se) {
   html { 
  background: url('http://enkelkodning.se/bilder/background.png') no-repeat center center fixed;
  -webkit-background-size: cover;
  -moz-background-size: cover;
  -o-background-size: cover;
  background-size: cover;
  color:#fff;
  text-shadow:0px 1px 1px #fff;

}



.wf-loading #navigation{
    visibility: hidden;
  }

div#socialbar {
  width: 100%;
  height: 63px;
  background-repeat: repeat;
}

div#socialbar div.container {
  width: 1100px;
  margin-left: auto;
  margin-right: auto;
}

div#socialbar div.statement {
  float: left;
  width: 790px;
  color: #7e7e7e;
  font-family: 'Droid Sans', sans-serif;
  font-size: 12px;
  padding-top: 24px;
}

div#socialbar div.statement span.source {
  color: #424242;
}

div#socialbar ul.icons {
  float: right;
  margin-left: 30px;
  list-style-type: none;
}

div#socialbar ul.icons li {
  float: left;
  padding: 14px 5px;
}

div#maincontent {
 min-height: 1500px;
}

div#content {
  width:780px;
  background:#424242;
  background: #424242;
  background-repeat: repeat;
  padding-top: 10px;
}

div#spbar {
  width:780px;
  margin-bottom: 20px;
}

div#spbar h3.headline {
  width: 100%;
  height: 30px;
}

.text {
color:#fff;
text-shadow:none;
}

div#spbar h3.headline span.bar span.text {
  font-size: 18px;
  font-weight: 500;
  text-transform: uppercase;
  color: #fff;
  text-shadow:none;
  display: block;
  float: left;
  padding-right: 10px;
  margin-left: 10px;
  margin-top: -3px;
  font-family: "proxima-nova", arial;
  margin-bottom: 2px;
}

div#spbar h3.headline span.bar {
  background-color: rgba(255, 255, 255, 0.11);
  display: inline-block;
  width: 100%;
  padding: 9px 0 5px 0;
}

div#spbar div.spcon {
  width:760px;
  height: 62px;
  padding: 18px;
  background:rgba(25,25,25,.0);
  background-repeat: repeat;
}

#now_publiclir_stats {
  background:#fff;
  color:#000;
  text-shadow:0px 0px 0px;
  font-family:Trebuchet MS;

}

div#spbar div.spcon img {
  background:green;
  border-radius:15px;
}
div#container {
  min-height: 500px;
  margin-bottom: 20px;
}

div#header {
  width: 1100px;
  background:rgba(25,25,25,.0);
  
  background-repeat: repeat;
  margin-bottom: 20px;
  margin-left: auto;
  margin-right: auto;
}

h1#logo {
 font-size: 0;
  color: transparent;
  display: block;
  width: 280px;
  height: 63px;
  background:rgba(25,25,25,.0);
  margin-top: 27px;
  line-height: 1;
  margin-left: 30px;

  padding-top: 10px;
  padding-bottom: 10px;
  margin-left: 35px;
}

h1#logo img {
  
}

ul#navigation {
  list-style-type: none;
  float: right;
  margin-right: 30px;
}

ul#navigation li {
  float: left;
  margin-left: 10px;
  padding: 32px 0 28px 0;
  color: #EBEBEB;
}

ul#navigation li:hover {
  border-top: solid 3px #1B2125;
  background-color: #5C5C5C;
  color: #367;
}

ul#navigation li:hover a {
color: #367;
  margin-top: -3px;
  -webkit-transition: none;
  -moz-transition: none;
  -ms-transition: none;
  -o-transition: none;

}

ul#navigation li a {
  font-family: "proxima-nova", arial;
  font-size: 14px;
  color: #367;
text-shadow:0px 1px 1px #000;
  font-weight: bold;
  text-transform: uppercase;
  text-decoration: none;
  display: block;
  -moz-border-radius: 3px;
  border-radius: 3px;
  padding: 3px 5px;
  -webkit-transition: none;
  -moz-transition: none;
  -ms-transition: none;
  -o-transition: none;
}

ul#navigation li a:hover {
color: #367;
}

ul#navigation li a.active {
}

ul#navigation li.submenu {
  display: block;
}

ul#navigation li.submenu a {
  display: block;
}

ul#navigation li.submenu ul {
  padding: 0;
  margin: 0;
  margin-top: 28px;
  padding: 10px 0 10px 0;
  padding-right: 16px;
}

ul#navigation li a.registernav:hover {
    background:#424242;
}

ul#navigation li ul
{
  margin: 0;
  position: absolute;
  visibility: hidden;
  z-index: 500;
  margin: 0;
  background-color: #5C5C5C;
  -webkit-box-shadow: 0px 10px 24px rgba(50, 50, 50, 0.91);
  -moz-box-shadow: 0px 10px 24px rgba(50, 50, 50, 0.91);
  box-shadow: 0px 10px 24px rgba(50, 50, 50, 0.91);
  min-width: 150px;
  padding-right: 8px;
}

ul#navigation li.submenu ul li
{
  float: none;
  display: inline;
  vertical-align: middle;
  padding: 0;
  margin: 0;
}



ul#navigation li.submenu ul li img.arrow {
  position: absolute;
  margin-top: -7px;
  margin-left: 20px;
}

ul#navigation li.submenu ul li a
{
  padding-top: 8px;
  padding-bottom: 8px;
  padding-right: 8px;
  padding-left: 8px;
  width: 100%;
  display: block;
  margin: 0;
  color: #367;
  vertical-align: middle;
  -moz-border-radius: 0px;
  border-radius: 0px;  
  border: 0;
}

ul#navigation li.submenu ul li a:hover
{
  color: #367 !important;
}

ul#navigation li.submenu ul li a.top
{
  margin-top: -10px;
}

ul#navigation li.submenu ul li a.bottom
{
  margin-bottom: -10px;
}


div#statsbar {
  width: 1100px;
  background:#424242;
  background: rgba(255, 255, 255, 0.13);
  background-repeat: repeat;
  margin-bottom: 20px;  
  margin-left: auto;
  margin-right: auto;
}

div#statsbar div.topic {
  font-family: 'Droid Sans', sans-serif;
  font-size: 15px;
  width: 151px;
  text-align: center;
  color: #ffffff;
  font-weight: bold;
  text-transform: uppercase;
  float: left;
  display: block;
  background: rgba(255, 255, 255, 0.27);
  background:#424242;
  background-repeat: repeat;
  padding: 5px;
  margin: 4px;
}

.server-table-accordion-link even a {
color:#367;
text-decoration:none;
}

server-table-accordion-link even a:hover {
color: #367;
text-decoration:underline;
}

div#statsbar div.statement {
  font-family: 'Droid Sans', sans-serif;
  font-size: 12px;
  color: #ffffff;
  float: left;
  margin-left: 9px;
  margin-top: 10px;
  font-weight: normal;
}

div#wrapper {
  width: 1100px;
  margin-left: auto;
  margin-right: auto;
}

div#statsbar .padding {
  padding: 10px;
}

div.bigbox {
  border: 1px solid rgba(0, 0, 0, 0.3);
  -moz-border-radius: 3px;
  border-radius: 3px;
  background-color: #fafaf6;
  margin-bottom: 33px;
}

div#sidebar {
}

img.sidebarheadline {
  margin-bottom: 13px;
}

div#user, div#admin, div#googletranslate {
  border: 1px solid rgba(0, 0, 0, 0.3);
  -moz-border-radius: 0px;
  border-radius: 0px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
  background-color: #fafaf6;
}

div#gameservers_right {
  border: 1px solid rgba(0, 0, 0, 0.3);
  -moz-border-radius: 0px;
  border-radius: 0px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
  background-color: #fafaf6;
  padding: 3px;
}

.servers-right-table {
width: 100%;
}

.servers-right-table a {
  color: #fff;
text-shadow:0px 1px 1px #000;
  
}

.servers-right-table tr {
width: 100%;
}

.servers-right-table tr.odd {
background-color: #f1f1f1;
}

.server-table-accordion-link td {
  padding: 2px !important;
  font-size: 12px;
}

.server-table-accordion-link td img {
  width: 14px;
  height: 14px;
}

.server-table-accordion-link:hover {
cursor: pointer;
background-color: #ccc !important;
}

.server-table-accordion-target {
display: none;
font-size: 12px;
}

.server-table-accordion-link-selected {
background-color: #ccc !important;
}

.server-table-accordion-target td {
border-width: 0px 2px 2px 2px;
border-style: solid;
border-color: #ccc;
padding: 2px !important;
}

.gameservers_right_playerstable, .gameservers_right_playerstable tr {
    width: 100%;
}

.gameservers_right_playerstable tr td {
  border: 0;
}

div#search {
  border: 1px solid #d4d4d4;
  -moz-border-radius: 4px;
  border-radius: 4px;
  height: 34px;
  background:#424242;
}

div#search input {
  width: 204px;
  height: 34px;
  font-family: "proxima-nova", arial;
  font-size: 12px;
  font-weight: bold;
  color: rgba(0, 0, 0, 0.3);
  border: 0;
  margin: 0;
  float: left;
  background:#424242;
  padding: 0px 5px 0px 30px;
}

div#search img {
  border:0;
  width: 59px;
  height: 34px;
  padding: 0;
  margin: 0;
  float: left;
}

div#search img:hover {
  cursor: pointer;
}

div#search_argument_categories {
  position: absolute;
  z-index: 1000;
  width: 298px;
  border: 1px solid rgba(0, 0, 0, 0.3);
  -moz-border-radius: 8px;
  border-radius: 8px;
  background-color: #fafaf6;
  margin-top: 16px;
  -webkit-box-shadow: 0px 10px 24px rgba(50, 50, 50, 0.91);
  -moz-box-shadow: 0px 10px 24px rgba(50, 50, 50, 0.91);
  box-shadow: 0px 10px 24px rgba(50, 50, 50, 0.91);
}

div#search_argument_categories div.arrowup {
  font-size: 0;
  width: 13px;
  height: 7px;
  background:#424242;
  margin-top: -7px;
  margin-right: 24px;
  float: right;
}

div#search_argument_categories ul {
  list-style-type: none;
  -webkit-box-shadow: 0px 10px 24px rgba(50, 50, 50, 0.91);
-moz-box-shadow: 0px 10px 24px rgba(50, 50, 50, 0.91);
box-shadow: 0px 10px 24px rgba(50, 50, 50, 0.91);
}

div#search_argument_categories ul li {
  
}

div#search_argument_categories ul li a {
  display: block;
  padding: 10px 15px;
  font-family: 'Varela Round', sans-serif;
  font-size: 14px;
  color: #367;
  font-weight: bold;
  text-decoration: none;
}

div#search_argument_categories ul li a:hover {
  background-color: #367;
}

div#search_argument_categories ul li a img {
  float: right;
}

div#search_argument_categories ul li a.bottomborder {
  border-bottom-width: 1px;
  border-style: solid;
  border-color: #367;
}

div#search_argument_categories ul li a.top {
  border-top-left-radius: 8px;
  border-top-right-radius: 8px;
}

div#search_argument_categories ul li a.bottom {
  border-bottom-left-radius: 8px;
  border-bottom-right-radius: 8px;
}

div#facebookbox {
  padding: 5px;
  border: 1px solid rgba(0, 0, 0, 0.3);
  -moz-border-radius: 0px;
  border-radius: 0px;
  background-color: #ffffff;
  margin-bottom: 33px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
}


a {
  color:#367;
}

li a {
  color:#367;
}
div#gamingnews {
  margin-bottom: 33px;
}

div#latestthreads, div#latestactions, div#sponsors {
  padding: 5px;
  border: 1px solid rgba(0, 0, 0, 0.3);
  -moz-border-radius: 0px;
  border-radius: 0px;
  background-color: #ffffff;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
}

div#footer {
  width: 100%;
  font-family: "proxima-nova", arial;
  background:#424242;
  margin-top: 30px;
}

div#footer div.navigation {
  margin-left: auto;
  margin-right: auto;
  width: 1100px;
  padding: 25px 10px 10px 10px;
}

div#footer div.navigation ul {
  list-style-type: none;
  line-height: 30px;
}

div#footer div.navigation ul li {
  float: left;
  text-transform: uppercase;
  font-weight: bold;
  color: #fff;
  font-size: 14px;
}

div#footer div.navigation ul li.spacer {
  margin-right: 100px;
}

div#footer div.navigation ul li ul li {
  float: none;
}

div#footer div.navigation ul li ul li, div#footer div.navigation ul li ul li a {
  text-transform: none;
  font-weight: normal;
  color: #367;
text-shadow:0px 1px 1px #000;
  font-size: 12px;
  text-decoration: none;
}

div#footer div.navigation ul li div.footersponsors {
  width: 190px;
}

div#footer div.navigation ul li div.footersponsors a img {
  float: left;
  margin: 10px;
}

div#footer div.copyright {
  font-family: 'Varela Round', sans-serif;  
  font-size: 12px;
  color: #ffffff;
  text-transform: uppercase;
  border-bottom-width: 1px;
  border-bottom-style: solid;
  border-bottom-color: #ffffff;
  margin-top: 60px;
  padding-bottom: 12px;
}

div.subnavigation div.topnav {
  height: 37px;
  background:rgba(25,25,25,.0);
  border-top-left-radius: 5px;
  border-top-right-radius: 5px;
  border-bottom-right-radius: 0px;
  border-bottom-left-radius: 0px;
}

div.subnavigation div.topnav ul {
  list-style-type: none;
  margin-left: 5px;
}

div.subnavigation div.topnav ul li {
  float: left;
  margin-left: 15px;
}

div.subnavigation div.topnav ul li a {
    color: #367;
text-shadow:0px 1px 1px #000;
  text-decoration: none;
  font-family: 'Varela Round', sans-serif;
  font-size: 13px;
  font-weight: 600;
  display: block;
  height: 28px;
  border-top-left-radius: 3px;
  border-top-right-radius: 3px;
  border-bottom-right-radius: 0px;
  border-bottom-left-radius: 0px;
  padding: 13px 15px 0px 15px;
  margin-top: -4px;
}

div.subnavigation div.topnav ul li a.active {
  color: #367;
  background:rgba(25,25,25,.0);
}

div.subnavigation div.topnav ul li a:hover, div.subnavigation div.topnav ul li a.active:hover {
  background:rgba(25,25,25,.0);
  color: #367;
}

div.subnavigation div.subnav {
  height: 37px;
  background:rgba(25,25,25,.0);
}

div.subnavigation div.subnav ul {
  list-style-type: none;
  margin-left: 31px;
}

div.subnavigation div.subnav ul li {
  float: left;
  margin-right: 5px;
  padding-top: 10px;
}

div.subnavigation div.subnav ul li.bullet {
  color: #ffffff;
  font-family: 'Droid Sans', sans-serif;
  font-weight: bold;
  line-height: 1em;
  margin-top: 3px;
}

div.subnavigation div.subnav ul li a {
  color: #367;
  text-decoration: none;
  font-family: 'Droid Sans', sans-serif;
  font-size: 12px;
  font-weight: bold;
  line-height: 1em;
  display: block;
  padding: 3px 5px;
  border-radius: 3px;
}

div.subnavigation div.subnav ul li a.active {
  color: #367;
  background:#424242;
}

div.subnavigation div.subnav ul li a:hover, div.subnavigation div.subnav ul li a.active:hover {

}

.copyright:after {
content: "                         Omgjord av ViKiNG TiGER... Grrr.... raaaawrr!!!!";
text-align:center;
border-bottom:5px solid red;
padding:5px;
background:rgba('25,25,25,.5');
border-radius-left-top:5px;
border-radius-right-bottom:5px;
font-size:30px;
}
}
