css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("social.xfire.com") {

.header_bottom,
.footer,
.friends_playing,
.widget.list .item p.currently_playing img,
.fb,
#uvTab {
  display: none !important;
}

.header {
  box-shadow: none !important;
}
    
.header .navigation a.home.xfire {
  background: #726C80 !important;
}
    
.header .header_top {
  background: #1C1C1C !important;
}
    
body {
  background-color: #141414 !important;
}

.content {
  background: none !important;
  border: 0 !important;
}
    
.player_card {
  background: #1C1C1C !important;
  border-radius: 0 !important;
  box-shadow: none !important;
  color: #888888 !important;
  font-weight: normal !important;
}
    
.player_card .profile .player_info ul li > span {
  color: #BBBBBB !important;
  font-weight: normal !important;
}
    

    
.player_card .player_stats .stat.time:before,
.player_card .player_stats .stat.hours:before,
.player_card > .currently_playing div.game-info .game_info p.stat.time:before, 
.player_card > .currently_playing div.game-info .game_info p.stat.hours:before {
  background-image: url(data:image/png;base64,) !important;
}
    
.player_card .player_stats .stat.screenshots:before {
  background-image: url(data:image/png;base64,) !important;
}
    
.player_card .player_stats .stat.videos:before {
  background-image: url(data:image/png;base64,) !important;
}
    
.player_card > .currently_playing div.game-info .game_info p.stat.rank:before {
  background-image: url(data:image/png;base64,) !important;
}
    
.player_card > .currently_playing div.game-info .game_info p .detailed {
  color: #BBBBBB !important;
}
    
.player_card > .currently_playing h2 {
  margin-left: -23px !important;
  margin-right: 5px !important;
  text-align:center
}
    
.profile {
  background: #232323 !important;
  border-radius: 0 !important;
  box-shadow: none !important;
}
    
.player_card > .currently_playing div.game-info {
  background-color: #232323 !important;
  border-radius: 0 !important;
  box-shadow: none !important;
}
    
.game_info {
  margin-top: 30px !important;
}
    
.widget.list .item.green {
  border-color: #3D393F !important;
}
    
.widget.list .item.more {
  border-radius: 0 !important;
  box-shadow: none !important;
}
    
.widget.list .item p.status span {
  color: #888888 !important;
  margin-left: 1px !important;
}
    
.widget.list .item p.currently_playing a {
  color: #888888 !important;
  margin-left: -2px !important;
}
    
.widget.list .item p.community_name {
  color: #888888 !important;
}
    
.box,
body.action-show.controller-games .game_card .details .detailed_tabs {
  border-radius: 0 !important;
  box-shadow: none !important;
}
    
.box.activity_wrapper .box.activity_item .user_block img,
.box.activity_wrapper .box.activity_item .activity_inner img {
  border-color: #232323 !important;
}
    
body.controller-users.action-show table.games td a,
body.controller-users.action-show table.games th,
.player_card > .currently_playing div.game-info .game_info .title-holder a,
body.controller-accounts .edit-header h2,
body.action-index.controller-potential_friends .box h2,
body.action-index.controller-potential_friends #friend_results .potential_friend .user_info .username,
.game-link,
body.action-show.controller-games .game_card .details .overview .game-data p span,
.minimal_tabs.ui-tabs.ui-widget .ui-tabs-nav li a {
  color: #FFFFFF !important;
  font-weight: normal !important;
}
    
body.controller-users.action-show table.games th {
  background: #232323 !important;
}
    
body.controller-accounts .edit-form .edit-actions .save {
  background: #232323 !important;
  border: none !important;
  border-radius: 0 !important;
  color: #BBBBBB !important;
}
    
body.controller-accounts .edit-form form .input input[type="text"],
body.controller-accounts .edit-form form .input textarea,
body.controller-php_compatibility.action-blog .p_template2_col2 > div .box .blog_subject_input,
body.controller-php_compatibility.action-blog .p_template2_col2 > div .box textarea,
body.controller-accounts .edit-form form .input input[type="password"],
body.controller-accounts .edit-form form .input input[type="email"],
body.action-index.controller-potential_friends .find_friends input[name="friend_query"] {
  border-radius: 0 !important;
  background: #141414 !important;
  color: #FFFFFF !important;
  border: none !important;
}
    
body.action-index.controller-videos .media-list.media-list-tiled .media-list-item,
.media-list-tiled .media-list-container .media-list-item {
  border-radius: 0 !important;
  background: #191919 !important;
}
    
.media-list-vertical .media-list-container .media-list-item {
  border-radius: 0 !important;
  background: #141414 !important;
}
    
.media-list-tiled,
.media-list-tiled .media-list-container {
  box-shadow: none !important;
  background: #141414 !important;
}
    
body.action-index.controller-videos .media-list.media-list-tiled .media-list-item .media-list-item-details .stats .stat .comments,
.media-list-tiled .media-list-container .media-list-item .media-list-item-details .stats .stat .comments {
  width: 0px !important;
  height: 0px !important;
  background-image: url(data:image/png;base64,) !important;
  background-size: 8px 8px;
  padding: 4px 4px !important;
  margin: -1px !important;
}
    
body.action-index.controller-videos .media-list.media-list-tiled .media-list-item .media-list-item-details .stats .stat .rating,
.media-list-tiled .media-list-container .media-list-item .media-list-item-details .stats .stat .rating {
  width: 0px !important;
  height: 0px !important;
  background-image: url(data:image/png;base64,) !important;
  background-size: 8px 8px;
  padding: 4px 4px !important;
  margin: -1px !important;
}
    
body.action-index.controller-videos .media-list.media-list-tiled .media-list-item .media-list-item-details .stats .stat .views,
.media-list-tiled .media-list-container .media-list-item .media-list-item-details .stats .stat .views {
  width: 0px !important;
  height: 0px !important;
  background-image: url(data:image/png;base64,) !important;
  background-size: 12px 8px;
  padding: 4px 6px !important;
  margin: -1px !important;
}
    
.media-list-tiled a.media-item-more,
.media-list-vertical a.media-item-more,
body.controller-pages.action-home .new_client_link a,
body.action-index.controller-potential_friends .find_friends input[name="friend_query_submit"],
body.action-index.controller-potential_friends .box a.fb_find,
body.action-index.controller-potential_friends #friend_results .potential_friend .user_info a.add_friend {
  border-radius: 0 !important; 
  background: #232323 !important;
}
    
body.controller-pages.action-home .new_client_link,
body.action-index.controller-potential_friends #friend_results .result_header, 
body.action-index.controller-potential_friends #friend_results .potential_friend,
.widget.avatar {
  border-radius: 0 !important; 
  background: #1C1C1C !important;
}
    
body.controller-pages.action-home .new_client_link h1,
body.action-show.controller-games .game_card .details .overview .game-data #game-title-holder h1 {
  background: none !important;
  border: 0 !important;
}
    
.db_user {
  background-color: #1C1C1C !important;
}
    
.db_user:hover {
  background-color: #0A0A0A !important;
}
    
body .jcarousel-skin-landing .jcarousel-next-horizontal {
  background: url(data:image/png;base64,) no-repeat scroll 5px center, -moz-linear-gradient(right center , #726C80 0%, #726C80 100%) repeat scroll 0% 0% transparent;
}
    
body .jcarousel-skin-landing .jcarousel-prev-disabled-horizontal {
  background: url(data:image/png;base64,) no-repeat scroll 2px center, -moz-linear-gradient(right center , #282828 0%, #282828 100%) repeat scroll 0% 0% transparent;
}
    
body .jcarousel-skin-landing .jcarousel-prev-disabled-horizontal:hover {
  background: url(data:image/png;base64,) no-repeat scroll 2px center, -moz-linear-gradient(right center , #232323 0%, #232323 100%) repeat scroll 0% 0% transparent;
}
    
a {
  color: #888888;
}
    
h2 {
  font-weight: normal !important;
}
    
h1 {
  background: #726C80 !important;
  border-radius: 0 !important;
  text-shadow: none !important;
}
    
}
