css:
@-moz-document url-prefix("http://yomou.syosetu.com/search.php") {
/*各作品全体*/
div.searchkekka_box{
	border:2px solid #c0c0c0;
	background-color:#f0f0f0;
	margin:10px 0px 20px 10px;
 	padding:10px;
	position: relative;
	text-align:right;
}

div.searchkekka_box div.novel_h{
	text-align:left;
}

/*作品タイトル部分のリンク*/
a.tl{
	text-decoration: none !important;
}


/*短編・完結・連載中表示部分*/
div.searchkekka_box td.left{
	visibility: visible !important;
	position: absolute;
	display: block;
	text-align:left;
	text-decoration: none;
	top:38px;
	left:10px;
	max-width:400px;
	z-index: 10;
}

div.searchkekka_box td,
div.searchkekka_box div{
	text-align:left;
	width:716px;
	overflow:visible;
	display:inline-block;
	}

/*あらすじ部分*/
div.ex{
	border-top:1px solid #c0c0c0;
	border-bottom:1px solid #c0c0c0;
	background-color:#ffffff;
	padding:10px;
	position:relative;
	left:-10px
}

/* br を除去*/
div.searchkekka_box  br{
	display:none ;
	}

/*ジャンルとキーワードで改行*/
div.ex ~ br:nth-of-type(2){
	display:inline !important;
}

/*読了時間（文字数）以外は非表示*/
div.ex ~ span.marginleft:nth-of-type(1){
	display: inline !important;
	margin-left:0px;
	margin-right:0px;
}
div.searchkekka_box td span.marginleft,
div.searchkekka_box td img{
	display: none;
}

/*総合評価ポイント*/
div.searchkekka_box td span.attention{
	margin-left:10px;
	margin-right:10px;
}

}
