css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.toodledo.com/tasks/index.php#") {

/*##################################################################*/
/*##################################################################*/
/*##################################################################*/
    
    * {
        font-family: ubuntu condensed!important;
    }

    [content*="!"] {
        text-decoration: underline!important;
    }

    #head, #left_side *, #banner *, #rchunk *:not(#tasks):not(img):not([class*="dett"]):not([class*="sub"]):not([id*="rep"]),.tlbr_sec:not(.col4):not(#tlbr_sort):not(#tlbr_show):not(span):not(.dim) {
        border: none!important;
        background: #fff!important;
        font-size: 14px;
    }

    body, [id*="tlbr"], #badge_filt {
        background: none!important;
    }


/*CONTAINER*/

    #tlbr_search,  #head, #rchunktop, #banner,  #rchunk, #main, body, #left_side {
        height: 0!important;
        padding: 0!important;
    }


/*TASK CONTAINER*/

    #tasks,#rchunk {
        position: absolute!important;
        color: transparent!important;
        background: transparent!important;
        padding: 0!important;
        margin: 0!important;
        left: 2%!important;
        top: 15px!important;
        width: 90%!important;
        font-size: 0!important;
    }


/*POPUPs*/

    .popmenu {
        position: fixed!important;
        right: 45%!important;
        top: 50%!important;
    }

    
/*##################################################################*/
/*##################################################################*/
/*##################################################################*/


/*ROW ELEMENTS*/

    .row, .cols span {
        padding: 0!important;
    }

    .chd img, .bnoted,.bnote,[class*="subp"], .subm, [class*="bsub"], .ch,.dett.col4, .ch img, .col0, .col1, .col2,.col128, .std, .st, .st img, .std img {
        height: 2em!important;
        width: 2em!important;
        position: relative;
        top: 0!important;
        padding: 0px!important;
        margin: 0!important;
        margin-bottom: 0.5em!important;
        margin-right: 0.5em!important;
        border: 1px solid #fafafa!important;
        font-size: 14px!important;
    }

    .col128,.col1,.col2, .bnote,.bnoted {
        width: 4.5%!important;
        color: #fafafa!important;
    }
    

/*NOTE*/   
   
    .note, .ednote {
        margin-bottom: 20px!important;
    }

   .bnote {
        background:#fff!important;
    }

    .bnote, .bnoted {
        position: relative!important;
        left: -91.9%!important;
        /*z-index: 1!important;*/
    }
    

    
/*CHECKBOX*/

    .chd {
        position: relative!important;
        left: -10%!important;
    }

    .chd img {
        width: 80em!important;
        height: 8px!important;
        margin-top: 0.5em!important;
        background: red!important;
        z-index: 8!important;
    }

    
/*STAR*/
    
    .bstared  {
        background: yellow!important;
        border-radius: 1000px!important;
    }
    
    .bstar {
        background: #fff!important;
    }

    .st, .std {
        position: relative!important;
        left: 19%!important;
    }

    .st img, .std img {
        border-color: #fff!important;        
    }
    
/* REPEAT */    

    .col4 * {
        background: #ccc!important;
        border-color: #ccc!important;
        color: transparent!important;
        font-size: 32px;
    }

    
/*TASK TITLE*/

    .col0 {
        /*z-index: 1!important;*/
        padding-left: 10px!important;
        margin-left: 10px!important;
        border: none!important;
        border-left: none!important;
        border-right: none!important;
        width: 50%!important;
        position: relative!important;
        left: 3%!important;
        top: 15%!important;
    }

    .col0 span {
        position: relative!important;
        top: 0px!important;
        font-size: 14px!important;
    }

    .subtasks  .col0 {
        padding-left: 6%!important;
        font-style: italic;
    }

    
/*FOLDER*/

    .col1 {
        text-align: center!important;
    }


    
/*DATE*/
    
    .col2 {
        /*z-index: 0!important;*/
        text-align: center!important;
    }

    .dett.col2 span {
        position: relative;
        top: 25%!important;
        font-size: 14px!important;
    }

    .date0 {
        color: #777;
    }

    .date1 {
        color: #555!important;
    }

    .date2, .dett.col2 [class*="date2"] {
        color: #00f!important;
        font-weight: normal!important;
    }

    .date3 {
        color: #900!important;
        font-weight: normal!important;
    }

    .dett.col2 [class*="date3"] {
        color: transparent!important;
    }

    
/* SUBTASKS */

    .subtasks {
        border: none!important;
        margin: 0!important;
        margin-bottom: 20px!important;
        position: relative!important;
        bottom: 7px!important;
        width: 100%;
    }

    .subtasks .row, .subtasks .row * {
        margin-bottom: 0!important;
        border-top: none!important;
    }

    .subp, .subm {
        position: relative!important;
        right: 8.3%!important;
    }

    .subm {
        background: #eee!important;
        border-color: #eee!important;
    }

    [parent="0"] {
        background: #fff!important;
    }

    
/*SEPARATOR*/

    .sep {
        display: none!important;
    }

    /*#sepDueinthepast, #sepDueTomorrow,#sepDueinthenext7days,#sepDueTomorrow,#sepDueToday,*/
    #sepNoDueDate {
        display: block!important;
        background: transparent!important;
        border: 1px solid black!important;
        height: 70px;
    }
    
    
/*SPECIAL COLOR*/
    
    .bnoted, .subp, .subtasks .bnoted {
        background: #ddd!important;
        border-color: #ddd!important;
    }

    .dim {
        background: none!important;
        color: transparent!important;
        /*z-index: 1!important;*/
        padding: 0!important;
    }

    
/*##################################################################*/
/*##################################################################*/
/*##################################################################*/



/* NAV AND SETTINGS */

    #viewby {
        position: fixed;
        bottom: 0!important;
        padding: 5px!important;
        z-index: 9;
        width: 100%;
        background: none!important;
    }

    #welcome, #proflinks, #welcome b {
        font-size: 0px!important;
        height: 0!important;
        width: 0!important;
    }
    
    
    #proflinks a,  [id*="vb"], /*settings and nav*/
    
    A.onthefly, #forgetsearch, #modifysearch, #savesearch, #tlbr_multi B, #tlbr_multi, /*nav specific*/
    
    #tlbr_show ,#tlbr_show *,#tlbr_sort /*sort and filter*/{
        position: fixed!important;
        z-index: 11!important;
        margin: 0!important;
        text-align: left!important;
        font-weight: normal!important;
        font-size: 14px!important;
        padding: 3px!important;
        text-decoration: none!important;
        text-transform: none!important;
        text-transform: lowercase!important;
        color: #eee!important;
        border: none!important;
        width: auto!important;
        height: 2%!important;
    }

    #tlbr_show ,#tlbr_show *,#tlbr_sort{
        top: 94%!important;        
    }

    #tlbr_show B {
        width: 2%!important;
        left: 85%!important;
    }

    #tlbr_show, #tlbr_show * {
        left: 87%!important;
    }

    #tlbr_sort {
        left: 90%!important;
    }

    #forgetsearch, #modifysearch, #savesearch, #tlbr_multi B, #tlbr_multi,A.onthefly {
        top: 91%!important;
    }

    
    #forgetsearch, #savesearch, A.onthefly {
        left: 85%!important;
    }

    #modifysearch {
        left: 90%!important;
    }

    #tlbr_multi, #tlbr_multi B {
        left: 95%!important;
    }

    #proflinks a,  [id*="vb"] {
        top:0!important;
    }

    #vb2 {
        left: 85%;
    }

    #vb8 {
        left: 90%;
    }

    #proflinks a {
        left: 95%!important;
    }


    ::-moz-placeholder { /* Mozilla Firefox 19+ */
        color:    #eee;
    }
    
    #quickAdd, #tlbr_quick, .addField {
        position: fixed;
        top: 97%!important;
        right: 0%!important;
        width: 14%!important;
        font-size: 14px!important;
        z-index:11!important;
        border: none!important;
        background: #fff!important;
    }

    
/* SEARCH */

    #searchform, #rule {
        position: fixed!important;
        width: 70%!important;
        left: 0!important;
        top: 6%;
        z-index: 10!important;
    }

    
/* SIDE TABS */
    
    #tabs {
        position: fixed;
        width: 15%!important;
        left: 84.5%;
        top: 5%;
        z-index: 8!important;        
    }

    .tab a, #tab0 *{
        color: #aaa!important;
        font-weight: normal!important;
    }

    #tab0, #tabon0 {
        position: fixed!important;
        top:3%!important;
    }
    
    .tabon a {
        background: #ee9!important;
    }


/* TAB 0 

    #tab0 *, #tabon0 * {
        position: fixed!important;
        top: 8%!important;
        right: 2%!important;
        width: 100px!important;
        height: 100px!important;
        border-radius: 129px 129px 129px 129px !important;
        color: transparent!important;
        border: 5px dotted #aaa!important;
    }
    */

/*##################################################################*/
/*##################################################################*/
/*##################################################################*/

    
/*--- hiding ---*/

.tab A[title="All Tasks"],
#sbhover,
#logo, 
#tlbr_icons, 
#collabSwitch, 
#bottom, 
#userstylewarning,
#viewby H3, 
#vb1,
#vb4,
#vb3,
#vb5,
#vb6,
#vb7,
#vb21,
#sec_help, 
#sec_tool, 
#sec_forum, 
#sec_organize, 
#sec_list, 
#sec_outline, 
#sec_upgrade, 
#proflinks A[href="/pro.php"], 
#proflinks A[href="/subscribe/index.php"], 
#proflinks A[href="/signout.php"], 
[value="Signout"],
#tasks B, 
.det90,
#tb_cust,
[title="All Tasks"],
.ax, 
[title="delete task"],
[onclick="return dividerClicked(0);"],
[onclick="return dividerClicked(1);"],
#action_addmultiple, 
#importlnk,
.sprt_tlbr.msrch, 
.sprt_tlbr.mfol, 
.sprt_tlbr.mstat, 
.sprt_tlbr.mcon, 
.sprt_tlbr.mcal, 
.sprt_tlbr.mrep,
.sprt_tlbr.mdue, 
.sprt_tlbr.mstar,
.sprt_tlbr.mparnt,
.shr,
.spdt,
.moresubs,
.shove,
.mgo,
#searchField,
#tasks I,
#addTask,
#tip,
#chhover,
#colhead,
#tlbr_new,
#tlbr_sort B,
#footpad,
#sec_notebook,
#otfti,
#charsleft,
#sec_task
{
display:none!important;
}

}
