css:
@-moz-document domain("wikipedia.org") {
/* Wikipedia Gentle Suck

2014-10-10

ripped from 
https://userstyles.org/styles/101645/another-wikipedia-style
jamesmclendon.com */


div#p-personal {
    top: 10px;
}

div#mw-panel {
    position: absolute;
}

#p-search {
	top: 93px;
    position: absolute;
                                            
}

.mw-indicators {
	bottom:30px;
    opacity: 0.2;
}

#left-navigation,
#p-views,
div#p-personal,
div#p-cactions.vectorMenu,
div.articleFeedbackv5-panel,
#p-logo {
    display: none;
}

 
#bodyContent {
	font-family: "Merriweather", 'Georgia', serif;
	line-height: 1.8;
	font-size: 0.85em;
  color: #333;
}
 
div#content.mw-body,
div#footer{
	max-width: 45em;
	margin: auto;
	background: #f6f6f6;
	border: none;
}

form#searchform {
	max-width: 47em;
	margin: auto;
}

div#content.mw-body,
h1,
h2,
h3 { color: #444; }

div#right-navigation { width: 100%; }

div#p-search {
	width: 100%;
	margin: 0;
}

div#simpleSearch {
	float: right;
	margin-right: 1em;
}


body {
 background-color: #fefefe;
}

#mw-page-base {
    background: none;
  }

div#content.mw-body, div#footer 
{
 background: none 
}

.infobox, .vertical-navbox {
    background: #fff !important;
  	border: 1px solid #eee !important;
  }

  .vertical-navbox th, .vertical-navbox tr, .vertical-navbox td,
.infobox th, .infobox tr, .infobox td, .NavHead {
	padding: 0.5em;
  background: #fff !important;
  border: none !important;
}
    
.navbox-title {
	background: #fff !important;
  border-bottom: 1px dotted #efefef;
}

    
    
a:hover, a:visited, a, a.stub, a.new  {
	color: #000066 !important;
  	font-weight: 600;
}


      
.navbox-abovebelow, th.navbox-group, .navbox-subgroup .navbox-title {
background: #fff !important;
}
    
.topicon
{
opacity: 0;
}

div#simpleSearch #searchInput {
 background: #fff; 
  padding: 5px;
  border: 1px solid #eee;
}

div#simpleSearch #searchButton, div#simpleSearch #mw-searchButton {
 	display: none; 
}

div#simpleSearch {
 background: none; 
  border: none;
  margin-right:-5px;
}


div.thumbinner, #toc {
  padding: 0 !important;
  margin: 0 !important;
  background: #efefef;
  border: 0 !important;
}


div.thumbinner div {
 margin: 0 !important;
  padding: 0 !important;
}

.thumbimage {
border: 0px solid #fff !important;
  margin-bottom:5px;
  padding: 0 !important;
  margin: 0 !important;
}

.thumbimage {
width: 222px !important;
    height: auto !important;
  }

#toc
{
  
  	border: 1px dashed #efefef;
}

#toc > ul 
{ 
 	padding: 15px !important;
	font-size: 0.8em;
}

#toc #toctitle
{
 	padding: 15px 0 0 0 !important; 
}

.mw-content-ltr .thumbcaption
{
	padding: 0.7em 1.4em 1.4em 1.4em !important;
}


.infobox, .tright, .tleft, .thumbinner, #toc, .vertical-navbox {
	width: 222px !important;
    max-width: 222px !important;
}

.ambox
{
 margin: 12px 0 2px 0 !important;
}

.mbox-small-left
{
 	width: 100% !important; 
  border-collapse: inherit !important;
font-size: inherit !important;
line-height: inherit !important;
}
}
