css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wykop.pl") {
body#frameset, .overlay, #site, .comments-stream {
    background: url('http://i.imgur.com/dES0SNJ.jpg'); /*<----------Tutaj własne tło*/background-attachment:fixed; 
}
#nav, #footer, button, .button, .showSpoiler, .show-more, ul.related li > div div.content, ul.related li > div span.data > span.cover, .summary, input[type="text"], input[type="password"], input[type="date"], .form-big > div:first-child, .form-big > div:last-child, .ac_results, #zgloszenie ul.reason-list li:hover, .dropdown table td a:hover {
    background: none repeat scroll 0% 0% #1F1414;/*<----kolor górnej tabelki*/
    
}
ul.related li > div span.data > span em.logoreklama, .article h2 a:visited, a.showSpoiler, a.show-more, .sub-menu.inline-list > *:after, .lcontrast .affect, blockquote p.description, .media-header p.description, .light, .usercard span.info, .article.preview h2 a + sup {
    color: #FF0000 /*<-----kolor tagów, kiedy dodano wpis itd.*/!important; 
}
.settings select, .rbl-block, .comments-stream > li, .dropdown > div, .comments-stream.touch-content, textarea, input.create[type="text"]:hover, input.create[type="text"]:focus, .media-content .manager, #nav input, #input-addUrl, #zgloszenie, #footer a.tag:hover, #nav .nav li.active a, li.ac_over, .media-content .description, .media-content .description a:last-child, .media-content .description span.file-size, .dropdown table tr, .with-legend, .with-legend legend, form select, .media-content .description span.file-size, .media-content .description a.affect.hide, .form-big fieldset input[type="text"], .form-big fieldset input[type="password"] {
    background: none repeat scroll 0% 0% #000000; background-color:rgba(0,0,0,0.9); /*<-----tło wpisów i znalezisk*/
}
#nav li img.avatar {
    border-bottom: medium none !important;
    width: 50px;
    height: 50px;                          /*Większy avatar na belce*/
    margin-top: 0px;
}
i.icon {
    background: url('http://i.imgur.com/Bd9jK6x.png') /*nowe logo wypoku, pierdzielilem sie z tym 20 minut bo cos nie pasowalo JA PIERDOLE!!*/;
    text-indent: -9999px;
    vertical-align: middle;
}
i.wykop-logo { /*ustawienie loga*/
    width: 93px;
    height: 19px;
    background-position: 0px -148px;
}
.r-block, .grid-right > .r-block, .grid-right > div {
    background: none repeat scroll 0% 0% rgba(0,0,0,0.9); /*tło obserwowanych i popularnych tagów*/   
}    
}
