css:
@-moz-document url-prefix("http://www.aywas.com/") {
body {font-family:Verdana; font-size:11px; background:url('http://i.imgur.com/0eR10NE.gif') center right #f3f9e0 repeat fixed !important;}

div#main {background: #fff}
div#container {
	width: 1200px; padding:20px; position:relative; margin-left:80px;}
		div#backdrop{
			width: 100%;
			-moz-border-radius: 20px;
			-webkit-border-radius: 20px;
			border-radius: 20px;
			background-color: #FFF;
			border: 2px solid #77a800; padding:6px;
  margin-top:50px;

		}

	#global-quick-search {
		top: 59px; right:50px;
	}

	body.day #page-header #page-time, 	body.night #page-header #page-time{ position:absolute; right:120px; top:35px; }

#page-header{
	width: 1200px;
	height: 80px;
	position: fixed; left:9; top:10px;
  padding: -20px;
	background: url(http://i.imgur.com/qQFjeBR.png) bottom left repeat #fff;
	margin: -2px -20px 0 0;
  			-moz-border-radius: 20px;
			-webkit-border-radius:  20px;
			border-radius:  20px;
			background-color: #FFF;
			border: 2px solid #77a800; z-index:1;
}
	#page-header #logo{
		width: 290px;
		height: 77px; background: url(http://i.imgur.com/F9PnCIx.png);
		text-indent: -9999px; margin-top:-20px;
	}

#page-nav {position:relative;  margin-top:-20px;}
#page-nav li h2 {font-size:14px; font-weight:bold;}
#page-nav .sub-nav a,#page-nav .sub-nav a:hover {font-size:11px; color: #29440b;}
#page-nav li:hover {background: #29440b;}
#content table {-webkit-border-radius: 22px;  
-moz-border-radius: 22px;
     border-radius: 22px;} 
  
  /*end page nav*/
  
        /* Side div stuff */
      
  div#side{ background:#fff;
				width: 165px;
				float: right;
    position:fixed; left:1320px; top:5px;
				margin: 0px 10px;
				text-align: center;
				font-size: 11px;
    					-moz-border-radius: 20px;
					-webkit-border-radius: 20px;
    border-radius:20px; padding:10px;           border: solid 2px #77a800;
			}
      				div#side h3 h3{
					font-weight: bold;
				}
			

								div#side-backdrop{
					-moz-border-radius: 20px;
					-webkit-border-radius: 20px;
    border-radius:20px;
					height: 200px;
					background-position: top right;
					background-repeat: no-repeat;
					position: relative;
					margin: 0 0 20px 0;

				}
		
				div#side dl{
					margin: 10px 20px 5px 25px;
				}
				
					div#side dl dt{
						background-image: url(http://www.aywas.com/images/screen/side-elements.png) no-repeat;
						width: 20px;
						height: 20px;
						float: left;
						overflow: hidden;
						text-indent: -9999px
					}
        div#side p a{margin-bottom:10px;}
    
    /*end side*/
  
    div#content {margin-left:20px;margin-right:20px;}
#content th {background: #850823; height:30px;
    -webkit-border-top-right-radius: 18px;
      border-top-left-radius: 18px;    
-moz-border-top-right-radius: 18px;
      border-top-left-radius: 18px; 
    border-top-right-radius: 18px; 
      border-top-left-radius: 18px;
      text-align:center;
  border: #77a800; padding:0;margin:0}
    
 
   		table.y-tables tr.row-2,
	table.y-tables tr.row-even,
	table.y-tables tr.highlight,
	table.y-tables tr td.highlight,
	table.css3 tr:nth-child(odd) td{
        background-color:#fff2f2; }
      
      #highlight {color:#fff;}
      
#content .row-2{ background: #fff2f2;}

div#container a, a:hover {color:#850823;}
  
/**
 * Page notices
 */
div.page-notice{border-radius: 20px;
	-moz-border-radius: 20px;
	-webkit-border-radius: 20px;
	margin: 10px auto;
	padding: 10px;
}

	div.page-notice.error{
		color: #850823;
		background-color: #fff2f2;
		border: 2px solid#850823;
	}

	div.page-notice.success{
		color: #000;
		background-color: #d2e2ac;
		border: 2px solid #77a800;
	}
    form-adopt {border-radius: 20px;
	-moz-border-radius: 20px;
    -webkit-border-radius: 20px;}
      #notice-table {border-radius: 20;}
    
    /*Big notices*/
#user-notice-table {width:1000px; border-radius: 20px;
	-moz-border-radius: 20px;
	-webkit-border-radius: 20px;}
      #user-notice-table td {font-size:10.5px; padding:5px 10px 5px 10px;}
.gen-small{ font-size:9px; }
    table.y-tables{
	border: 1px solid #77a800;
}

    
#y-tables center {border: 2px solid #77a800; text-align:left; padding:10px;}
    table.user-notice-table {border: solid 1px #77a800; background-color: #eab8b8;}
	table.y-tables tr th.highlight,
	table.y-tables tr.highlight th{
		background-color: #eab8b8;
		color: #FFF; height:20px;
	}    
 .category-title {color:#fff;}    
#a-button {    			-moz-border-radius: 165px;
			-webkit-border-radius: 16px;
        border-radius: 16px;}
      
      /* Buttons */
      
      .ctabtn,
.ctabtn span, .ctabtb a,
.ctabtn input{
    background-image: url(http://i.imgur.com/wiFTjpc.png);
    color: #fff;
    cursor: pointer;
	font-weight: bold; font-size:10px; 	letter-spacing: -0.5px;
}
      
      
    div.search_box {width:1000px;   border-radius: 20px;
	-moz-border-radius: 20px;
	-webkit-border-radius: 20px;}

	table.y-tables tr th.highlight,
	table.y-tables tr.highlight th{-moz-border-radius:0; -webkit-border-radius:0; border-radius:0;}

    th {color:#fff;}
}
