css:
@-moz-document domain("yandex.ru"),
               domain("yandex.ua"),
               domain("yandex.kz"),
               domain("yandex.by")
{
     /* Add pattern as background */
     .b-page{
         background: #f6f5f3;
     }
    
    /* Remove everything besides the search information */
    .b-table b-line_bar__table,
    .b-head-stripe,
    .b-head,
    .b-table,
    .b-footer_spacer,
    .b-line_foot,
    .b-keyboard-popup__gap
    {
        display: none !important;
    }
    
    .b-line_bar{
        background: -moz-linear-gradient(right top , #ffdb4c 0px, #fc0 100%) repeat scroll 0 0 #ffdb4c !important;
        background: -webkit-linear-gradient(top right,#ffdb4c 0,#fc0 100%);
        border: none            !important;
        margin-bottom: 100px    !important;
    }
    
    /* Start editing the search container */
    
    .b-line_head{
        border-top: 1px solid #d1d0cf       !important;
        border-left: 1px solid #d1d0cf      !important;
        border-right: 1px solid #d1d0cf     !important;
    }
    
    .b-line_search{
        border-bottom: 1px solid #d1d0cf    !important;
        border-left: 1px solid #d1d0cf      !important;
        border-right: 1px solid #d1d0cf     !important;
    }
    
    .b-line{
        height: 100%            !important;
        background-color: #fff  !important;
    }
    
    /* Start editing the popup keyboard */
    
    .b-keyboard-popup__bar{
        background-image: none !important;
    }
    
    .b-keyboard-popup__close{
        width: 100%     !important;
        float: none     !important;
        opacity: 0      !important;
        display: block  !important;
    }
    
    .b-keyboard__key,
    .b-keyboard__key-l,
    .b-keyboard__key-r{
        background-image: none      !important;
        background-color: #f6f5f3   !important;
    }
    
    .b-keyboard__key{
        border: 1px solid #d1d0cf !important;
    }
    
    .b-keyboard__lang{
        width: 160px    !important;
    }
    
    .b-keyboard__lang-i{
        background-color: #f6f5f3   !important;
        height: 22px                !important;
        padding-top: 8px            !important;
        width: 98px                 !important;
        text-align: center          !important;
        top: 0px                    !important;
    }
}
