css:
@-moz-document url("https://tweetdeck.twitter.com/") {
.btn-positive, .btn-positive-on-blue, .btn-positive:focus, #calcurrent, .list-item:hover, .list-item:active, .list-item.is-selected, .is-selectable.is-selected, .accs .acc-selected, .accs .acc-twitter {
   background-color: #E59132; 
}
    
.btn:focus, .btn-on-blue:focus, .btn-positive:focus, .compose-account:focus, input:focus, select:focus {
  outline: 5px auto #E59132 !important;
  box-shadow: #E59132 0px 0px 5px 0px !important;
          webkit-box-shadow: #E59132 0px 0px 5px 0px !important;
          moz-box-shadow: #E59132 0px 0px 5px 0px !important;
    }
.btn-on-blue:focus, .btn-positive-on-blue:focus {
      background-color: #525252 !important;   
}
        
::selection {
     background-color: #E59132;    
          color: #FFF;
}
  
.btn-positive:hover, .btn-positive-on-blue:hover, .accs:hover .acc-selected:hover, .accs:hover .acc-twitter:hover {
   background-color: #E28016; 
}
   
.column-header {
  background-color: #1B1B1B;
}

.link-complex:hover, a:hover, a, .facet-subtitle, .toggle-item, .is-options-open .column-settings-link, .is-options-open:hover .column-settings-link:hover, .icon-dot, .is-new .column-type-icon, .btn-options-tray:hover, .icon-search.icon-small {
	color: #E59132;
}

.inline-reply, .compose, .btn-on-blue, .app-columns-container, .btn.js-hide-drawer, .app-search-fake, .column-options {
	background-color: #404040;    
}    

.column, .tweet-detail-wrapper {
   background-color: #262626; 
}
  
.btn-on-blue {
  border: 0;
}
    
.btn-on-blue:hover, .btn.js-hide-drawer:hover, .button-tray, .btn-on-blue:active {
	background-color: #525252 !important;      
}
  
.btn-positive-on-blue.is-disabled, .btn-positive-on-blue.is-disabled:hover {
  	background-color: #525252;
    color: #CCC;
    border: 0;
}

.btn-on-blue.is-disabled, .btn-on-blue.is-disabled:hover {
	   color: #AAA;
       background-color: #404040 !important;   
}
  
.compose-header {
     height: 0;
     width: 0;
    }

.reply-triangle {
   border-bottom-color: #404040;
  }

.sprite {
   background-image: url('http://puu.sh/aLQiC/08a49d5a10.png'); 
  }

body {
 background-color: #E59132; 
  background-image: none;
}

.spinner-centered {
       display: none !important;
      }

.column.is-focused {
     box-shadow: 0px 0px 0px 6px #E59132; 
    }

.tweet-avatar {
  	background: transparent;
	-moz-border-radius: 24px;
	border-radius: 24px;
	-webkit-border-radius: 24px;
}
}
