css:
@-moz-document url-prefix("http://www.campus-booster.net/"){
span#top_dtlLinks a{
  text-decoration: none;
  color: #00008B;
}

span#top_dtlLinks a:hover{
  color: #222;
}

input#top_btnSignOut{
  height: 17px !important; 
}

table.accueiltableniv1 tr > td > div > div:not([class]):not([id]){
  height: 17px !important; 
}

table#links_dl_profile td div{
 	/*padding: 7px 0;*/
  	margin-bottom: 5px;
}

div.zoneprofilunselected{
	padding-left: 0px;  
}

table#links_dl_profile td a{
 	padding: 7px 0;
  	display: block;
  	color : #00008B;
}

table#links_dl_profile td div.zoneprofilselected a{
  color: #fff;
}

table#links_dl_profile td a:hover{
 	background-color: #00008B;
  	color: #fff;
}

tr#s_spaces_trMySpace a{
  font-family: Arial;
  font-size: 17px;
  text-decoration: none !important;
  color: #00008B;
}

tr#s_spaces_trMySpace a:hover{
 color: #222; 
}

.sm2ChildCell{
  padding: 0;
}
  
.sm2ChildCell a {
  padding: 5px 10px;
  font-size: 13px;
  display: block;
}
  
.sm2ChildCell:hover{
    background-color: #0063af ;
}
      
.sm2ChildCell:hover a{
    color: #fff ;
}

.rightdiv {
	min-height: 818px;
}

div.newMainContent fieldset{
    border: 0px;
}

input[type="submit"]{
  background-color: #0063af;
  border: 0px;
  border-radius: 3px;
  padding: 5px;
  color: #fff;
  cursor: pointer;
}

input[type="submit"]:hover{
  background-color: #444;
  border: 0px;
}

input[type="submit"]:active{
  background-color: #222;
}

.topheader{
    cursor: pointer;
  }

.newTabcontrolHyperlinkCellActive,
.newTabcontrolHyperlinkCellInactive{
  	border-top-left-radius: 3px;
    border-top-right-radius: 3px;
}
#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_divCursus table,
#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_fstMain table{
 	border: 1px solid #BAD2DD !important;
  	border-collapse: inherit !important;
  	border-radius: 3px;
}
#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_divCursus tr:not(:first-child):hover td,
#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_fstMain table:nth-child(2) tr:not(:first-child):hover td{
 	background-color: #0063af !important;
  	color: #fff;
}

#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_divCursus tr:nth-child(1) td,
#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_fstMain table:nth-child(2) tr:nth-child(1) td{
 	color: #024E87 !important;
  	background-color: #EEEFF0 !important;
  	border-bottom: solid 1px #BAD2DD;
}

#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_fstMain > table:nth-child(1) tr:nth-child(1) td,
#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_fstMain > table:nth-child(1) tr:last-child td{
 	background-color: #0063af !important;
  	color: #fff;
}

#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_divCursus tr:nth-child(even) td,
#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_fstMain table:nth-child(2) tr:nth-child(even) td{
 	background-color: #fff !important;
}

#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_divCursus tr:nth-child(odd) td,
#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_fstMain table:nth-child(2) tr:nth-child(odd) td{
 	background-color: #eff3ff !important;
}

#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_fstMain > table:nth-child(1) tr td{
 	background-color: #eff3ff !important;
}

#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_divCursus tr td div{
 	font-size: 10px !important;
}

#ctl00_ContentPlaceHolder1_ucStudentMark_ctl01_divCursus tr td div > b{
 	font-style: italic;
}

td.headerLinks{
	padding: 2px 5px;
}

td.headerLinks > #ctl00_divUserInfo{
	margin-right: 0px !important;
}

.headerLinks > #ctl00_divUserInfo a,
.headerLinks a#ctl00_lnkMyBoosterForm{
	font-size: 12px;
    color: #0063af !important;
}

.headerLinks > #ctl00_divUserInfo a:hover,
.headerLinks a#ctl00_lnkMyBoosterForm:hover{
    color: #333 !important;
}

#aspnetForm div[align=center] > table > tbody > tr:nth-child(3){
	float:left;
    background-color: #eff3ff;
  	width: 775px;
}

#aspnetForm div[align=center] > table > tbody > tr:nth-child(4){
    float: right;
    min-width: 249px;
}

td.maincontent > div.rightdiv > div:first-child{
	padding: 0px !important;
}

#ctl00_ContentPlaceHolder1_TabLoader1_updMain .newMainContent{
	padding: 0px;
}

.newMainContent #ctl00_ContentPlaceHolder1_TabLoader1_ctl01_lblHome div.menu-top{
    width: auto;
    padding: 0px 10px;
    text-align: justify;
}

#ctl00_ContentPlaceHolder1_TabLoader1_ctl01_divAccount table#ctl00_ContentPlaceHolder1_TabLoader1_ctl01_grvAccount tr.newGridviewAltRow{
	background-color: #eff3ff !important;
}

#ctl00_ContentPlaceHolder1_TabLoader1_ctl01_divAccount table#ctl00_ContentPlaceHolder1_TabLoader1_ctl01_grvAccount tr.newGridviewRow,
#ctl00_ContentPlaceHolder1_TabLoader1_ctl01_divAccount table#ctl00_ContentPlaceHolder1_TabLoader1_ctl01_grvAccount tr.newGridviewAltRow{
	font-size: 10px;
}

#ctl00_ContentPlaceHolder1_TabLoader1_ctl01_divAccount table#ctl00_ContentPlaceHolder1_TabLoader1_ctl01_grvAccount tr.newGridviewRow:hover,
#ctl00_ContentPlaceHolder1_TabLoader1_ctl01_divAccount table#ctl00_ContentPlaceHolder1_TabLoader1_ctl01_grvAccount tr.newGridviewAltRow:hover{
	background-color: #0063af !important;
    color: #fff;
}
}
