css:
@-moz-document domain("garagegames.com") {
/* Basic dark background */
html,
.button,
.button.ui-state-hover,
.button.ui-state-active,
a.button.profile-link,
a.button.profile-link.ui-state-hover,
a.button.profile-link.ui-state-active,
input[type=text], input[type=password], input[type=file], textarea, select,
input[type=text].active, input[type=password].active, input[type=file].active, textarea.active, select.active,
div#container,
div.section-container,
div.section-header,
div.section-tabs,
div.section-content,
div.section-footer,
div#page-turner {
  background: none;
  background-color: #222;
}

/* Background of items that may need background images */
div.row-0,
table.tablesorter,
table.tablesorter thead tr th, table.tablesorter tfoot tr th,
table.tablesorter tbody td {
  background-color: #222;
}

/* Alternate colour for tables and so on */
div.row-1,
table.tablesorter tbody tr.odd td {
  background-color: #333;
}

/* Code blocks */
.dp-highlighter,
.dp-highlighter div.tools,
.dp-highlighter ol li,
.dp-highlighter ol li.alt {
  background-color: #111;
}

.dp-highlighter ol li:hover,
.dp-highlighter ol li.alt:hover {
  background-color: #222;
}

.dp-highlighter ol li,
.dp-highlighter .columns div,
.dp-highlighter .tools {
  border-left: 1px solid #333 !important;
}

.dp-highlighter ol li span {
  color: #ccc;
}

.dp-highlighter .tools a {
  color: #666;
}

.dp-highlighter .comment,
.dp-highlighter .comments {
  color: #6ab825;
}

.dp-highlighter .string {
  color: #D2691E;
}

/* Don't allow line numbers to be selected. */
.dp-highlighter ol {
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
}
.dp-highlighter ol span {
  -webkit-user-select: all;
  -moz-user-select: all;
  -ms-user-select: all;
}

/* Author info after posts and blogs */
div.section-content > div:nth-child(1) > div:nth-child(2),
div#blog-container > div:nth-child(2) {
  background: none!important;
  background-color: #222!important;
  /*border: none!important;*/
}

/* Page turner */
div#page-turner {
  margin: 0;
  padding: 0;
}

/* Button borders */
.button.ui-state-default,
.button.ui-state-hover,
.button.ui-state-active {
  border: none;
}

/* Font colour */
body,
a.clicker.text,
h1, h2, h3, h4, h5, h6,
.button.ui-state-default,
.button.ui-state-hover,
.button.ui-state-active,
a.button.profile-link,
a.button.profile-link.ui-state-hover,
a.button.profile-link.ui-state-active,
table.tablesorter tbody td,
table.tablesorter thead tr th, table.tablesorter tfoot tr th,
div.paging a,
div.section-tabs li a {
  background: none;
  color: #ddd!important;
}

body p.date,
blockquote,
div.comment div.body em,
div.section-tabs li a.current {
  color: #999;
}

a {
  color: #17D;
  text-decoration: none;
}

a:hover {
  color: #17D;
  text-decoration: underline;
}

a:visited {
  color: #49A;
}

/* Input elements */
input[type=text], input[type=password], input[type=file], textarea, select,
input[type=text].active, input[type=password].active, input[type=file].active, textarea.active, select.active
{
  color: #BBB;
  font-family: Verdana, 'Helvetica Neue', Arial, Helvetica, sans-serif;
}

/* Quotes in posts */
blockquote span:nth-child(1) {
  padding-right: 0.5em;
  font-weight: bold;
}

/* Forum tables */
table.tablesorter thead tr th,
table.tablesorter tfoot tr th {
  border: none;
}

/* Post date */
div.comment > div.body > div {
  border: none!important;
}

div.comment div.body em {
  font-size: 90%;
}

/* Post border */
div.comment {
  border-bottom: none;
  border-top: 1px solid #555;
  margin-bottom: none;
  padding-bottom: none;
  margin-top: 10px;
  padding-top: 10px;
}

/* Comment spacing */
div#commentList {
  margin-top: 2em;
}
}
