css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://balusc.blogspot.ca/") {

    /* THIS IS A STYLE USED FOR balusc.blogspot.ca */

    body, p, li, div, a, span, strong {
        font-size:  13pt !important;
    }

    div pre,
    span.codekeyword, 
    span.codeannotation,
    span.codevalue, 
    span.codeconstant, 
    span.codetag, 
    span.codeproperty, 
    span.codecomment1, 
    span.codestatic {
        font-family:        Inconsolata, Consolas, 'Courier New' !important;
  	/*	font-size: 		    10pt !important;*/
        line-height:        1.25 !important;
  	/*	background-color:   #DADADA; */
	}  

    div#header-wrapper,
    div#footer-wrapper,
    div#sidebar,
    div#fastmenu, 
    p.note, 
    div.post-footer, 
    div#comments, 
    div#blog-pager, 
    header, 
    h2.date-header,
    footer {
        display:    none;
    }

    div#content-wrapper {
        width:  auto;
        border: none;
    }

    body {
        background: none !important;
    }

}
