css:
@-moz-document url-prefix(http://redmine){







/*--------------------------------*/
/*вставляйте стили выше этих линий*/
/*--------------------------------*/

body{
	font-family: Verdana, sans-serif;
	background-color: #408F54;

	font-size:14px;
	width:auto;
}

.contextual a.icon-edit{
  margin:4px;
    padding-right:10px;
  padding-left:22px;
  background-color:#408F54;
  background-position:5px 50%;
  border-radius:4px;
    color:white;
  }


th{
	border:none;
	background-color:none;
}

td{
	height:20px;
	border:none;
}

.group td{
	height:0;
}

.issues td{
	height:30px;
}

.attributes{
	font-size:12px !important;
	border-collapse:collapse !important;
}

.attributes td{
	height:10px;
	color:#666;
}

.attributes th{
	color:#666;
}

.issue .author{
	font-size:12px;
        color:#666;
}

.gravatar{
border-radius:50%;
      border:none;
}

.journal .details{
  font-size:11px;
  color:#666;
  font-style:italic;
  }

#header {
background: #3F8F5B;
  color:#fff;
padding: 10px;
min-height: 55px;
margin: 0;
}
#top-menu {
margin: 0;
padding: 6px 10px 2px;
background: #3F8F5B;
}
#main {
margin: 0;
}

#content {
padding: 10px 23px;
  border-top:none;
}
table.list tr.issue a {
color: #1B1B1B;
}

a, a:link, a:visited {
color: #205B93;
}

#main-menu li a {
background-color: #3F8F5B;
font-weight: normal;

}
#main-menu li a:hover {

  background-color: #A1D4B3;
  color:#FFF;
text-decoration: none;
}

#main-menu li a.selected {
background-color: #fff;
  border:none;
text-decoration: none;
}

#sidebar {
width: 19%;
}

div.issue {
background: none;
border: none;
}
h4 img.gravatar {
margin: -2px 4px -4px 0;
padding: 0;
}

#history .journal:nth-child(2n+1) {
background: #eee;
}
#history .journal {
padding: 8px 14px;
}

}
