css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
#posts .post.new_post 
{
min-height: 88px !important;
}

.new_post_label/*_icon*/ > i
{
background-image: url('https://31.media.tumblr.com/328d34405f92507e0415f814925db691/tumblr_inline_nab4dyi5jy1qkug5o.png') !important;
background-size: 513px, 190px !important;
margin-left: 1px !important;
margin-top:  -7px !important;
background-repeat: no-repeat !important;
}

.new_post_label
{
padding: 70px 1px 5px 0px !important;
}

.new_post_label > i 
{
color: transparent !important;
}

#new_post_label_text > i
{
background-position: 3px -4px !important;
}
#new_post_label_text:active > i
{
background-position: 3px -4px !important;
}
#new_post_label_text:hover > i
{
background-position: 3px -6px !important;
}

