css:
@-moz-document url("http://tgdmb.com") {
/* =============================================================
        Start "normalize"
============================================================== */

  /*! normalize.css v3.0.1 | MIT License | git.io/normalize */

  /**
   * 1. Set default font family to sans-serif.
   * 2. Prevent iOS text size adjust after orientation change, without disabling
   *    user zoom.
   */

  html {
    font-family: sans-serif; /* 1 */
    -ms-text-size-adjust: 100%; /* 2 */
    -webkit-text-size-adjust: 100%; /* 2 */
    font-size: 62.5%;
    max-width: 100%;
  }

  /**
   * Remove default margin.
   */

  body {
    margin: 0;
    max-width: 100%;
  }

  /* Tables
     ========================================================================== */

  /**
   * Remove most spacing between table cells.
   */

  table {
    border-collapse: collapse;
    border-spacing: 0;
  }

  td,
  th {
    padding: 0;
  }

/* =============================================================
        End "normalize"
============================================================== */

/* ===========================================================
        PX to EM
============================================================ */ 

  hr  { height: 0; border: solid #D1D7DC 0; border-top-width: 0.1em;}

  /* This is the border line & background colour round the entire page */
  .bodyline   { background-color: #FFFFFF; border: .1em #98AAB1 solid; }

  /* This is the outline round the main forum tables */
  .forumline  { background-color: #FFFFFF; border: .2em #006699 solid; }

  /* Menu at top of page */
	.mainmenu a { font-size: 2em; padding: 0.5em; }

  /* Header cells - the blue and silver gradient backgrounds */
  th  {
    color: #FFA34F; font-size: 1.1em; font-weight : bold;
    background-color: #006699; height: 1.5625em;
  /*  background-image: url(templates/subSilver/images/cellpic3.gif);*/
  }

  td.cat,td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom {
  /*  background-image: url(templates/subSilver/images/cellpic1.gif);*/
    background-color:#D1D7DC; border: #FFFFFF; border-style: solid; height: 1.75em;
  }
  
  /*
    Setting additional nice inner borders for the main table cells.
    The names indicate which sides the border will be on.
    Don't worry if you don't understand this, just ignore it :-)
  */
  td.cat,td.catHead,td.catBottom {
    height: 2.9em;
    border-width: 0;
  }
  th.thHead,th.thSides,th.thTop,th.thLeft,th.thRight,th.thBottom,th.thCornerL,th.thCornerR {
    font-weight: bold; border: #FFFFFF; border-style: solid; height: 1.75em;
  }

  th.thHead,td.catHead { font-size: .75em; border-width: 0.1em 0.1em 0 0.1em; }
  
  th.thHead,td.catHead { font-size: 1.2em; border-width: 0.1em  0.1em  0 0.1em ; }
  th.thSides,td.catSides,td.spaceRow   { border-width: 0 0.1em  0 0.1em ; }
  th.thRight,td.catRight,td.row3Right  { border-width: 0 0.1em  0 0; }
  th.thLeft,td.catLeft      { border-width: 0 0 0 0.1em ; }
  th.thBottom,td.catBottom  { border-width: 0 0.1em  0.1em  0.1em ; }
  th.thTop     { border-width: 0.1em  0 0 0; }
  th.thCornerL { border-width: 0.1em  0 0 0.1em ; }
  th.thCornerR { border-width: 0.1em  0.1em  0 0; }

  /* The largest text used in the index page title and toptic title etc. */
  .maintitle  {
    font-weight: bold; font-size: 2.2em; font-family: "Trebuchet MS",Verdana, Arial, Helvetica, sans-serif;
    text-decoration: none; line-height : 120%; color : #000000;
  }

  /* General text */
  .gen * { font-size : 1.2em !important; }
  .genmed * { font-size : 1.1em !important; }
  .gensmall * { font-size : 1em !important; }

  /* The register, login, search etc links at the top of the page */
  .mainmenu       { font-size : 1.1em; color : #000000 }

  .cattitle       { font-weight: bold; font-size: 1.2em ; letter-spacing: 0.1em; color : #006699}

  .forumlink { font-weight: bold; font-size: 1.2em; color : #006699; }

  .nav { font-weight: bold; font-size: 1.1em; color : #000000;}

  .topictitle,h1,h2   { font-weight: bold; font-size: 1.1em; color : #000000; }

  /* Name of poster in viewmsg.php and viewtopic.php and other places */
  .name           { font-size : 1.1em; color : #000000;}

  /* Location, number of posts, post date etc */
  .postdetails        { font-size : 1em; color : #000000; }   

  /* The content of the posts (body of text) */
  .postbody { font-size : 1.2em; line-height: 1.125em}

  /* Quote & Code blocks */
  .code {
    font-family: Courier, 'Courier New', sans-serif; font-size: 1.1em; color: #006600;
    background-color: #FAFAFA; border: #D1D7DC; border-style: solid;
    border-left-width: 0.1em; border-top-width: 0.1em; border-right-width: 0.1em; border-bottom-width: 0.1em
  }

  .quote {
    font-family: Verdana, Arial, Helvetica, sans-serif; font-size: 1.1em; color: #444444; line-height: 125%;
    background-color: #FAFAFA; border: #D1D7DC; border-style: solid;
    border-left-width: 0.1em; border-top-width: 0.1em; border-right-width: 0.1em; border-bottom-width: 0.1em
  }

  .copyright      { font-size: 1em; font-family: Verdana, Arial, Helvetica, sans-serif; color: #444444; letter-spacing: -0.1em;}

  /* Form elements */
  input,textarea, select {
    color : #000000;
    font: normal 1.1em Verdana, Arial, Helvetica, sans-serif;
    border-color : #000000;
  }

  input { text-indent : 2px; }

  /* The buttons used for bbCode styling in message post */
  input.button {
    background-color : #EFEFEF;
    color : #000000;
    font-size: 1.1em; font-family: Verdana, Arial, Helvetica, sans-serif;
  }

/* =============================================================
        Custom mobile changes
============================================================= */

/* Mobile-ize the header menu */
/*body > table { max-height:100%; }*/
.bodyline > table:first-of-type,
.bodyline > table:first-of-type table 
	{ background: yellow; /*height: 50em;*/ max-height:100%;  }

.bodyline > table table { height: 10em; } /* fix menu "scrolling" when mobile */

/*.bodyline > table span,
.bodyline > table span a
	{ display: inline-block; background: green; }*/

.bodyline > table:first-of-type table span,
.bodyline > table:first-of-type table span a
	{ display: inline-block; background: green; }


  img { max-width: 50rem;} /* Fix huge images breaking posts */

  table, tbody, tr, td { display: block; max-width: 100%;}

  td input { width:100% !important; }

  html body table {
		overflow-x: auto !important;
		display: block !important;
  }
}

@-moz-document url-prefix("http://tgdmb.com/login.php") {
body table td.bodyline form table.forumline tr:nth-child(2) td.row1  { background: pink; }

body table td.bodyline form table.forumline tr:nth-child(2) td.row1 table td:not( :nth-child(2) )
	{ width: 0; }

body table td.bodyline form table.forumline tr:nth-child(2) td.row1 table td:nth-child(2) span
	{ display: block; }

body table td.bodyline form table.forumline tr:nth-child(2) td.row1 table td:nth-child(2),
body table td.bodyline form table.forumline tr:nth-child(2) td.row1 table td:nth-child(2) input,
body table td.bodyline form table.forumline tr:nth-child(2) td.row1 table td[colspan],
body table td.bodyline form table.forumline tr:nth-child(2) td.row1 table td:nth-child(2) span
	{ width: 100%; }

input[type=text], input[type=url], input[type=email], input[type=password], input[type=tel], input[type=submit]
    {
        -webkit-appearance: none; -moz-appearance: none;
        display: block;
        margin: 0;
        width: 100%; height: 4em;
    	line-height: 4em; font-size: 1.7em;
    	border: 0.1em solid #bbb;
    }

input[type=checkbox] {
 width: 2em; height: 2em;
 -webkit-border-radius: 22px; -moz-border-radius: 22px; border-radius: 22px;
 border: 0.1em solid #bbb;
}

button[type=submit], input[type=submit] {
 -webkit-appearance: none; -moz-appearance: none;
 display: block;
 margin: 1.5em 0;
 font-size: 1em; line-height: 2.5em;
 color: #333;
 font-weight: bold;
 height: 2.5em; width: 100%;
 background: #fdfdfd; background: -moz-linear-gradient(top, #fdfdfd 0%, #bebebe 100%); background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#fdfdfd), color-stop(100%,#bebebe)); background: -webkit-linear-gradient(top, #fdfdfd 0%,#bebebe 100%); background: -o-linear-gradient(top, #fdfdfd 0%,#bebebe 100%); background: -ms-linear-gradient(top, #fdfdfd 0%,#bebebe 100%); background: linear-gradient(to bottom, #fdfdfd 0%,#bebebe 100%);
 border: .1em solid #bbb;
 -webkit-border-radius: 1em; -moz-border-radius: 1em; border-radius: 1em;
}



}

@-moz-document url-prefix("http://tgdmb.com/viewforum.php") {
/* ====================================
        Subforum Styles
======================================*/
/* Start sub forum styles */

  .bodyline td:nth-child(1) {
    display: none;
  }
    
  .bodyline td:nth-child(3),
  .bodyline td:nth-child(4),
  .bodyline td:nth-child(5),
  .bodyline td:nth-child(6){
    display: table-cell;
  } 
  
  .bodyline span.gensmall br { display: none; }

/* End sub forum styles */
}

@-moz-document url-prefix("http://tgdmb.com/viewtopic.php") {
/* ====================================
        Thread Styles
======================================*/
/* Reset some stuff from index */
  
  .forumline tr:nth-child(1),
  .forumline tr:nth-child(2)
    { display : initial; }

  /* End reset */

  .quote { font-size: 0.6875rem; }
  .postbody { font-size : 0.75rem; line-height: 1.125rem}
    .postbody br { display: initial; }

  .forumline td:nth-child(1) { display : table-cell; }

  td.row1,
  td.row2,
  td.row3,
  td.row3Right {
    display: table-cell;
  }
  
  .forumline tr td.row1:nth-child(1),
  .forumline tr td.row2:nth-child(1) {
    background: red;
    max-width: 20%;
  }
  
  .forumline tr td.row1:nth-child(2),
  .forumline tr td.row2:nth-child(2) {
    width: 100%
  }
  
  img {
  max-width:800px;
  }
  
  a.name {
  font-size: larger;
  }
  
  .bodyline .row1 {
  display: table-cell;
  }
  
  .forumline td[width="150"] {
    display: table-cell !important;
    margin: 0;
    padding: 0 0 0 0;
    background: yellow !important;
    width: 100% !important;
  }
  
  .forumline td[width="150"] span {
    display: inline-block;
    position: relative;
  }
  
  .forumline td[width="150"] br {
    display: none;
  }
  
  .forumline td[width="150"] img {
    max-width: 50px;
    padding : 0 .5em;
  }
  
  .forumline td[height="28"].row2 {
  display: block; 
  }
  
  span.postbody {
    line-height: 125%;
    font-size: 1.5em !important;
  }
  
  .forumline td span.postbody span[style] {
    display: inline;
    font-size: 1em;
    padding: 1em 0;
  }
  
  .bodyline td.quote {
    display: block;
    font-size: 24px !important;
    padding: 1em;
  }
}
