css:
@-moz-document url("http://www.nextgenupdate.com/forums/infernoshout.php?do=detach"), url("http://www.nextgenupdate.com/forums/shoutbox-iframe.php") {
/**** main *****/
html
{
	background-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAM0lEQVQI1zXKsQ0AMQjAQCf6BgmxBfvv5obyU9H6fLr7V6kqVC5ARLDxAswMmYnKt7LnA+lSE2PlUFYpAAAAAElFTkSuQmCC) !important;
	background-attachment:fixed;
	background-position:center top;
}

/* shout frame borders + padding */
div [style="padding-top: 1px; padding-bottom: 1px;"]
{
	padding-top: 3px !important;
	padding-bottom: 3px !important;
	border-width: 1px;
	border-color: #333333;
	border-style: solid hidden hidden hidden;
}

/* shoutbox smilies and editshout bg */
#shoutbox_smilies_row,#shoutbox_editshout tr:nth-child(1) td
{
	background-color: rgba(255,255,255,0.15) !important;
}

/* shout time */
.time
{
	color: rgba(136, 136, 136, 1.0) !important;
	font-size: 75%;
}

.bootstrap .well
{
	min-height: 20px;
	padding: 19px;
	margin-bottom: 20px;
	color: rgba(136, 136, 136, 1.0);
	background-color: transparent !important;
	text-shadow: 1px 1px 2px rgba(0,0,0,0.51), -1px 1px 2px rgba(0,0,0,0.51), -1px -1px 2px rgba(0,0,0,0.51), 1px -1px 2px rgba(0,0,0,0.51);
	border: 1px solid #eee;
	border: 1px solid rgba(0, 0, 0, 0.05);
	-webkit-border-radius: 4px;
	border-radius: 4px;
	-webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
	box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.05);
}

.bootstrap-navbar-inner
{
	background-image: none !important;
	background: -moz-linear-gradient(top,  rgba(255,255,255,0.15) 0%, rgba(255,255,255,0.32) 51%, rgba(255,255,255,0.15) 100%) !important;
	background: linear-gradient(to bottom,  rgba(255,255,255,0.15) 0%,rgba(255,255,255,0.32) 51%,rgba(255,255,255,0.15) 100%) !important;
}

.highlight
{
	font-weight: bold !important;
	color: rgba(255, 255, 255, 0.8) !important;
	background-color: rgba(0, 136, 255, 0.8) !important;
	border-radius: 4px 4px 4px 4px !important;
	padding-left: 3px !important;
	padding-right: 3px !important;
}

span[id="contact"], input[id="vbshout_pro_shoutbox_editor"], input[value="Shout"], input[value="Smilies"], input[id="sb_mem_bold"], input[id="sb_mem_underline"], input[id="sb_mem_italic"], span[name="smiliebtn"], input[class="span6"], input[value="Update"], input[value="Delete"], input[value="Cancel"]
{
	background-color: rgba(0,0,0,0.7) !important;
    color: #c3c3c3 !important;
	background-image: none !important;
	border: none !important;
	text-shadow: none !important;
}

select[id="sb_color_mem"], select[id="sb_font_mem"], .btn-group>a
{
	background-color: rgba(0,0,0,0.7) !important;
    color: #c3c3c3 !important;
	background-image: none !important;
	border: none !important;
}

select[id="sb_color_mem"] option, select[id="sb_font_mem"] option
{
	background-color: rgba(0,0,0,0.7) !important;
}

select[id="sb_color_mem"] option[value="Black"], .bootstrap .bootstrap-navbar .dropdown-menu a
{
	color:#c3c3c3 !important;
}

.bootstrap .bootstrap-navbar .dropdown-menu:before, .bootstrap .bootstrap-navbar .dropdown-menu:after
{
	border-bottom: 6px solid rgba(0,0,0,0.7) !important;
}

.bootstrap .bootstrap-navbar .dropdown-menu
{
    background-color: rgba(0,0,0,0.7) !important;
}

/***** fonts and font colors *****/
a:hover
{
	text-decoration: none !important;
}

/* system and fixedsys fonts look like ass with italic - we can get rid of it :D */
span[style="font-family: System"] i, span[style="font-family: Fixedsys"] i
{
	font-style: normal;
}

/* lighten lots of font colors */
font[color="Black"]
{
	color: #c3c3c3;
}
font[color="Blue"]
{
	color: #2d4dff;
}
font[color="#0715db"]
{
	color: #2d4dff;
}
font[color="Red"]
{
	color: #ff3333;
}
font[color="Crimson"]
{
	color: #DC143C;
}
font[color="Purple"]
{
	color: #993399;
}
font[color="Green"]
{
	color: #339933;
}
font[color="Brown"]
{
	color: #B75555;
}

/***** usergroup colors *****/

/* admins */
span[style="font-weight:bold;color:red"]
{
	color: rgba(255, 255, 255, 0.8) !important;
	background-color: rgba(255, 0, 0, 0.8);
	border-radius: 4px 4px 4px 4px;
	padding-left: 4px;
	padding-right: 4px;
}

/* prems */
span[style="font-style:italic; color: #FF5e00;"]
{
	color: #FF5e00 !important;
}

/* formers (bold) */
span[style="font-weight: bold; color: #FF8040;"]
{
	border: dashed 1px rgba(255, 128, 64, 0.3);
	border-radius: 4px 4px 4px 4px;
	padding-left: 4px;
	padding-right: 4px;
}

/* formers (non-bold) */
span[style="color: #FF5e00;"]
{
	border: dashed 1px rgba(255, 94, 0, 0.3);
	border-radius: 4px 4px 4px 4px;
	padding-left: 3px;
	padding-right: 3px;
}

/* mods */
span[style="font-weight: bold; color: #2B6983"]
{
	color: rgba(255, 255, 255, 0.8) !important;
	background-color: rgba(43, 105, 131, 0.8);
	border-radius: 4px 4px 4px 4px;
	padding-left: 4px;
	padding-right: 4px;
}

/* supers */
span[style="font-weight: bold; color:black;"]
{
	color: rgba(255, 255, 255, 0.8) !important;
	background-color: rgba(0, 0, 0, 0.8);
	border-radius: 4px 4px 4px 4px;
	padding-left: 4px;
	padding-right: 4px;
}

/* reporters */
span[style="font-weight: bold; color:#0099ff;"]
{
	border: solid 1px rgba(0, 153, 255, 0.3);
	border-radius: 4px 4px 4px 4px;
	padding-left: 4px;
	padding-right: 4px;
}

/* gsquad */
span[style="font-weight: bold; color:#68c5e7;"]
{
	border: solid 1px rgba(104, 197, 231, 0.3);
	border-radius: 4px 4px 4px 4px;
	padding-left: 4px;
	padding-right: 4px;
}

/* gobble */
span[style="color:#696969;"]
{
	color: rgba(169, 169, 169, 0.7) !important;
	border: solid 1px rgba(169, 169, 169, 0.3);
	border-radius: 4px 4px 4px 4px;
	padding-left: 4px;
	padding-right: 4px;
}

/* blue elites */
font[color="#0715db"]
{
	color: #2B37DB !important;
}

/* pink elites */
font[color="#FF00FF"]
{
	color: #FF00FF !important;
}

/* green1 elites */
font[color="#2A782B"]
{
	color: #488B46 !important;
}

/* green2 elites */
font[color="#4BD579"]
{
}

/* green3 elites */
font[color="#14AF8D"]
{
}

/* raspberry elites */
font[color="#872657"]
{
	color: #9F5179 !important;
}

/* purple elites */
font[color="#7900d6"]
{
	color: #8C25D7;
}

/* grey elites */
font[color="#949494"]
{
	color: #d3d3d3;
}
    
/* banned users */
span[style="color: white;"]
{
	color: #000000 !important;
}
span[style="color: white;"]:after
{
	content: url(http://www.nextgenupdate.com/forums/images/smilies/banhamma.gif);
}

/* nons */
span[style="color: #A3A3A3;"]
{
}
}
