@namespace url(http://www.w3.org/1999/xhtml);
/**
 Stylish Template für FR Online
 
Autor: J. Strübig <jstruebig@web.de>
**/

@-moz-document domain("fr-online.de") {

body, html{
   padding:.2em;
    font-size:19px !important;
    font-family:  "DejaVu Serif" !important;
}
a:link{
        color:blue !important;    
        text-decoration:underline !important;
}
a:visited{
        color:#99f !important;
}

.bannerTop, .bannerRightAd, .AutoHideAd{
    display:none !important;
}

    /**
    Der eigentliche Inhalt
    */
    #All{
        position:static !important;
        top:0 !important;;
        width:55em !important;
        margin:0 auto !important;
        float:none !important;
    
    }
/*
Die obere (grüne) Linkleiste
*/
    #MainLinks{
        height: auto !important;
        float: left !important;
        width: 100% !important;
    }
    #MainLinks a:link{
        color:white !important;
    }

    #MainLinks a.MainLink.active{
        background: linear-gradient(to bottom , rgb(136, 255, 136) 0%, rgb(0, 128, 0) 100%)  !important;
        color:white !important;
    }
    #MainLinks a:visited{
        color:#aaa !important;
    }

    #SubNav{
        height: auto !important;
    }
    
    .HeaderThemenNav{
        width: auto !important;
        height: auto !important;
        line-height: 1em !important;
    }
    #ContainerContent{
        margin:0 auto !important;
        width: 55em !important;
    }
    
    #ContainerBoxen, .Redaktionsmodul{
        display:none !important;
    }
    
    .Col3Box, #ContainerContent {
        width: auto !important;
    }
    .teasertext{
        font-size:1em !important;
        line-height:1.2em !important;
    }
    .article_text, .articleText, .Col3Box p{
        font-size:18px !important;
        line-height: 1.3em !important;
        word-spacing:.2em !important;
        text-align:justify !important;
    }
} /* ende domain*/
