@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*  FAVICON  */
.tabbrowser-tab[label="Stylish"] .tab-icon-image {
    width: 16px !important;
    height: 16px !important;
    list-style-image: url(chrome://stylish/skin/16.png) !important; }
/*  ----  */



@-moz-document
    url("chrome://stylish/content/manage-standalone.xul"),
    url("chrome://stylish/content/manage-standalone.xul?sidebar") {

/*  MAIN CONTAINER  */

#stylish-manage-standalone {
    background-image: none !important;
    background-color: rgb(45,53,63) !important;
    color: rgb(51,159,254) !important;
	padding: 0px 0px 0px 0px !important;
}

/*  TOP BAR  */
#userstyles-heading {
    height: 30px !important;
}


/*  SEARCH INPUT  */
#styles-filter {
    -moz-appearance: none !important;
    margin: 0px 3px 0px 3px !important;
    border-radius: 0px !important;
    padding: 3px !important;
    border: 1px solid rgb(27,31,35) !important;
    color: rgb(255,255,255) !important;
    background-color: rgba(105,114,122,1) !important;
    background-image:
            linear-gradient(
                rgba(66,73,79,1),
                rgba(66,73,79,0)
                ) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 24px 0px 0px rgba(66,73,79,1) inset
            !important;
    transition: all 700ms ease-in-out 0s !important;
}/*  ----  */
#styles-filter:hover {
    -moz-appearance: none !important;
    border: 1px solid rgba(164,167,172,0.5) !important;
    transition: border 300ms ease 0s !important;
}/*  ----  */
#styles-filter[focused="true"] {
    color: rgb(185,191,198) !important;
    background-color: rgba(105,114,122,1) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 0px 0px 0px rgba(66,73,79,1) inset
            !important;
    transition: all 300ms ease 0s !important;
}


.textbox-search-icon { list-style-image: url(
                data:image/png;base64,
                ) !important;
  -moz-image-region: rect(0, 0px, 0px, 0) !important; }
.textbox-search-clear { list-style-image: url(
                data:image/png;base64,
                ) !important;
  -moz-image-region: rect(0, 0px, 0px, 0) !important; }
/*  ----  */






/*  GROUPS MENU  */
#styles-sort {
    width:auto !Important;
    height:10px !Important;
    -moz-appearance: none !important;
    padding: 0px 0px 0px 0px !important;
    margin: 1px 5px 2px 0px !important;
    font-size: 13px !important;
    background-color:rgb(64,72,82) !important;
    border: 1px solid rgba(179,182,186,0.4) !important;
    box-shadow:
              0 0 0 1px rgba(66,79,95,0) inset
              !important;
    transition: all 500ms ease 0s !important;
}
#styles-sort:hover,
#styles-sort:focus {
    -moz-appearance: none !important;
    background-color:rgb(37,44,51) !important;
    border-color: rgb(255,255,255) !important;
    box-shadow:
              0 0 0 1px rgba(66,79,95,0) inset
              !important;
    transition: all 300ms ease 0s !important;
}
#styles-sort .menulist-label-box {
    background-color: transparent !important;
    color: rgb(179,182,186) !important;
}

#styles-sort:hover .menulist-label-box ,
#styles-sort:focus .menulist-label-box {
    color: rgb(255,255,255) !important;
}

#styles-sort[open="true"] {
    border-color: rgb(23,185,247) !important;
    transition: all 300ms ease 0s !important;
}
#styles-sort[open="true"] .menulist-label-box {
    color: rgb(23,185,247) !important;
    text-shadow: 0px 0px 2px rgba(8,159,218,0.7) !important;
    transition: all 300ms ease 0s !important;
}



#styles-sort dropmarker{
    -moz-appearance: none !important;
    width: 12px !important;
    height: 12px !important;
    background-color: transparent !important;
    border: 0px solid transparent !important;
    padding: 7px 0px 0px 0px !important;
    opacity: 0.6  !important;
    list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAMUlEQVQoz8WRyQ0AMAzCQMr+K7srxI+o/C2uAjGaSJ0AJOkWwDhgImE6YEpjV+r/4x7lPgknh6mMxAAAAABJRU5ErkJggg==
                ) !important;
}
#styles-sort:hover dropmarker:not([disabled="true"]) ,
#styles-sort:focus dropmarker:not([disabled="true"]) {
    opacity: 1  !important;
}
dropmarker:hover:active:not([disabled="true"]),
menulist:hover:active > .menulist-dropmarker {
    padding: 7px 0px 0px 0px !important;
}
#styles-sort[open="true"] dropmarker { list-style-image: url(
                data:image/png;base64,
                ) !important;
    opacity: 1 !important; }
dropmarker[disabled="true"] { list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAMUlEQVQoz8WRyQ0AMAzCQMr+K7srxI+o/C2uAjGaSJ0AJOkWwDhgImE6YEpjV+r/4x7lPgknh6mMxAAAAABJRU5ErkJggg==
                ) !important;
    opacity: 0.05 !important; }
/*  ----  */






/*  LIST CONTAINER  */

#styles {
	-moz-appearance: none !important;
	background-color: rgb(52,65,80) !important;
	border: 0px solid !important;
	border-bottom: 1px solid rgb(63,74,88) !important;
}
#styles > scrollbox {
	background-color: rgb(37,44,51) !important;
}

richlistbox {
	-moz-appearance: listbox !important;
	margin: 0px 0px 0px 0px !important;
}
/*  ----  */





/*   HEADERS GROUP LIST  */

*[anonid="heading"] {
	background-color: rgb(37,44,51) !important;
	border-top: 1px solid rgb(55,58,66) !important;
	border-bottom: 1px solid rgb(25,30,35) !important;
	padding: 0px !important;
}
*[anonid="heading"]:hover ,
*[anonid="heading"]:-moz-focusring {
	background-color: rgb(31,37,43) !important;
}
*[closed="true"] *{
    -moz-appearance: none !important;
	color: rgb(146,150,153) !important;
}
*[closed="true"]:last-child {
	box-shadow: 0px 6px 10px 0px rgba(0,0,0,0.5) !important;
    transition: all 300ms ease-in-out 0s !important;
}


style-container > vbox > hbox:not([closed="true"]) hbox>label{
    color: rgb(23,185,247) ;
}
style-container:hover {
	background-color: rgb(31,37,43) !important;
}

style-container > vbox > hbox >button >hbox {
  border: 0px solid transparent !important;
  padding-top: 0px !important;
  padding-bottom: 2px !important;
  -moz-padding-start: 1px !important;
  -moz-padding-end: 1px !important;
}

*[anonid="twisty"] .button-icon {
	margin-right: 4px !important;
}
*[anonid="twisty"] { list-style-image: url(
               data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPElEQVQY02P8//8/AzGAhRhFjCd+/WchRhFBE2GK8CpEVoRTIboirAqxKcJQiEsRikJ8ihgYGBgYiQ1wAJ56HC2vHF1bAAAAAElFTkSuQmCC
               ) !important; }
*[closed="true"] *[anonid="twisty"] { list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAYklEQVQY033PywmAQAxF0RtwKQo2qFiZWy1JwRa0AIfnxoFhiHm7wMkPikiaJQ04aaq6A0ZJi5ldEQToganGHnTxH8w4n3FHEKAFBiCED7Ca2RmtzuiInknAViIPpm/SXne/bAklqD8BRaEAAAAASUVORK5CYII=
                ) !important; }
/*  ----  */



/*  STYLES  */
richlistitem
 {
	padding-top: 1px !important;
	padding-bottom: 2px !important;
	-moz-padding-start: 2px !important;
	-moz-padding-end: 3px !important;
	min-height: 20px !important;
	color: white !important;
	background-color: rgb(52,65,80) !important;
	background-clip: border-box !important;
	border-bottom: 1px solid rgba(194,211,228,0.1) !important;
	box-shadow: 0px -1px 0px rgba(4,14,25,0.3) inset !important;
}
richlistitem[is-disabled="true"] {
	color: rgb(103,117,133)  !important;
}
richlistitem:hover{
	background-color: rgb(46,57,71) !important;
}

 richlistitem[selected="true"] {
	background-color: rgb(0,85,128) !important;
	color: white !important;
    background-image:
            linear-gradient(to right, black, black),
            linear-gradient(to bottom,
                rgb(19,77,140) 0%,
                rgb(0,67,127) 25%,
                rgb(0,47,88) 100%)
            !important;
    background-repeat: no-repeat, repeat-x !important;
    background-position: top right, top left !important;
    background-size: 1px 100%, auto !important;
    border-bottom: 1px solid rgba(11,12,13,0.25) !important;
    border-width: 0px !important;
    margin: 0px 0px 0px 1px !important;
}

*[anonid="tags"] {
	color: rgb(140,184,216) !important;
	font-style: normal !important;
}

/*  ----  *
#styles richlistitem *[anonid="tags"],
#styles richlistitem *[anonid="type"] 
}/*  ----  */






/*  ICON STYLES  */
#styles richlistitem:not([selected=true]) image[anonid="status-icon"] { list-style-image: url(
                data:image/png;base64,
                ) !important; }
#styles richlistitem[selected=true] image[anonid="status-icon"] { list-style-image: url(
                data:image/png;base64,
                ) !important; }
#styles richlistitem[is-disabled="true"]:not([selected=true]) image[anonid="status-icon"] { list-style-image: url(
                data:image/png;base64,
                ) !important;
     filter: none !important; }
#styles richlistitem[is-disabled="true"][selected=true] image[anonid="status-icon"] {
     list-style-image: url(
                data:image/png;base64,
                ) !important;
     filter: none !important; } 
/*  ----  */



/*  BUTTONS   */

button:-moz-focusring > .button-box,
menulist:-moz-focusring:not([open="true"]) > .menulist-label-box {
    border: 1px solid transparent !important; }

#styles  richlistitem deck > hbox {
    height: 28px !important;
    padding: 4px 0px 3px 0px !important;
}
#stylish-manage-standalone > hbox  {
    height: 29px !important;
    padding: 4px 0px 3px 0px !important;
}

richlistitem deck button ,
window > hbox button {
     -moz-appearance: none !important;
    background-color: rgba(58,68,79,0) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(122,137,152,0.47),
                rgba(129,139,150,0.14)
                ) !important;
    border-width: 0px !important;
    border-style: solid !important;
    border-color: rgb(29,37,46) !important;
    border-radius: 3px !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 1px 0px 1px rgba(140,158,176,0.40),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
    color: rgb(215,241,255) !important;
    text-shadow: 0px -1px 0px rgba(11,12,13,0.45) !important;
    transition: all 150ms ease-in-out 0s !important;
}

window > hbox button:hover,
window > hbox button:focus,
richlistitem deck button:hover,
richlistitem deck button:focus,
richlistitem deck button:focus:hover,
richlistitem deck button:focus:hover
{
    background-color: rgba(48,58,69,0) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(111,126,140,0.45),
                rgba(93,105,118,0.20)
                ) !important;
    border-width: 0px !important;
    border-style: solid !important;
    border-color: rgb(29,37,46) !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 0px 3px 2px rgba(10,18,25,0.25),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
	color: rgb(152,217,252) !important;
}
window > hbox button:active,
richlistitem deck button:active
window > hbox button:active:hover,
richlistitem deck button:active:hover
{
    background-color: rgba(5,15,26,0.05) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(255,255,255,0.0),
                rgba(0,0,0,0.0)
                ) !important;
    border-width: 0px !important;
    border-style: solid !important;
    border-color: rgb(29,37,46) !important;
    box-shadow:
          inset 0px 0px 4px 1px rgba(0,0,0,0.95),
          inset 0px 0px 0px 1px rgba(5,15,26,0.2),
                   0px 0px 0px 1px rgba(233,244,255,0.10)
          !important;
	color: rgb(101,191,255) !important;
}



button[anonid="delete"] {
    background-color: rgb(130,0,0) !important;
}
button[anonid="delete"]:focus,
button[anonid="delete"]:hover {
    color: rgb(255,255,255) !important;
    background-color: rgb(180,0,0) !important;
}
button[anonid="delete"]:active,
button[anonid="delete"]:hover:active {
    color: rgb(255,255,255) !important;
    background-color: rgb(100,0,0) !important;
}
/*  ----  */



/*  UPDATE PROGRESSMETER   */
progressmeter {
    -moz-appearance: none !important;
    margin: 6px 30px 6px 10px !important;
    border: 0px solid rgb(0,0,0) !important;
    background-color: transparent  !important;
}
.progress-bar {
    -moz-appearance: none !important;
    background-color: transparent  !important;
    background: url(
        data:image/gif;base64,
        )  no-repeat left center !important;
}
/*  ----  */



/*  MENU  */
menupopup {
   -moz-appearance: none !important;
   padding-top: 5px  !important;
   padding-bottom: 5px  !important;
   border-radius: 4px !important;
   font-weight: normal !important;
   border: 1px solid hsl(210,11%,10%) !important;
   box-shadow: 0 1px 0 hsla(209,29%,72%,.25) inset !important;
   background-color: transparent !important;
   background-image:
            linear-gradient(to bottom,
                hsl(209,18%,18%),
                hsl(210,11%,16%)
                ) !important;
}


menupopup menuitem:not([disabled="true"]) {
   -moz-appearance: none !important;
   color: rgb(204,204,204) !important;
}
menupopup menuitem[disabled="true"] {
    -moz-appearance: none !important;
    color: rgb(100,100,100) !important;
}


menupopup menuitem[_moz-menuactive="true"]:not([disabled="true"]) {
     color: hsl(208,100%,60%) !important;
     background-color: rgb(20,20,20) !important;
}
menupopup menuitem[_moz-menuactive="true"][disabled="true"] {
     background-color: rgb(20,20,20) !important;
}


menuseparator {
    -moz-appearance: none !important;
    padding: 0px 1px 0px 1px !important;
    margin: 2px 0px 2px 0px !important;
    border-top: 0px solid rgb(29,33,37)   !important;
    border-bottom: 0px solid rgb(58,70,80)   !important;
    height: 2px !important;
    background: 
            linear-gradient(to right,
                rgba(29,33,37,0) 0%,
                rgba(29,33,37,1) 20%,
                rgba(29,33,37,1) 80%,
                rgba(29,33,37,0) 100%) ,
            linear-gradient(to right,
                rgba(58,70,80,0) 0%,
                rgba(58,70,80,1) 20%,
                rgba(58,70,80,1) 80%,
                rgba(58,70,80,0) 100%)
             !important;
    background-repeat: no-repeat, no-repeat  !important;
    background-size: 100% 1px , 100% 1px  !important;
    background-position:  top center,  bottom center  !important;
    background-color:  transparent  !important;
}/*  ----  */


}
