@-moz-document
    url("chrome://stylish/content/edit.xul") {

/*  TEXTBOXES  */
textbox {
    -moz-appearance: none !important;
    margin: 0px 0px 0px 0px !important;
    min-height: 22px !important;
    border-radius: 0px !important;
    padding: 3px !important;
    border: 1px solid rgb(27,31,35) !important;
    color: rgb(200,200,200) !important;
    background-color: rgba(105,114,122,1) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 24px 0px 0px rgba(66,73,79,1) inset
            !important;
    transition: all 700ms ease-in-out 0s !important;
}
textbox:hover {
    -moz-appearance: none !important;
    border: 1px solid rgba(164,167,172,0.5) !important;
    transition: all 300ms ease 0s !important;
}
textbox[focused="true"] {
    color: rgb(255,252,255) !important;
    background-color: rgba(105,114,122,1) !important;
    background-image:
            linear-gradient(
                rgba(66,73,79,1),
                rgba(66,73,79,0)
                ) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 0px 0px 0px rgba(66,73,79,1) inset
            !important;
    transition: all 300ms ease 0s !important;
}
#name, #tag, #update-url {
    max-height: 22px !important;
}
::-moz-selection {
    background: rgb(20,20,20) !important;
    color: hsl(208,100%,60%) !important;
}
/*  ----  */





/*  NAME  */
#name {
    margin: 0px 0px 3px 64px !important;}
/*  NAME  */


/*  TAGS  */
#tags {
    margin: 0px 30px 3px 3px !important;}
#tags > hbox > input {
    background: url(
                data:image/png;base64,
                ) no-repeat scroll -1px -1px transparent !important;
    padding: 0px 0px 0px 17px !important;
}
#stylish > vbox > grid > rows > row:first-child {
    -moz-box-flex: 4 !important;
    min-width: 180px !important;}
#stylish > vbox > grid > rows > row:last-child {
    -moz-box-flex: 1 !important;}
/*  ----  */



/*  NAME & TAGS ONE LINE  */
#stylish > vbox > grid   {
    display: -moz-box !important;}
#stylish > vbox > grid > rows  {
    -moz-box-orient: horizontal !important;
    -moz-box-flex: 1 !important;}
#stylish > vbox > grid > rows > row > label {
    display: block !important;
    visibility: collapse !important;
}/*  ----  */







/*  TOP BUTTONS  */
#editor-tools > button:first-child > .button-box .box-inherit,
#wrap-lines .checkbox-label-box {
    display: none !important;
}
#editor-tools > button:first-child,
#wrap-lines {
    -moz-appearance: none !important;
    height: 22px !Important;
    min-width: 26px !Important;
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 0px !important;
    background-color: rgba(58,68,79,0) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(122,137,152,0.47),
                rgba(129,139,150,0.14)
                ) !important;
    border-width: 0px !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 1px 0px 1px rgba(140,158,176,0.40),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
    transition: all 150ms ease-in-out 0s !important;
}
#editor-tools > button:first-child:hover,
#editor-tools > button:first-child:focus,
#wrap-lines:hover:not([checked="true"]) ,
#wrap-lines:-moz-focusring:not([checked="true"]) ,
#wrap-lines:-moz-focusring:hover:not([checked="true"])
 {
    background-color: rgba(48,58,69,0) !important;
    background-image: 
            linear-gradient(to bottom,
                rgba(111,126,140,0.45),
                rgba(93,105,118,0.20)
                ) !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 0px 3px 2px rgba(10,18,25,0.25),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
    transition: all 150ms ease-in-out 0s !important;
}
#editor-tools > button:first-child[open="true"],
#wrap-lines[checked="true"] {
    background-color: rgba(5,15,26,0.05) !important;
    background-image: 
            linear-gradient(to bottom,
                rgba(255,255,255,0.0),
                rgba(0,0,0,0.0)
                ) !important;
    box-shadow:
          inset 0px 0px 4px 1px rgba(0,0,0,0.95),
          inset 0px 0px 0px 1px rgba(5,15,26,0.2),
                   0px 0px 0px 1px rgba(233,244,255,0.10)
          !important;
    transition: all 150ms ease-in-out 0s !important;
}/*  ----  */







/*  EDITOR TOOLS  */
#editor-tools {
    position: fixed !important;
    top: 2px !important;
    margin: 0px 0px 0px 3px !important;
    padding: 0px 0px 0px 0px !important;
    border: 0px solid !important;
    height: 22px !important;
    width: 57px !important;
}
#editor-tools > button:first-child .box-inherit.button-box , #editor-tools > #wrap-lines * {
    border: 0px solid !important;
    -moz-padding-start: 0px !important;
    -moz-padding-end: 0px !important;
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 0px !important;
}
#editor-tools > button:first-child .button-menu-dropmarker {
    -moz-appearance: none !important;
    list-style-image: url(
                data:image/png;base64,
                ) !important;
    width: 16px !important;
    height: 16px !important;
}
#editor-tools > button:first-child:hover .button-menu-dropmarker ,
#editor-tools > button:first-child:focus .button-menu-dropmarker {
    list-style-image: url(
                data:image/png;base64,
                ) !important;}
/*  ----  */





/*  WRAP LINES  */
#wrap-lines {
    display: block !important;
    position: relative !important;
    top: -22px !important;
    left: 31px !important;
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 5px !important;
}
#wrap-lines .checkbox-check {
    -moz-appearance: none !important;
    width: 16px !important;
    height: 16px !important;
    background: url(
                data:image/png;base64,
                ) transparent !important;
}
#wrap-lines:hover > .checkbox-check,
#wrap-lines:-moz-focusring > .checkbox-check {
    background: url(
                data:image/png;base64,
                ) transparent !important;}
#wrap-lines[checked="true"] > .checkbox-check {
    background: url(
                data:image/png;base64,
                ) transparent !important;}
#wrap-lines[checked="true"]:hover > .checkbox-check,
#wrap-lines[checked="true"]:-moz-focusring > .checkbox-check {
    background: url(
                data:image/png;base64,
                ) transparent !important;}
/*  ----  */





/*  UPDATE  */
#update-url {
    display: block !important;
    position: fixed !important;
    top: 0px !important;
    right: 0px !important;
    margin: 2px 3px 0px 0px !important;
    width: -moz-calc(100% - 67px) !important;
}
#update-url  .textbox-input-box {
    width: 100% !important;}
#update-url:not([focused="true"]) {
    max-width: 24px !important;
    transition: all 900ms cubic-bezier(25,.1,.25,1) 0s !important;
}
#update-url[focused="true"] {
    max-width: 100% !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 rgba(65,73,84,1),
            0px 0px 0px 0px rgba(66,73,79,1) inset
            !important;
    transition: all 900ms cubic-bezier(25,.1,.25,1) 0s !important;
}
#update-url > hbox > input {
  -moz-appearance: none !important;
    background: url(
                data:image/png;base64,
                ) no-repeat scroll -1px -1px transparent !important;
    padding: 0px 0px 0px 16px !important;
}
/*  ----  */












#internal-code * {
    -moz-appearance: none !important;
    background-color: rgb(19,28,38) !important;
    color: rgb(143,160,177) !important;
}






/*  BUTTON ICONS  *
button[label*="Insert"]
{
  -moz-appearance: none !important;
  min-width: 3.0em;
  min-height: 22px !important;
  background: url(
               data:image/png;base64,
                ) no-repeat center center !important;
}
button[label*="Previ"]
{
  background: url(
                data:image/png;base64,
                ) no-repeat 4px center !important;
  -moz-padding-start: 18px;
}
button[label="Guardar"], button[label="Save"]
{
  background: url(
                data:image/png;base64,
                ) no-repeat 4px center !important;
  -moz-padding-start: 18px;
}
button[label*="Cancel"]
{
  background: url(
               data:image/png;base64,
                ) no-repeat 4px center !important;
  -moz-padding-start: 18px;
}
/*  ----  */





/* ERRORs */
#errors {
    margin: -0px -0px -0px -0px !important;
    padding: 2px 0px 0px 0px !important;
  	overflow: auto !important;
    border-top: 1px solid rgb(63,74,88) !important;
  	 box-shadow:
            0px 1px 0px 0px  rgb(45,53,63) inset,
            10px 0px 15px -10px  rgba(0,0,0,1) inset,
    		    0px 3px 5px 1px rgba(0,0,0,0.5) inset
		        !important;
  	background-color: rgb(170,0,0) !important;
  	color: rgb(255,255,255) !important;
}
#errors > label {
	  margin: 0px 3px 2px 3px !important;
}
#errors[style="display: none;"] {
  	display: -moz-box !important;
    min-height: 0em !important;
    max-height: 0em !important;
    transition: all 1500ms ease-in-out 0s !important;
}
#errors[style="display: -moz-box;"] {
    display: -moz-box !important;
    min-height: 1em !important;
    max-height: 8em !important;
    transition: all 1000ms ease-in-out 0s !important;
}
/*  ----  */

}





@-moz-document
    url("chrome://stylish/content/edit.xul") ,
    url("chrome://stylish/content/install.xul") {


dialog {
    -moz-appearance: none !important;
    background-color: rgb(45,53,63) !important;
    color: rgb(51,159,254) !important;
	padding: 2px 0px 2px 0px;
}


/*  BUTTONS  */
#stylish > hbox {
    height: 18px !important;
    padding: 3px 3px 0px 3px !important;
    border-top: 1px solid rgb(63, 74, 88) !important;
}
.dialog-button {
    font: menu;
    margin: 0px 3px 0px 3px !important;
}

/*  BUTTONS  OUTLINE  */
button .button-box ,
button:-moz-focusring > .button-box {
    border: 0px solid transparent !important; }
/*  ----  */

/*  BUTTONS BACKGROUND  */
.dialog-button-box button {
     -moz-appearance: none !important;
    background-color: rgba(58,68,79,0) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(122,137,152,0.47),
                rgba(129,139,150,0.14)
                ) !important;
    border-width: 0px !important;
    border-style: solid !important;
    border-color: rgb(29,37,46) !important;
    border-radius: 3px !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 1px 0px 1px rgba(140,158,176,0.40),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
    color: rgb(215,241,255) !important;
    text-shadow: 0px -1px 0px rgba(11,12,13,0.45) !important;
    text-align: center !important;
    padding: 1px 2px 2px 4px !important;
    transition: all 150ms ease-in-out 0s !important;
}
.dialog-button-box button:focus,
.dialog-button-box button:hover,
.dialog-button-box button[type="menu"]:hover,
.dialog-button-box button[type="menu"]:focus {
    background-color: rgba(48,58,69,0) !important;
    background-image: 
            linear-gradient(to bottom,
                rgba(111,126,140,0.45),
                rgba(93,105,118,0.20)
                ) !important;
    border-width: 0px !important;
    border-style: solid !important;
    border-color: rgb(29,37,46) !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 0px 3px 2px rgba(10,18,25,0.25),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
	  color: rgb(152,217,252) !important;
}

.dialog-button-box button:active,
.dialog-button-box button:active:hover,
button[type="menu"][open="true"] {
    background-color: rgba(5,15,26,0.05) !important;
    background-image: 
            linear-gradient(to bottom,
                rgba(255,255,255,0.0),
                rgba(0,0,0,0.0)
                ) !important;
    border-width: 0px !important;
    border-style: solid !important;
    border-color: rgb(29,37,46) !important;
    box-shadow:
          inset 0px 0px 4px 1px rgba(0,0,0,0.95),
          inset 0px 0px 0px 1px rgba(5,15,26,0.2),
                   0px 0px 0px 1px rgba(233,244,255,0.10)
          !important;
	color: rgb(101,191,255) !important;
}


button[label*="Instal"] ,
button[label*="Guardar"] ,
button[label*="Save"] {
    background-color: rgb(0,102,0) !important;
}
button[label*="Instal"]:focus ,
button[label*="Instal"]:hover ,
button[label*="Guardar"]:focus ,
button[label*="Guardar"]:hover ,
button[label*="Save"]:focus ,
button[label*="Save"]:hover {
    color: rgb(255,255,255) !important;
    background-color: rgb(0,140,0) !important;
}
button[label*="Instal"]:active ,
button[label*="Instal"]:hover:active ,
button[label*="Guardar"]:active ,
button[label*="Guardar"]:hover:active ,
button[label*="Save"]:active ,
button[label*="Save"]:hover:active {
    color: rgb(255,255,255) !important;
    background-color: rgb(0,64,0) !important;
}

button[label*="Cancel"] {
    background-color: rgb(130,0,0) !important;
}
button[label*="Cancel"]:focus,
button[label*="Cancel"]:hover {
    color: rgb(255,255,255) !important;
    background-color: rgb(180,0,0) !important;
}
button[label*="Cancel"]:active,
button[label*="Cancel"]:hover:active {
    color: rgb(255,255,255) !important;
    background-color: rgb(100,0,0) !important;
}/*  ----  */






/*  MENU  */
menupopup {
    -moz-appearance: none !important;
    padding-top: 5px  !important;
    padding-bottom: 5px  !important;
    border-radius: 4px !important;
    font-weight: normal !important;
    border: 1px solid rgb(22,25,28) !important;
    box-shadow: 0 1px 0 rgba(162,184,204,0.25) inset !important;
    background-color: transparent !important;
    background-image:
            linear-gradient(to bottom,
                rgb(37,46,54),
                rgb(36,40,45)
                ) !important;
}


menupopup menuitem:not([disabled="true"]) {
    -moz-appearance: none !important;
    color: rgb(204,204,204) !important;
}
menupopup menuitem[disabled="true"] {
    -moz-appearance: none !important;
    color: rgb(100,100,100) !important;
}


menupopup menuitem[_moz-menuactive="true"]:not([disabled="true"]) {
    color: hsl(208,100%,60%) !important;
    background-color: rgb(20,20,20) !important;
}
menupopup menuitem[_moz-menuactive="true"][disabled="true"] {
    background-color: rgb(20,20,20) !important;
}


menuseparator {
    -moz-appearance: none !important;
    padding: 0px 1px 0px 1px !important;
    margin: 2px 0px 2px 0px !important;
    border-top: 0px solid rgb(29,33,37) !important;
    border-bottom: 0px solid rgb(58,70,80) !important;
    height: 2px !important;
    background: 
            linear-gradient(to right,
                rgba(29,33,37,0) 0%,rgba(29,33,37,1) 20%,
                rgba(29,33,37,1) 80%,rgba(29,33,37,0) 100%) ,
            linear-gradient(to right,
                rgba(58,70,80,0) 0%,rgba(58,70,80,1) 20%,
                rgba(58,70,80,1) 80%,rgba(58,70,80,0) 100%)
             !important;
    background-repeat: no-repeat, no-repeat !important;
    background-size: 100% 1px , 100% 1px  !important;
    background-position:  top center,  bottom center !important;
    background-color:  transparent !important;
}/*  ----  */

}


/*  SWITCH TO INSTALL  */
@-moz-document
    url("chrome://stylish/content/install.xul") {
#install-intro {
    font-size: 18px !important;}
}
/*  ----  */
