css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("runescape.com") {

  
   /*this fixes an issue where if a forum / thread doesnt have enough threads in it, the background behind the arrows is floating, this removes the image so its not ramdomly just floating you can delete this if you want to but its better than having a random floating background */
    
    div.PageControlAdvancedRight,
div.PageControlAdvancedLeft {
background:no-repeat;
    overflow:visible;
}
    /*this fixes an issue where the buttons are floating above the bottom page nav control bar by moving it to the proper area, you can remove this if you want , but you should just leave it as is */
    .PageControlAdvanced div.PageControlAdvancedRight .PageControlAdvancedDisabled {
 margin-top:43px
}
    
    
    
    
    /* the following hides the search user text you may delete this if you want to view the search user text */
    .userSearch #lookupuser label,
.userView #lookupuser label{
display:none;
}
    /*this removes the blue (rs3) background from around the area where you create a new thread*/
     #threadDetails{
        width: 813px;
        background:  no-repeat top left transparent;
        margin: auto;
        overflow: hidden;
        position: relative;
    }
        /* the following moves the navigation trail so that it is no longer hiding behind the play free now button*/
.SubNavigation{
 left: 263px;
    margin-left:-100px;
 position: absolute;
 top: 55px;
}
        /* the following hides the clan crest(rs3) while in account settings , you may remove this if you want to see the your crest */
    #CrestWrapper{display:none !important;}
    
        /* the following changes black text in a forum to white so its more easily readable you may delete this code to regain the original color */
 .bb-black {color:#fff ! important;}
    /* the following changes blue text in a forum to a ligher blue so its more easily readable you may delete this code to regain the original color */
     .bb-blue {color:#19bfe3 !important ;}
        /* the following changes purple text in a forum to a ligher purple  so its more easily readable you may delete this code to regain the original color */
     .bb-purple {color:#b416a7 !important ;}
        /* the following changes green text in a forum to a ligher green so its more easily readable you may delete this code to regain the original color */
     .bb-green {color:#0f0 ! important;}
    
    
        /* the following Removes the word "forums" on the top left you can remove this if you wish , altho its only hiding the word "forums" so that it doesnt take up space */
    #MainTitle{visibility:hidden;}

    
    /*the following gets rid of the gray background to the left of the chat box , you can remove this if you want to */
    .BubbleContainer.Blue .Bubble,
.BubbleContainer.Blue .BubbleHeader,
.BubbleContainer.Blue .BubbleFooter {
    background-image:   url('http://www.freddieyoho.com/runescape/imgs/rs-imgs/forumBubbleBlue.png?1');
}
    
    
    
        /*the following gets rid of the gray background to the left of the chat box , you can remove this if you want to */
.BubbleContainer.Green .Bubble,
.BubbleContainer.Green .BubbleHeader,
.BubbleContainer.Green .BubbleFooter {
    background-image:   url('http://www.freddieyoho.com/runescape/imgs/rs-imgs/aforumBubbleGreen.png?1');
}
    
        /*the following gets rid of the gray background to the left of the chat box , you can remove this if you want to */
.BubbleContainer.Gold .Bubble,
.BubbleContainer.Gold .BubbleHeader,
.BubbleContainer.Gold .BubbleFooter {
    background-image:   url('http://www.freddieyoho.com/runescape/imgs/rs-imgs/aforumBubbleGold.png?1');
}
    
    
        /*the following gets rid of the gray background to the left of the chat box , you can remove this if you want to */
.BubbleContainer.Purple .Bubble,
.BubbleContainer.Purple .BubbleHeader,
.BubbleContainer.Purple .BubbleFooter {
    background-image:   url('http://www.freddieyoho.com/runescape/imgs/rs-imgs/forumBubblePurple.png?1');
}

    
    
        /*the following gets rid of the under line under the main title , you can remove this if you wish*/
    
    .gradientUnderline{
        clear:none;
  
 background: no-repeat 0 0;
margin-top:-10px;



}    
    /*the following gets rid of the under line under the main title , you can remove this if you wish*/
.gradientUnderlineEnd{
 background: no-repeat right 0; 
     padding-right:6px;
 height:30px;
}
        /*the following gets rid of the under line under the main title , you can remove this if you wish*/
.gradientUnderline .gradientUnderlineNoEnd{
 background:  no-repeat right 0;

}
        /*the following gets rid of the under line under the main title , you can remove this if you wish*/
.gradientUnderlineEnd > div{

}
        /*the following gets rid of the under line under the main title , you can remove this if you wish*/
    .gradientUnderline .underlineLeft,.gradientUnderline .underlineRight{
 background:  no-repeat top left;

}    /*the following gets rid of the under line under the main title , you can remove this if you wish*/
.gradientUnderline .underLineCentre{
 background: no-repeat;

}    /*the following gets rid of the under line under the main title , you can remove this if you wish*/
.gradientUnderline .underlineLeft,.gradientUnderline .underlineRight{


}    /*the following gets rid of the under line under the main title , you can remove this if you wish*/
    .gradientUnderline .underlineRight{
 background:no-repeat;
}
    
        /*the following gets rid of the gray background around your threads, so that the oldschool background can be seen , you may remove this*/
    #contentmsg{
 background-color: rgba(255,255,255,0);
 border-left:0px solid #3A3A3A;
 border-right: 0px solid #3A3A3A;
 clear: both;
 margin: 0 95px;
 overflow: hidden;
 width: 808px;
   
}
    
      
        /*the following gets rid of the gray background to the left of the chat box , you can remove this if you want to */
    .BubbleContainer.Grey .Bubble,
.BubbleContainer.Grey .BubbleHeader,
.BubbleContainer.Grey .BubbleFooter {
    background-image:   url('http://www.freddieyoho.com/runescape/imgs/rs-imgs/forumBubbleGrey.png?1');
}
    
    /*the following fixes a problem with oldschool advice not being clickable, it is NOT advised to remove this */
    
        #group65 .RaggedBoxHeader{
            display:none;
 position: absolute;
 left: 0px;
 top: 0;
 height: 54px;
 width: 100%;
 z-index: 3;
}
        /*the following fixes a problem with oldschool - Miscellaneous not being clickable, it is NOT advised to remove this */
           #group52 .RaggedBoxHeader{
            display:none;
 position: absolute;
 left: 0px;
 top: 0;
 height: 54px;
 width: 100%;
 z-index: 3;
}
        /* the following code removes the brown border on the very bottom of the forums screen , you can remove this to show the border if you choose to do so */
    
    
.bottomBackground {
 background: no-repeat center bottom;
 bottom: -20px;
 height: 306px;
 position: absolute;
 width: 100%;
}    
    
    
    



    
        
        /* the following code fixes a random blank area from making the curser a pointer finger that when clicked does absolutly nothing */
    
    
    .RaggedBoxTogglyJs .RaggedBoxHeader {
    cursor: default;
}
    
    
        
        /* the following code removes the background around forums, jump thread , seach threads, code of conduct and my porfile , you can remove this if you would like to see the background*/
    
    .MainContentTopBg{
 background: no-repeat 0 0;
 overflow: hidden;
}
    
    

    
      /*the following code adds an orange border around the forum names/ back forward bar button and also fixes a problem where the border wasn't around the image but just a blank area and shortens the bar so that it doesnt overlap the background*/
    .BarFullTop{
 background: url(http://www.freddieyoho.com/runescape/imgs/rs-imgs/BarFullTop.png) top left no-repeat;
        border: 1px solid #F3B13F;
        border-radius:30px;
 width: 700px;
 height: 45px;
 float: left;

    }
    /*this fixes an issue where the next page buttons were hidden , DO-NOT CHANGE this */
 div.PageControlAdvanced {
 background: url(http://www.runescape.com/img/global/content/BarFullBottom.png?1) top left no-repeat;
 margin: 0 0 0 148px;
 width: 700px;
 height: 45px;
        border: 1px solid #F3B13F;
        border-radius:30px;
 float: left;
 padding-top: 11px;
 text-align: center;
}
        /*this fixes an issue where the next page buttons were hidden , DO-NOT CHANGE this */
    .PageControlAdvanced div.PageControlAdvancedRight {
    margin-right: 15px;
       margin-top: -37px;
}

     /* =======centers the main title inside the forums===========*/
.headerBar{
    margin: 32px 0 -3px 148px;
    position: relative;
    z-index: 5;
}
 /* =======centers the main title on the main screen===========*/
.rootView   .headerBar{
 margin: 32px 0 -3px 148px;
}
     /* =======moves the page details so that they arent hidden , DO NOT CHANGE THIS ===========*/
    .headerBar #threadQuickDetails{
    position: absolute;
    bottom: -7px;
    left: 333px;
    text-align: center;
    width: 211px;
    font-size: 11px;
    height: 36px;
    padding-top: 4px;
    line-height: 16px;
        margin: 12px 0 18px 200px;
    background:  bottom center no-repeat;
}
    
    
    
    
    /* the following code removes the sky background in the back and replaces it with the oldschool background, you may remove this to return the sky background  */
   

    
#Background{
background-image: url(http://www.runescape.com/img/rsp777/bg2.jpg);
    background-position: top center;
    background-repeat: repeat-y;

}

    

    
    
    
    
        /* the following code removes the background grayish black background around the forums  if you wish to remove this the border will return */
    
    .MainContentBg{
 background:  repeat-y top;


}
    
    
    
    
    
    
    
    

        /* the following code removes the + and -  icons you can remove this line if you choose */
.RaggedBoxTogglyJs .RaggedBoxToggle{
 display:none;
}
    
    
    /* the following code removes the legend for title, icon, threads, posts and date  you can remove these to show the legend if you perfer it  */
.forumData span.title{display:none;}
.forumData span.icon{display:none;}
.forumData span.threads{display:none;}
.forumData span.posts{display:none;}
.forumData span.date{display:none;}
    
    /* this hides the line above the oldschool announcement section - you can remove the following line to show the line if you perfer it showing */
#group63 .dataHeader{display:none;}

    
    .forumData.threads .dataHeader{display:none;}
  
    
    /* the following code makes the border slightly brighter you may remove the following code to change the border back to the original color, to remove the border completely change border-bottom: 1px solid #F3B13F; to border-bottom: 0px solid #F3B13F; to make the border thicker increese the 1px to how ever thick you want it  */
    
    
.dataHeader{    font-size: 0px;
    
    border-bottom: 1px solid #F3B13F;
    height: 17px;
    margin-bottom: 12px;
    width: 758px;}
    
    
    
    
    
    
       /* the following line of text hides the blue background around the posts (inside all forums), you may remove this to show the background if you choose*/
    
         #forumBox .RaggedBoxHeader,
#forumBox .RaggedBoxTop,
#forumBox .RaggedBoxBg,
#forumBox .RaggedBoxBottom{
 background:  0 top no-repeat;
}
    
    
    
    
   /* the following hides the group title names , you can remove the line below to show the titles of all groups  */
.RaggedBoxTitle{display:none;}

    
    
