#CrumbTrailContainer {
 height: 0px;
 display:none;

 width: 40px;  
}
#CrumbTrail{
 position: absolute;
 top: 105px;
   
 margin-left:-150px;
 font-size: 10px;
 line-height: 0.9em;
 color: #797979;
 z-index: 10;
}
 #Trail{
  line-height: 15px;
 }
 #CrumbTrail a{
  color: #CE9759;
  -webkit-transition: color 300ms  ; -webkit-backface-visibility: hidden; transition: color 300ms  ;

 }
#CrumbTrail a:hover{
 color: white;
 text-decoration: none;
 -webkit-transition: color 100ms  ; -webkit-backface-visibility: hidden; transition: color 100ms  ;

}
#CrumbTrail span{

}


