css:
@-moz-document domain("onvasortir.com") {

#pmfooter-wrapper {
    display:none !important;
}

body {
  padding:0 !important;
}
/* retaillage des éléments de structure de la page */
.Site,
.geo-header,
.hg,
.hd  {
  width:100% !important;
}

.TabPrincipal   {
  width:1250px !important;
}

.Navi { width:332px !important; }
.background_centre { width:918px !important; }
.background_droite { display:none !important; }
table.TabConnectes { width:50px !important; }

/* colonne de gauche */
.NaviBloc {
  float:left;
}

/* -- bloc 1 : mon compte -- */
.NaviBloc, .NaviBloc>tbody>tr>td { position:relative !important; }
.Navi>.NaviBloc:first-child { border:1px solid black !important; }
.Navi>.NaviBloc:first-child * { background:none !important; }

.NaviBloc_Haut2 { padding-left:0 !important; }
.NaviBloc_Milieu2>div:first-child { float:right; position:relative; top:-30px; width:30px}
.NaviBloc_Milieu2>div:first-child+br { display:none}
.NaviBloc_Milieu2 { padding-top:0 !important; }
a[href="vue_sortie_all.php?autre=Cadeau"]+br { display:none; }

.Navi>table:first-child+div,
.Navi>table:first-child+div+br {
display:none;
}
/* -- bloc 2 : calendrier -- */
.calendar2009-haut,
.calendar2009-bas {
  display:none;
}
.calendar .footrow {
  display:none;
}

/* -- bloc 3 : anniversaires -- */

/* -- bloc 4 : sondage -- */
.Navi table .NaviBloc:first-child+a+.NaviBloc br { display:none }
.Navi table .NaviBloc:first-child+a+.NaviBloc .sondage_res { float:none !important; }
.Navi table .NaviBloc:first-child+a+.NaviBloc .sondage_res+br { display:block !important; }
/* -- bloc 5 : bienvenue -- */
.Navi table .NaviBloc:first-child+a+.NaviBloc+.NaviBloc {
clear:both;
}
/* -- bloc 6 : un membre -- */


/* colonne de droite */
#statut {
  width:230px;
}
#SaveStatut {
  position:absolute;
  top:0;
  right:0;
}
.background_droite div {
  position:relative;
  text-align:left;
}

table.TabConnectes,
table.TabConnectes table,
table.TabConnectes tr,
table.TabConnectes td {
  display:block;
}
table.TabConnectes tr { clear:both; }
table.TabConnectes td { float:left; }
.TabConnectesFemmes,
.TabConnectesHommes {
  width:100px;
}

.Barre {
    display:none;
}
    
/* suppression de certaines marges */
.jeslide+br { display:none }
td[width="17"] { display:none }
.MenuLigne1_TD { padding-top:0 !important }
.deploi { display:none }
.NaviBloc {  margin-left:0px !important; }
.NaviBloc_Bas2 { display:none; }
.Navi { padding-left:2px !important; padding-right:0 !important; }

/* suppression de certains éléments inutiles */
.EnteteSite,
.geo-footer,
.jeslide,
.SeparateurHorizontal,
img[src="http://www.OnVaSortir.com/design/puce1.gif"],
img[src="etoile_argent.gif"],
img[src="etoile_bronze.gif"],
img[src="etoile_or.gif"],
img[src="etoile_platine.gif"]  {
    display:none !important;
}

/* liste de sorties */
.suisinscrit {
  background-color:#30FF30 !important;
}

/* détail d'une sortie */
.PADentete_corps {
  display:none
}
.PADpost_corps {
  padding-bottom:0px !important;
  float:left;
}
.PADpost_ecarte {
  padding:0 !important;
}

/* forum : on garde la notion de blocs */
.PADtitreBlanc_global {
  clear:both;
}

/* forum : pagination en dessous */
#dernierforum>center {
  clear:both;
}

/****** REGLAGES ********/
/* affichage des bords */
.TabPrincipal>tbody>tr>td {
/*  border:1px solid blue; */
}
/* on ne centre rien */
center {
  text-align:left !important;
}


.background_centre > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > div:nth-child(3) {
    /*  display:none; */
}

} /* end @-moz-document */
