css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.l-content, .l-content clearfix{background:none!important;}
/*--BERKAY YURUR--*/	
div.plexi.show.color {background:rgba(0,0,0,0.5)!important;}

.controls_section.follow_list.recommended_tumblelogs, .controls_section.follow_list.trending_tumblelogs {width:inherit!important;}
      
.search_results_container .posts_wrapper {background-color:rgba(0,0,0,0)!important;}

.search_results_flex #container:before, .search_results_container .tumblelog_mask_item {background-color:rgba(0,0,0,0)!important;}

.post_compact_alt{background: url('http://i.imgur.com/KlegjcC.jpg')!important;}

.post_compact_alt.content_normal .post_container:after, .radar .radar_footer .radar_avatar:before{display:none!important;}

.post_compact_alt.content_media_cover .post_content:before {background-color:transparent!important;}

.post_full .post_footer {background:none!important;}

.controls_section.user_list li a:before {background:none!important;border-right:none!important;}

div.follow {color:#333!important;}

.controls_section.user_list li a .tumblelog_title, .controls_section.user_list li a .tumblelog_name {color:rgba(0,0,0,0.5)!important;}
	
.logo{-webkit-transform:rotate(4deg); transform:rotate(4deg); width:200px!important;} 
    	
li.section_header.account_header, .controls_section li.section_header  {color:#333!important;}

#popover_button_blogs {background-size:100% 100%!important;}
  
.search_form_row {background:none!important;}

.controls_section{border-radius:5px!important;}
	
.post_full:not(.new_post_buttons) {
  background: url('http://i.imgur.com/KlegjcC.jpg')!important;
  background-size:100% auto!important;
  box-shadow:none!important;
}

@media screen and (-webkit-min-device-pixel-ratio:0) {
  .post .post_content {background:transparent!important;
padding-left:10px!important; padding-right:15px!important;}

 .post_full:not(.new_post_buttons) {
  padding: 10px 0px!important;
  background:  transparent !important;
  background-size:100% 100%!important;
  box-shadow:none!important;
border-style: solid!important;
border-width: 10px 10px 10px 10px!important;
-moz-border-image: url(http://i.imgur.com/6HyoBt4.png) 36 22 31 20 stretch;
-webkit-border-image: url(http://i.imgur.com/6HyoBt4.png) 36 22 31 20 stretch;
-o-border-image: url(http://i.imgur.com/6HyoBt4.png) 36 22 31 20 stretch;}
}
  
body, #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
	background-color:#000!important; 
  	background-image: url('http://static.tumblr.com/aakvbav/KKtnad809/asdasdaci.jpg')!important; 
    background-repeat:repeat-y;
  	background-size:100% auto; 
  	background-attachment:fixed!important;}

div.plexi.color.active.show, .ui_dialog_lock.opaque, #fullscreen_post_bg {
  background-image: url('http://static.tumblr.com/aakvbav/KKtnad809/asdasdaci.jpg')!important; 
  opacity:0.7!important;}

.ui_dialog,  #signup_forms_container, #signup_forms_submit{
    -webkit-transform:rotate(-2deg) translate3d(0,0,0);
    -moz-transform:rotate(-2deg) translate3d(0,0,0)!important;}
  
#signup_account{
    -webkit-transform:rotate(2deg) translate3d(0,0,0);
    -moz-transform:rotate(2deg) translate3d(0,0,0)!important;}
  
a, .txt{
    color:#000!important;}

.input_wrapper, .tags{
    background: rgba(255,255,255,0.8)!important;}

.right_column{
    background:transparent!important;}
	
.left_column{
    background:transparent!important;}	

.controls_section a .count {
    color:#000!important;}

.selection_nipple, #right_column:after, .post_avatar:after {
    display:none!important;}

.chrome.close{
    -webkit-transform: rotate(-3deg) translate3d(0,0,0); 
    -moz-transform: rotate(-3deg) translate3d(0,0,0)!important;}
    
.notification_pane{
    background:transparent!important;}

 .controls_section, .radar_controls, 
.left_column.notifications_page,  .white_pane, #main_column, #page_nav, #fixed_navigation, #help_full, #following, .finder_state, .chrome_nav, #staff_picks, .tabs_3, #tabs, .popover_inner, .search_subheading, .post_form_wrapper_inner, a.customize{
	background-image: url('http://static.tumblr.com/aakvbav/OJonad4iz/sagmen__.jpg')!important; 	
    background-size:100%;
    box-shadow:0px 5px 10px rgba(0,0,0,0.7);}
    
#post_form.active{
    background-image: url('http://static.tumblr.com/aakvbav/OJonad4iz/sagmen__.jpg')!important; 	
    background-size:100%;}
    
#content_bottom{
    display:none!important;}
    
#dashboard_controls_open_blog{
    background-image: url('http://i.imgur.com/Fcsr7DR.png')!important; 
    box-shadow:none!important;}
    
div.notification_inner.clearfix, a.posts, a.followers, a.activity, a.drafts, a.members, a.queue, #xkit_customize_button, .chrome.blue{
    background-image: url('http://static.tumblr.com/aakvbav/OJonad4iz/sagmen__.jpg')!important; 	
    background-size:100%; 
    transition-duration:0.3s; }
    
a.queue{
    border-bottom-left-radius: 5px!important; 
    border-bottom-right-radius: 5px!important; 
    z-index:80!important;}

#popover_button_blogs{
    background-image: url('http://i.imgur.com/Ifr4YZv.png')!important;}

.controls_section li:first-child, .controls_section li.first {
    border:0px!important; 
    box-shadow:none!important;}

.notification{
    color:#333!important;}

div#logout_button.tab.iconic{
    -webkit-transform: rotate(24deg); 
    -moz-transform: rotate(24deg)!important;}

a#new_post_label_text.new_post_label, a#new_post_label_photo.new_post_label, 
a#new_post_label_link.new_post_label,
a#new_post_label_chat.new_post_label,
a#new_post_label_video.new_post_label{
	background-image: url('http://static.tumblr.com/aakvbav/OJonad4iz/sagmen__.jpg')!important; 	
    background-size:100% 100%;
  	opacity:1!important;
    transition-duration:0.5s;}

a#new_post_label_audio.new_post_label{
  background-image: url('http://static.tumblr.com/aakvbav/OJonad4iz/sagmen__.jpg')!important;}

a#new_post_label_quote.new_post_label{
  background-image: url('http://static.tumblr.com/aakvbav/OJonad4iz/sagmen__.jpg')!important;}
   
        
#posts{ 
    background-image: url('http://static.tumblr.com/aakvbav/0qSnad57l/postarka.jpg')!important;
    background-position:-90px 20px;
    background-repeat:repeat-y;}        

a#new_post_label_text.new_post_label{
    -webkit-animation:swing 2.5s infinite ease-in-out; 
    animation: swing 2.5s infinite;
    border-top-left-radius: 8px;
    border-bottom-left-radius: 8px;
    z-index:100;
	
    box-shadow: -1px 2px 10px rgba(0,0,0,0.4)!important;}
          
@-webkit-keyframes swing{
      0%{-webkit-transform:rotate(-2deg) translate(2px,2px)}
     50%{-webkit-transform:rotate(-10deg) translate(9px,8px)}
    100%{-webkit-transform:rotate(-2deg) translate(2px,2px)}
}  
@-moz-keyframes swing{
      0%{ -moz-transform: rotate(-2deg) translate(2px,2px); }
     50%{ -moz-transform: rotate(-10deg) translate(9px,8px); }
    100%{ -moz-transform: rotate(-2deg) translate(2px,2px); }
}  
        
@-webkit-keyframes swing2{
      0%{-webkit-transform:rotate(-5deg) translate(0px,9px)}
     50%{-webkit-transform:rotate(-3deg) translate(0px,6px)}
    100%{-webkit-transform:rotate(-5deg) translate(0px,9px)}
}   
@-moz-keyframes swing2{
      0%{ transform: rotate(-5deg) translate(0px, 9px); }
     50%{ transform: rotate(-3deg) translate(0px, 6px); }
    100%{ transform: rotate(-5deg) translate(0px, 9px); }
}   

@-webkit-keyframes rotate{
      0%{-webkit-transform:rotate(2deg) }
     50%{-webkit-transform:rotate(-2deg) }
    100%{-webkit-transform:rotate(2deg) }
} 
                            
@-moz-keyframes rotate{
      0%{ -moz-transform: rotate(2deg); }
     50%{ -moz-transform: rotate(-2deg); }
    100%{ -moz-transform: rotate(2deg); }
}             
        
@-webkit-keyframes bounce{
      0%{-webkit-transform: rotate(3deg) translate(3px,2px);}
     70%{-webkit-transform: rotate(14deg) translate(12px,8px);}            
     85%{-webkit-transform: rotate(12deg) translate(10px,7px);}
    100%{-webkit-transform: rotate(14deg) translate(12px,8px);}
}  
                                          
@-moz-keyframes bounce{
      0%{ -moz-transform: rotate(3deg) translate(3px,2px); }
     70%{ -moz-transform: rotate(14deg) translate(12px,8px); }            
     85%{ -moz-transform: rotate(12deg) translate(10px,7px); }
    100%{ -moz-transform: rotate(14deg) translate(12px,8px); }
}      

@-webkit-keyframes flicker{
    0%{opacity:1}
    2%{opacity:0.2}
    15%{opacity:0.2}                
    16%{opacity:1}
    18%{opacity:0.2}
    38%{opacity:0.2}                
    40%{opacity:0.8}
    41%{opacity:1}
    43%{opacity:0.2}
    47%{opacity:0.2}                
    48%{opacity:0.9}
    49%{opacity:0.3} 
    70%{opacity:0.3}
    71%{opacity:0.9}
    72%{opacity:0.6}
    95%{opacity:0.3}
    96%{opacity:0.8}
    97%{opacity:0.3}
    99%{opacity:0.8}
}  
                                                  
@-moz-keyframes flicker{
     0%{ opacity:1; }
     2%{ opacity:0.2; }
    15%{ opacity:0.2; }                
    16%{ opacity:1; }
    18%{ opacity:0.2; }
    38%{ opacity:0.2; }                
    40%{ opacity:0.8; }
    41%{ opacity:1; }
    43%{ opacity:0.2; }
    47%{ opacity:0.2; }                
    48%{ opacity:0.9; }
    49%{ opacity:0.3; } 
    70%{ opacity:0.3; }
    71%{ opacity:0.9; }
    72%{ opacity:0.6; }
    95%{ opacity:0.3; }
    96%{ opacity:0.8; }
    97%{ opacity:0.3; }
    99%{ opacity:0.8; }
}    
            
@-webkit-keyframes flickerhover{
    0%{opacity:1}
    2%{opacity:0.2}
    15%{opacity:0.2}                
    16%{opacity:1}
    18%{opacity:0.2}
    38%{opacity:0.2}                
    40%{opacity:1}
    72%{opacity:0.9}
    95%{opacity:1}
    99%{opacity:1}
}  
  
@-moz-keyframes flickerhover{
     0%{ opacity:1; }
     2%{ opacity:0.2; }
    15%{ opacity:0.2; }                
    16%{ opacity:1; }
    18%{ opacity:0.2; }
    38%{ opacity:0.2; }                
    40%{ opacity:1; }
    72%{ opacity:0.9; }
    95%{ opacity:1; }
    99%{ opacity:1; }
}     
        
@-webkit-keyframes pulse{
      0%{opacity:1}
     50%{opacity:0.8}
    100%{opacity:1;}
}

@-moz-keyframes pulse{
      0%{opacity:1;}
     50%{opacity:0.8;}
    100%{opacity:1;}
}

  
@-webkit-keyframes float{
    0%{-webkit-transform: translate(-2px, 2px)}
    50%{-webkit-transform: translate(2px, -2px)}
    100%{-webkit-transform: translate(-2px, 2px)}
} 
  
@-moz-keyframes float{
      0%{ transform: translate(-2px, 2px); }
     50%{ transform: translate(2px, -2px); }
    100%{ transform: translate(-2px, 2px); }
}   
        
@-webkit-keyframes dropslide{
    0%{-webkit-transform: rotate(-1deg) translate(0px,0px)}
    20%{-webkit-transform: rotate(-5deg) translate(0px,0px)}
    100%{-webkit-transform: rotate(-5deg) translate(-20px, 1px)}
} 
@-moz-keyframes dropslide{
      0%{ transform: rotate(-1deg) translate(0px,0px); }
     20%{ transform: rotate(-5deg) translate(0px,0px); }
    100%{ transform: rotate(-5deg) translate(-20px, 1px); }
} 
         
a.activity{ 
    -webkit-transform-origin:bottom right; 
    -webkit-transform:rotate(-1deg); 
    -moz-transform-origin:bottom right; 
    -moz-transform:rotate(-1deg);}
              
a.activity:hover{
    -webkit-animation:dropslide 1s forwards ease-in-out!important;z-index:1337; 
    animation:dropslide 1s forwards ease-in-out!important;z-index:1337;}             

.tab_notice.inbox_notice_container{
    -webkit-animation:float 4s infinite ease-in-out; 
    animation:float 4s infinite ease-in-out;  
    transform-origin: 120% 120%;}
  
.tab.iconic.selected>a, #new_post_notice_container  {
    -webkit-animation:pulse 2s infinite ; } 
              
#logo a{
    background-image: url('http://i.imgur.com/JjxAzpR.png')!important;
    -webkit-animation:flicker 5s infinite; 
    animation:flicker 5s infinite;}  
       
#logo a:hover{
    -webkit-animation:flickerhover 2s forwards; 
    animation:flickerhover 2s forwards ;}   
  
.post_avatar{ 
    -webkit-animation: pulse 2s infinite ease-in-out;
    -webkit-transform: rotate(3deg);
    opacity:1;}
              
a#new_post_label_link.new_post_label{ 
    -webkit-transform: rotate(3deg) translate(3px,2px)  translate3d(0,0,0)!important;
    -moz-transform: rotate(3deg) translate(3px,2px)  translate3d(0,0,0)!important;
    box-shadow: 2px 2px 2px rgba(0,0,0,0.5)!important;
    z-index:100!important;}
        
a#new_post_label_link.new_post_label:hover{         
    -webkit-transform: rotate(14deg) translate(12px,8px)  translate3d(0,0,0)!important;
    -moz-transform: rotate(14deg) translate(12px,8px)  translate3d(0,0,0)!important;
    box-shadow: 5px 2px 10px rgba(0,0,0,0.3)!important;
    z-index:100!important;
    transition-delay: 0.1s;
    transition-duration:0.3s;
    transition: all 500ms cubic-bezier(1.000, -0.020, 0.415, 1.250);}

a#new_post_label_chat.new_post_label{
    -webkit-transform: rotate(-1deg) translate3d(0,0,0);
    -moz-transform: rotate(-1deg) translate3d(0,0,0);
    box-shadow: 2px 2px 10px rgba(0,0,0,0.4);
    z-index:99;}
  
a#new_post_label_photo.new_post_label{ z-index:99; box-shadow: 2px 2px 10px rgba(0,0,0,0.7);} 
                                                                  
a#new_post_label_quote.new_post_label{
    transition-duration:0.5s;
    -webkit-transform: translate(0px, 5px) rotate(1deg) translate3d(0,0,0);
    -moz-transform: translate(0px, 5px) rotate(1deg) translate3d(0,0,0);
    border-bottom-left-radius:5px;
    border-bottom-right-radius:2px;
    border-top-right-radius:2px;}
    
a#new_post_label_quote.new_post_label:hover{
    -webkit-transform: translate(0px, 5px);
    -moz-transform: translate(0px, 5px);}
        
a#new_post_label_video.new_post_label{ 
    border-top-right-radius: 8px;
    border-bottom-right-radius: 8px;
    -webkit-transform: rotate(-1deg) translate(-1px,2px)  translate3d(0,0,0)!important;
    -moz-transform: rotate(-1deg) translate(-1px,2px)  translate3d(0,0,0)!important;}

a#new_post_label_video.new_post_label:hover{    
    -webkit-transform: rotate(-12deg) translate(-5px,23px)  translate3d(0,0,0)!important; 
    -moz-transform: rotate(-12deg) translate(-5px,23px)  translate3d(0,0,0)!important; 
     transition-duration:0.3s;
     box-shadow: -5px 2px 5px rgba(0,0,0,0.3)!important;
     transition: all 500ms cubic-bezier(1.000, -0.185, 0.720, 0.745); }

a.posts{ 
    -webkit-animation:rotate 5s infinite ease-in-out; -webkit-transform: translate3d(0,0,0);
    animation:rotate 5s infinite ease-in-out; -moz-transform: translate3d(0,0,0); 
    box-shadow:0px -2px 5px rgba(0,0,0,0.3);} 
           
a.posts:hover{
    -webkit-transform: rotate(2deg) translate3d(0,0,0);
    box-shadow:0px -2px 5px rgba(0,0,0,0.3);}           

a.followers{
    box-shadow:0 -2px 5px rgba(0,0,0,0.4); 
    -webkit-transform:rotate(1deg);
    -moz-transform:rotate(1deg);}

a.send_fan_mail{
    -webkit-transform:rotate(-3deg);
    -moz-transform:rotate(-3deg);}
                 
a.drafts{     
    -webkit-animation: swing2 4s infinite ease-in-out;  
    -webkit-transform: rotate(-5deg) translate(1px,10px) translate3d(0,0,0)!important;
    animation:swing2 4s linear 0s infinite;  
    transform: rotate(-5deg) translate(1px,10px) translate3d(0,0,0)!important;
    border-bottom-left-radius: 8px;
    box-shadow: -2px 5px 5px rgba(0,0,0,0.5); 
    z-index:99!important; 
      }
            
a.followers:hover{
    -webkit-transform: rotate(-2deg) translate(0px,3px) translate3d(0,0,0)!important;
    -moz-transform: rotate(-2deg) translate(0px,3px) translate3d(0,0,0)!important;
    transition-duration:0.3s;
    box-shadow: -5px 2px 5px rgba(0,0,0,0.3)!important;
    opacity:1!important;z-index:100!important;}    
   
.post_full .post_permalink {
    background: url('http://i.imgur.com/licrBpW.png')!important;
    background-position:top right!important; 
    background-repeat:no-repeat!important;
    transition-duration:1s;         
}

.post_full .post_tags:after {
    display:none!important;}   
              
div#new_post.new_post_buttons.post.post_full.is_mine.with_avatar.new_post{
     background:transparent url('http://static.tumblr.com/aakvbav/OJonad4iz/sagmen__.jpg') no-repeat 2px 5px!important;
     box-shadow:none!important;}

.likes{
    -webkit-transform:rotate(1deg)translate3d(0,0,0)!important;
    -moz-transform:rotate(1deg)translate3d(0,0,0)!important;}

#right_column{background:transparent!important;}
 
#left_column{background:transparent!important;}
 
.post_full .post_header {color:#444!important;}


.post_full .post_footer {color:#333!important}


.post_full.is_note .post_body .note_item{ 
          box-shadow: inset 0 2px 10px 0px rgba(0,0,0,0.7);}
}
