css:
@-moz-document domain("wikipedia.org") {

* {
    border-color: #657b83 !important;
    outline-color: #657b83 !important;
    color: #839496 !important;
    background-color: transparent !important;
}

#footer, .navbox-group, .navbox-title, .navbox-list, table table, .articleFeedback-label, .articleFeedback-switch, .catlinks, .external, .wikitable td, h1, h2, h3, h4, h5, h6, h7, b, strong, em, i {
    background: transparent !important;
    color: inherit !important;
    border: none !important;
}
#mp-upper td {
    border: none !important;
}
html,body,#mw-page-base, .vectorTabs, .vectorTabs>ul, .vectorTabs>ul>li, #mw-head-base, #centralNotice>div, .ambox, .toc, .thumbinner, .navbox, .articleFeedback-panel, .tipsy-inner, .metadata, .infobox, .vertical-navbox, .wikitable, th, .gallerybox .thumb, .portal, .portal table, .navbox-abovebelow, .mw-search-formheader, .suggestions-results, .suggestions-special, #mp-topbanner, .MainPageBG, .fmbox, .ombox, .tmbox, .shortcutbox  {
    background: #eee8d5 !important;
    border: none !important;
}

#content, .vectorTabs>ul>li>span, .navbox-even, #collapsibleTable0, #collapsibleTable1, #collapsibleTable2, #collapsibleTable3, #collapsibleTable4, #collapsibleTable5, .wikitable th, .suggestions-result-current {
    background: #fdf6e3 !important;
    border: none !important;
}

#searchButton, .tipsy-arrow {
    display: none !important;
}

#searchInput {
    width: 100% !important;
}

#p-logo {
    padding-top: 30px !important;
    height: 113px !important;
    overflow: hidden !important;
}

.vectorTabs span {
    position: relative !important;
    top: .55em !important;
}
.vectorTabs a {
    padding-top: .7em !important;
}

.articleFeedback-label, .articleFeedback-switch, .external {
    padding: 0 !important;
}
.articleFeedback-rating-label {
    background: #839496 !important;
}
.articleFeedback-rating-label-full {
    background: #b58900 !important;
}
.articleFeedback-rating-clear {
    background: #dc322f !important;
}

:link {
    color: #268bd2 !important;
}
:visited {
    color: #6c71c4 !important;
}
::selection {
	background: #fdf6e3 !important;
	color: #839496 !important;
}
::-moz-selection {
	background: #93a1a1 !important;
	color: #fdf6e3 !important;
}
::-webkit-selection {
	background: #fdf6e3 !important;
	color: #839496 !important;
}

/* Hint to website designers --- DON'T USE STYLE= */
[style^="background:#770000"], [style^="background:#F00"] {
    background: transparent !important;
    border-color: #dc322f !important;
    color: #dc322f !important;
}
[style^="background:#FFEEAA"], [style^="background:#FEA"] {
    background: transparent !important;
    border-color: #cb4b16 !important;
    color: #cb4b16 !important;
}
[style^="background:#BBBBFF"], [style^="background:#BBF"],
[style^="background:#DDDDCC"], [style^="background:#DDC"] {
    background: transparent !important;
    border-color: #6c71c4 !important;
    color: #6c71c4 !important;
}
.tex, .summary img {
    background: #fff !important;
}
}
