css:
@-moz-document domain("android.com"){
*{
		border-color: #657b83 !important;
		outline-color: #657b83 !important;
		color: #839496;
		text-shadow: none !important;
		-moz-border-radius: 0 !important;
		-webkit-border-radius: 0 !important;
		-ms-border-radius: 0 !important;
		border-radius: 0 !important;
		-moz-box-shadow: none !important;
		-webkit-box-shadow: none !important;
		-ms-box-shadow: none !important;
		box-shadow: none !important;
	}
	
	html,body, select>option:hover, td, #classes-nav, #packages-nav, #side-nav, #devdoc-nav, .ui-resizable-handle, div.sidebox-wrapper, div.sidebox {
		background-image: none !important;
		background-color:#111111 !important;
	}
	div#sticky-header.develop{
  		background-color:#111111;
	}
	div#header-wrapper{
  		background-color:#111111;
	}
	div#header{
  		background-color:#111111;
	}
	h1{
		color: #33B5E5 !important;
	}

	a, h2, h3, h4{
		color:#99cc33 !important;
	}

	h5{
		color: #bbb !important;
	}
	
	input {
		color:#839496 !important;
	}

	::selection {
		background: #33B5E5 !important;
		color: #000000 !important;
	}

	::-moz-selection {
		background: #fdf6e3 !important;
		color: #839496 !important;
	}
	::-webkit-selection {
		background: #fdf6e3 !important;
		color: #839496 !important;
	}
	:link {
		color: #99cc33!important;
	}
	:visited {
		color: #99cc33!important;
	}

	.morehover>.top {
		background-color: none;
	}
	.morehover>.mid {
		background-color: #333333;
		background-image: url("http://www.colorhexa.com/333333.png");
	}

	.logo>a {
		background: url("data:image/png;base64,") !important;
	}
	.logo>a>img {
		opacity: 0;
		-ms-opacity: 0;
	}

	.jspVerticalBar, .jspHorizontalBar, pre {
		background: transparent !important;
	}

	#devdoc-nav>.jspContainer>.jspPane {
		right: 0 !important;
	}

	li a.selected {
		padding:2px 4px 2px 4px;
		border-radius:2px;
		color:#33B5E5 !important;
	}

	th, div#jd-header, .jd-details-title {
		background-color:#444 !important;
		padding:4px !important;
		border-radius:2px !important;
	}

	span.sympad{
		color: #ddd;
	}
		
	div.jspContainer {
		border:0px !important;
	}

	div#nav-swap {
		border-top:1px solid #181818 !important;
	}

	b {
		color:#a0f52e !important;
	}
	
	strong{
		font-weight:bold;
		color: #222;
	}

	span.new, span.new-child {
		color:#ffff00 !important;
	}

	code {
		color:#99cc33 !important;
	}

	pre {
		background-color: #111111 !important;
	}

	.locales select, .sites select {
	  background: #444444 right center no-repeat;
	}

	#packages-nav li.selected a, #packages-nav li.selected a:active, #packages-nav li.selected
	a:visited,
	#classes-nav li li.selected a, #classes-nav li li.selected a:active, #classes-nav li li.selected
	a:visited,
	#nav-tree li div.selected {
		font-weight: 500;
		color: #222222;
		background-color:#444444;
	}
}
