css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.myproana.com") {

html, body {

  color: #BBBBBB !important;

}



html > body {

  background-image: url("http://media.tumblr.com/4f3cbb1b66a76a19a9794a162373abc5/tumblr_inline_mns3smYCcI1qz4rgp.png") !important;

}



.cke_show_borders {

  background: #fff !important;

  color: #000 !important;

}



a {

  color: #CC0052 !important;

}



#main_search {

  color: #CC0052;

}



.desc, .desc.blend_links a, p.posted_info {

  color: #BBBBBB !important;

}



#content {

  background: url("http://media.tumblr.com/ea568d43430e0f881680e5cdb622a1b3/tumblr_inline_mns5n8XFqf1qz4rgp.png") !important;

}



#branding {

  background: none !important;

  padding: 20px 0 20px 0 !important;
  
  

}



.maintitle, #primary_nav, .popupInner h3, .ipsSideBlock h3 {

  background: url("http://media.tumblr.com/ae9c51bcb2fd4beeb0a904844bf2d818/tumblr_inline_mns5qraSAL1qz4rgp.png") !important;

  border: 1px solid #493324 !important;

  height: 26px !important;

  color: #A47D6A !important;

  box-shadow: none !important;

}



.maintitle, .ipsSideBlock h3 {

  height: 13px !important;

  padding: 10px !important;

  font-weight: bold !important;

}



.popupInner {

  background: #363636 !important;

  box-shadow: none !important;

}



#primary_nav {

  background: url("http://media.tumblr.com/ae9c51bcb2fd4beeb0a904844bf2d818/tumblr_inline_mns5qraSAL1qz4rgp.png") !important;

}



#primary_nav a {

  background: url("http://media.tumblr.com/ae9c51bcb2fd4beeb0a904844bf2d818/tumblr_inline_mns5qraSAL1qz4rgp.png") !important;

  background: none !important;

  color: #CC0052 !important;

  text-shadow: none !important;

  font-weight: bold;

}



#primary_nav a:hover {

  background: url("http://media.tumblr.com/ae9c51bcb2fd4beeb0a904844bf2d818/tumblr_inline_mns5qraSAL1qz4rgp.png") !important;

}



.maintitle a {

  color: #CC0052 !important;

  font-weight: bold;

}



.ipsBox {

  background: #CC0052 !important;

}



.ipsBox_container {

  background: #363636 !important;

}



.unread, .ipb_table tr {

  background: #191919 !important;

}



.col_c_stats, .col_c_post {

  background: #252525 !important;

}



.ipsBox_container {

  border: 1px solid #464646 !important;

}



table.ipb_table td {

  border-bottom: 1px solid #464646 !important;

}



.post_block {

  background: none repeat scroll 0 0 #191919 !important;

  border-color: #E8DED9 !important;

}



.row2, .post_block.row2, .col_f_icon {

  background: none repeat scroll 0 0 #262626 !important;

}



.post_body .post {

  color: #BBBBBB !important;

}



p.citation {

  background: #C29BAA !important;

  border: 1px solid #595959 !important;

  border-bottom: none !important;

}



.ipsFilterbar a:hover {

  color: #fff !important;

}



div.blockquote {

  background: #464646 !important;

  border: 1px solid #595959 !important;

}



#search_wrap {

  background: none repeat scroll 0 0 #1D1D1D !important;

  border: 1px solid #464646;

}



.ipsHeaderMenu {

  background: #585858 !important;

  border-radius: 0 0 6px 6px;

}



#user_link.menu_active, #notify_link.menu_active, #inbox_link.menu_active {

  background-color: #585858 !important;

}



.row1, .post_block.row1 {

  background-color: #585858 !important;

}



.ipsType_sectiontitle {

  border-bottom: none !important;

  color: #BD901A !important;

}



.ipsType_pagetitle, .ipsType_subtitle {

  color: #BBBBBB !important;

}



.desc.lighter, .desc.lighter.blend_links a {

  color: #5B5B5B !important;

}



.input_submit, .topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton {

  background: none repeat scroll 0 0 #CC0052 !important;

  border-color: #3B2407 !important;

  border-style: none !important;

  box-shadow: none !important;

  text-shadow: none !important;

  font-weight: bold !important;

  color: #FFFFFF !important;

}



.ipsButton_secondary, .pagination .back a, .pagination .forward a, #search_options {

  background: none repeat scroll 0 0 #CC0052 !important;

  border-color: #000 !important;

  color: #BBBBBB !important;

  box-shadow: none !important;

}



.general_box {

  background: none repeat scroll 0 0 #A3A3A3 !important;

}



.ipbmenu_content, .ipb_autocomplete {

  background-color: #585858 !important;

  border: 1px solid #363636 !important;

  box-shadow: none !important;

  color: #B2B2B2;

}





.general_box h3 {

  background: none repeat scroll 0 0 #494949 !important;

  color: #CC0052 !important;

  font-weight: bold !important;

}



.ipsVerticalTabbed_tabs li {

  background: #585858 !important;

  border-color: #242424 !important;

  color: #9F7662;

}



.ipsVerticalTabbed_tabs > ul {

  border: none !important;

}



.status_update {

  background: none repeat scroll 0 0 #191919 !important;

  color: #FFFFFF;

}



#user_link_menucontent a {

  color: #fff !important;

  text-shadow: none !important;

}



body#ipboard_body fieldset.submit, body#ipboard_body p.submit {

  background: #464646 !important;

}



.input_text, .ipsTagBox_wrapper {

  border: 1px solid #EAEAEA !important;

  color: #363636;

}



#main_search_form .search_app.active {

  background: #E0C927 !important;

  border: 2px solid #BDA71B !important;

  color: #AC472B !important;

}



.ipsSideBlock {

  background: #363636 !important;

}





img[src="http://media.tumblr.com/843d9d376aeac892e8de931a6bfb7c1c/tumblr_inline_mns4zw7pNv1qz4rgp.gif"] {

  width: 0 !important;

  height: 32px !important;

  padding-right: 32px;

  background: url("http://media.tumblr.com/843d9d376aeac892e8de931a6bfb7c1c/tumblr_inline_mns4zw7pNv1qz4rgp.gif") !important;
 

}



}
