	{
		color: #FFF !important;
	}

.header-sticky-wrapper li
	{
  		background: #22a7f0 !important;
	}	

.header-sticky-wrapper + .header-sticky-wrapper
	{
  		background: #444 !important;
	  	margin-bottom: 20px !important;
	}

.header-sticky-wrapper + .header-sticky-wrapper a
	{
		color: #FFF !important;
	}

.mob-like
	{
  		background-image: url('http://up413.siz.co.il/up2/ixo02kwzmrti.png') !important;
	}

.mobile-noti
	{
  		background-image: url('http://up413.siz.co.il/up1/nw2zi2ujczyt.png') !important;
	}

.mobile-pm
	{
  		background-image: url('http://up413.siz.co.il/up1/vzmmnnm2zgmt.png') !important;
	}

.mobile-regis
	{
  		background-image: url('http://up413.siz.co.il/up3/v5ketntnoglz.png') !important;
	}

.mobile-login
	{
  		background-image: url('http://up413.siz.co.il/up1/ng2ynhjq2zo1.png') !important;
	}

.mobile-facebook
	{
  		background-image: url('http://up413.siz.co.il/up3/m1y4mziwud3m.png') !important;
	}

/* ads we don't want */
ins.adsbygoogle, div#adss, .ad, .ads, .adsbygoogle, #ad, #ads, [id$="ad_container"], [id~="ad"], div#header + div, div#my2, div#div-gpt-ad-1384085590191-0, div#newmav
    {
      	Display: none !important ;
    }

div#page-home
    {
      	Background: #f5f5f5  !important ;
    }

div#sik
	{
  		text-align: center !important;
	}

div.cats2
	{
  		background: #444 !important;
		transition: background 0.5s;
	  	color: #fff !important;
    	text-shadow: none;
	}

div > div.cats2:first-child:hover
	{
  		background: #444 !important;
	}

div.cats2:hover, div.cats2:focus
	{
  		background: #22a7f0 !important;
	}

div.cats2 a
	{
  		color: #fff !important;
  		text-shadow: none !important;
	}


ul[data-role="listview"] div.cats2
	{
  		background: #22a7f0 !important;
		transition: background 0.5s;
	}

ul[data-role="listview"] div.cats2 a
	{
  		text-shadow: none;
  		font-family: Arial !important;
  		color: #fff !important;
  		font-weight: 700 !important;
	}

ul[data-role="listview"] div.cats2:hover, ul[data-role="listview"] div.cats2:active
	{
  		background: #444 !important;
		color: #fff !important;
    	text-shadow: none;
	}

a.si-icon.si-whatsapp.ui-link, a.si-icon.si-facebook.ui-link
	{
  		display: none;
	}

#newthreadlink_top
	{
  		background: #444;
	  	border-radius: 10px;
	}

.ui-bar-b
	{
  		background: #22a7f0;
  		border: none;
  		text-shadow: none;
	}
}
