css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://feedly.com"),
url-prefix("http://feedly.com") {
    
body{
    line-height: 1.12!important;
}
    
.subscriptionContents,
h1,h2,h3, .hhint,
div.signAlert,
[id*="herculeHint"], 
[id$="Box"],
[class$="Box"],
[class^="subscription"],
[class^="pageSection"],
[class^="cell"], [class^="cell"] *,
[class$="btn"],
[class$="UnreadCount"], [class$="UnreadCount"] > span,
[class$="Entry"],
[class$="Entry"] .title,
[class$="CallForAction"],
.youtubeVideo,
.welcomeNav,
.welcome, .welcome *,
.viewerLabel,
.userOptions,
.twitterTimeline li,
.tweetRollOverview,
.tweet, .tweet *,
.topRecommendedEntry .title,
.toolbar,
.toolButton,
.toBeTagged,
.title,
.trend,
.tip,
.textOnly,
.tab .feedIndexTitleHolder,
.summary,
.suggestion,
.subscriptionSpringCleaning,
.startTipContent,
.sponsor *,
.sources_categoryDrop,
.sourceTitle a, 
.sourceInfo,
.source, .source *,
.socialPost, .socialPost *,
.seed,
.section,
.recommendationInfo,
.reaction,
.quote,
.query,
.profile .information,
.proMiniTrigger,
.preferencesPanel,
.portfolio,
.pinButton,
.personalizationOption,
.option,
.notesHolder,
.nbrrecommendations,
.navSelector,
.name,
.moduleTitleAction,
.miniRealtimeSign,
.metadata,
.listLabel,
.lastModified,
.infoBox,
.info,
.googleSignupButton,
.frameActionsTop,
.feedTitle,
.feedIndexTitle,
.exploreInsert,
.explanation,
.etiquette,
.errorBox,
.entryFootNote,
.entryBody,
.entryBody *,
.entry,
.emptyModule,
.dropboxButton, .dropboxButton *,
.discoverySuggestion,
.datasheet, .datasheet *,
.customizer .label,
.conversation,
.condensedTools,
.commentsHolder, .commentsHolder *,
.column-subscription,
.category,
.button,
.bundle,
.basicEntry .title,
.basicEntry .metadata,
.authors,
.action,
.aboutBox,
.about,
#wizard,
#who, #who *,
#storeHint,
#store, #store *,
#realtimeSign,
#progressDialogMessage,
#proBanner,
#popupPart,
#listNavigation,
#herculePanel *,
#fixedProfile,
#feedlyUndoSign,
#feedlyTabsHolder,
#feedlyBanner,
#dropboxStatus,
#confirmationMessage, #confirmationMessage *,
#bigMarkAllAsReadButton,
#aboutArea{
    line-height: inherit!important;
}
    
#store .unfollowButton,
#store .dot,
.followDone,
.tagged,
.tag,
.welcome .exploreTitle,
.welcome .hintHeader,
.tile .label,
.toBeTagged {
font-family: sans-serif!important;
}
    
#feedlyTabs,#fixedProfile,
.topWikiWidget,
div[id$='main'],
.inlineFrame,
div[id^='feedlyPart']{
padding:0!important;
}

#floatingBar{
padding: 5px 0 0 0!important;
}

.feedIndex{
padding-left: 34px!important;
}

.moreHandle{
font-size: x-small!important;
margin-left: 42px!important;
color: rgb(190,80,60)!important;
}
 
.topWikiWidget,
div[id$='main'],
*[style*='margin-top'],
*[style*='margin-bottom'],
[class^='entry'] p{
margin-bottom: 0!important;
margin-top: 0!important;
}

#searchBoxPlaceholder{
margin-top: 3px!important;
}


#feedlyDialogFramePart div,
#feedlyPageHeader{
padding-top: 0!important;
}
    
[class$="UnreadCount"] *,.viewerIcon{height:auto!important;}

.inlineFrame{
min-height: 0!important;
min-width: 0!important;
}

[class$='Entry'],
[class^='entry']{
max-height: none!important;
max-width: none!important;
}

#feedlyTabsHolder{
width: 220px!important;
}

#mainBar{
width: 100%!important;
}

#floatingBar,
#feedlyFrame{
margin-left: 220px!important;
}

.feedIndexTitleHolder,
div[class*='UnreadCount']{
max-height: 16px!important;
}

#feedlyTabs,
#feedlyDialogFramePart,#store {
overflow-y: hidden!important;
}
    
.inlineFrame{
transition-property: none!important;
/* z-index: 502 */
border-width: 2px!important;
border-radius: 9px!important;
margin: 4px 0 !important;
}

*:not(.inlineFrame){
border: none!important;
}
#floatingBar h1{
font-size: 26px!important;
}
    
#frameActionsTop,#feedlyMessageBar,#sideArea,#section0,
#loadingEntries > img, 
#feedlyTitleBar > .hhint,
div[style='height:17px'], /*div spacer under x more sources*/
a[href*='plus\.google'],
div[data-uri^='pro'],
[class^='entry'] > br,
.nbrRecommendations,.u0Entry:hover .lastModified,.bottomWikiWidget,
a.websiteCallForAction,.feedIndexTitleHolder img.favicon,.sideAreaModule {
display: none !important;
}
    
.handle{margin-top:-8px!important;}
    
[class$="UnreadCount"] > span{margin-top:-18px!important;}
}
