css:
@-moz-document
    domain("wikipedia.org"),
    domain("wiki.archlinux.org") {

    #mw-page-base,
    #footer,
    #mw-head,
    #p-logo,
    #p-navigation,
    #p-interaction,
    #p-help,
    #p-tb,
    #p-coll-print_export,
    .uls-settings-trigger,
    #p-lang-label,
    .after-portlet,
    #toctitle,
    /* arch */
    #archnavbar,
    #column-one
    {
        display: none;
    }
    
    #mw-panel,#p-lang {
        padding: 5px!important;
        margin: 0!important;
        background: none!important;
    }
    
    #mw-panel {
        top: 40px!important;
        position: absolute!important;
        right: 0;
        left: auto!important;
    }
    
    #p-lang .body {
        margin: 0!important;
        text-align: right;
    }
    
    #toc {
        position: absolute;
        top: 40px;
        left: 0;
        right: calc(50% + 450px);
        border: none!important;
        background: none!important;
    }
    
    #toc a, #p-lang a {
        color: #000!important;
    }
    
    #content {
        border: none!important;
        margin: 40px auto!important;
        width: 800px;
        background: #FFF;
    }
        
    #content,.mw-body-content {
        position: static!important;
    }
    
    #content h1, #content h2 {
        border: none!important;
    }
    
    #content p {
        margin: 0!important;
    }
    
    #content p + p,
    #content dd + dd {
        text-indent: 1em;
    }

}
