css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
  #player.watch-medium #player-api {
    width: 1280px !important;
    height: 750px !important;
  }

  .watch-medium .player-height {
    height: 750px !important;
  }

  #player.watch-medium .watch7-playlist-bar-left {
    width: 980px !important;
  }

  #player.watch-medium #watch7-playlist-tray-container {
    left: 980px !important;
  }

  .distiller-first-time-promo {
    display: none !important
  }
  
  .site-center-aligned #player.watch-medium {
    width: 100% !important;
  }
  
  .appbar-flexwatch #theater-background, 
  .appbar-flexwatch-mini #theater-background {
    height: 760px !important;
  }
}
