#footer {
    margin-left: auto;
    margin-right: auto;
    display: block !important;
    max-width: 555px !important;
    background-image: url("http://i.imgur.com/W63VKb8.png") !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
